vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0, -1.0);

    // ground
    t.x = pos.y;

    vec3 gp = pos + vec3(0.0, 0.0, 0.0);
    float ground = udTriangle(gp,
        vec3(-1.4, 0.0, 3.3),
        vec3(0.0, 0.0, 5.0),
        vec3(2.2, 0.0, 5.0));

    // guys
    vec3 g1p = pos + vec3(0.0, 0.0, -4.1);
    g1p.xz *= rot(PI/6.0);
    float g1 = sdGuy(g1p, .2);
    float g2 = sdGuy(g1p+vec3(-0.4, 0.0, -.9), .2);
    float g3 = sdGuy(g1p+vec3(0.2, 0.0, -.5), .2);

    float guys = opU(g1, g2);
    guys = opU(guys, g3);

    // dragon
    vec3 d = pos + vec3(4.0, -1.0, 0.0);
    d.y += sin(pos.x+iTime)*.5;
    float dragonLine = sdLine(d, vec3(0.0), d + vec3(1.0, 0.0, 0.0)).x;
    float dragon = dragonLine - .2;

    // legs
    vec3 ep = pos + vec3(0.0, sin(iTime)*.5-.7, -1.0);
    float leg = sdLine(ep, vec3(0.0), ep + vec3(0.2, -.2, 0.0)).x - .05;
    ep = pos + vec3(2.0, sin(pos.x+iTime)*.5-1.0, -1.0);
    leg = opU(leg, sdLine(ep, vec3(0.0), ep + vec3(-0.15, -.15, 0.0)).x - .02);

    // head
    vec3 hp = pos + vec3(-1.4, sin(pos.x+iTime)*.5-1.0, -2.0);
    float head = sdLine(hp, vec3(0.0), hp + vec3(0.2, -.2, 0.0)).x - .02;

    t = vec2(ground, 0.0);
    t = opU2(t, vec2(guys, 1.0));
    t = opU2(t, vec2(dragon, 2.0));
    t = opU2(t, vec2(leg, 2.0));
    //t = opU2(t, vec2(head, 3.0));

    return t;
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 1.; i += 1./15.) {
        vec2 dist = map(pos + dir * t);
        if (dist.x < VOLUME) return 0.;
        f = min(f, k * dist.x / t);
        t += dist.x;
        if (t >= maxt) break;
    }
    return f;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(t, ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = ro+rd*t.x;
    vec3 col = vec3(0.0);
    float depth = clamp(pos.z, 0.0, 1.0);
    depth = 1.0 - sat(pos.z*.1);

    if(t.x == -1.0)
    {        
        col = vec3(0.0);

    }
    else
    {
        float glp = clamp(length(pos)*.1, 0.0, 1.0);
        vec3 N = GetNormal(ro+rd*t.x);
        vec3 L = vec3(3.0, 10.0, 0.0);
        float light = dot(N,normalize(L));
        float shade = GetShadow(pos, L, 4.);

        if(t.y == -1.0) 
        {
            col = vec3(.2+pos.z*.1);
            col *= shade;
        }
        else if(t.y == 0.0) // ground
        {
            col = vec3(.5);
            col *= light;
            col += shade*.5;
        }
        else if(t.y == 1.0) // guys
        {
            col = vec3(0.0);
            col += light*.3;
        }
        else if(t.y == 2.0) // dragon
        {
            col = vec3(1.0);
        }
        else if(t.y == 3.0) // tongue
        {
            col = vec3(1.0, 0.0, 0.0);
        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 1.0 * forward);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float time = iTime*.25;

    vec3 cp = vec3(0.0, 2.0, 6.0);
    vec3 ct = vec3(0.0, 0.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = vec3(length(uv));
    vec2 screenUV = fragCoord.xy / iResolution.xy;

    col = Render(cp, vd, uv);
    col.z += screenUV.y*.5;
    col -= random(screenUV) * .1;
    col *= 1.0-length(uv)*.4;

    fragColor = vec4(col,1.0);
}

const float PARTITIONS = 9.;
const float SHADETHRESH = .9;

vec3 dtoa(float d, vec3 amount){
    return vec3(1. / clamp(d*amount, vec3(1), amount));
}

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define nsin(x) (sin(x)*.5+.5)
#define q(x,p) (floor((x)/(p))*(p))

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    vec2 R = iResolution.xy;
    uv.x *= R.x / R.y;
    uv *= 2.;

    vec2 cellUL = vec2(-.5);
    vec2 cellBR = vec2(.5);
    float seed = 1e3;
    o = vec4(0);
    vec3 lineColor;
    vec3 a, a2;

    for(float i = 0.; i < PARTITIONS; ++ i) {
        vec4 h = hash42(1e3*cellUL+seed); // xy = pos to divide between cellUL / cellBR.
        vec4 h2 = hash42(1e2*cellUL+seed);
        h.xy = mix(cellUL, cellBR, h.xy);
        seed = h2.z+i;
        vec2 uv2 = uv;
        uv2 += q(sin(iTime*(h.z-.5)+(h.w*6.28))*.5, h2.w+.01);
        uv2.x += uv2.y * (h2.x-.5);
      	uv2.y += uv2.x * (h2.y-.5);
        float dl = min(length(uv2.x - h.x), length(uv2.y - h.y));
        if (h2.x > SHADETHRESH) {
            o.rgb = mix(h2.ywx, lineColor, a2 * nsin(150.*(uv.x+uv.y)));
        }
        dl = fract(dl);// cheap way to add more angles.
        a = dtoa(dl, vec3(800));
        a2 = dtoa(dl-.05, vec3(40));
        lineColor = hash42(h2.xz*1e3).rgb;
        o.rgb = max(o.rgb, lineColor * a);

        if (uv2.x < h.x) {
            if (uv2.y < h.y) {
                cellBR = h.xy;
            } else {
              	cellUL.y = h.y;
              	cellBR.x = h.x;
            }
        } else {
            if (uv2.y > h.y) {
                cellUL = h.xy;
            } else {
                cellUL.x = h.x;
                cellBR.y = h.y;
            }
	    }
        uv *= 1.05;
    }

    vec2 N = fragCoord / iResolution.xy-.5;
    o = clamp(o,0.,1.);
    o = pow(o,o-o+.8);
    o.a = 1.;
}

float grey(vec2 uv)
{
    vec3 c = texture(iChannel0, uv).rgb;
    return (c.r + c.g + c.b) / 3.;

    float aspect = iResolution.x / iResolution.y;
    vec2 aspect2 = vec2(aspect, 1.);
	return smoothstep(.5, .4, length(uv * aspect2 + - .5));
}

float laplace(vec2 uv)
{
    vec2 pixel = 1. / iResolution.yy;

	float center = grey(uv);
    float left = grey(uv - vec2(pixel.x, 0));
    float right = grey(uv + vec2(pixel.x, 0));
    float up = grey(uv - vec2(0, pixel.y));
    float down = grey(uv + vec2(0, pixel.y));

    float c = left + right + up + down - 4. * center;
    return c * .5 + .5;
}

float sobel_vertical(vec2 uv)
{
    vec2 p = 1. / iResolution.yy;

    float top = grey(uv - vec2(0, p.y));
    float topleft = grey(uv - vec2(-p.x, p.y));
    float topright = grey(uv + p);
    float bottom = grey(uv + vec2(0, p.y));
    float bottomleft = grey(uv + vec2(-p.x, -p.y));
    float bottomright = grey(uv + vec2(p.x, -p.y));

    float c = bottomleft + bottomright + 2. * bottom - topleft - topright - 2. * top;
    return c * .5 + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 col = vec3(0);

    float t = sin(iTime) * .2 + .5;
    float tl = t - .2;
    float tr = t + .2;

    float l = smoothstep(tl - .005, tl + .005, uv.x);
    float r = smoothstep(tr + .005, tr - .005, uv.x);

    col += l * r * grey(uv);
    col += (1. - l) * laplace(uv);
    col += (1. - r) * sobel_vertical(uv);


    fragColor = vec4(col, 1.);
}

vec3 hsvToRgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

const float pi = 3.1415926535897932384;

mat2 rotation ( in float _angle ) {
    return mat2(
        cos(_angle), sin(_angle),
    	-sin(_angle), cos(_angle)
    );
}

float angle(in vec2 pos) {
    if (abs(pos.x) < 0.0001) {
        if (pos.y >= 0.0) {
            return pi / 2.0;
        }
        if (pos.y < 0.0) {
            return -pi / 2.0;
        }
    }
    return atan(pos.y, pos.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime * 0.75;

    vec2 uv = rotation(pi * 1.25) *
        (fragCoord / iResolution.xy - 0.5);

    uv.x = uv.x * iResolution.x / iResolution.y;

    float theta = angle(uv) * 5.2 / pi;

    float uvrX = 19.0 * pow(
        length(uv),
        0.8
    );

    vec2 uvr = vec2(
        uvrX,
        theta * 5.0 + 1.6 * sin(uvrX + 4.0 + time)
    );

    vec3 color1 = hsvToRgb(vec3(
        0.56,
        0.7,
        0.75
    ));

    vec3 color2 = hsvToRgb(vec3(
        0.68,
        0.8,
        0.60
    ));

    vec2 uvrMacro = floor(uvr);
    vec2 uvrMicro = fract(uvr);

    // Inside gradient
    if (uvrMacro.x < 7.0) {
        fragColor = vec4(mix(
            color1,
            color2,
            0.5 + 0.5 * sin(uvr.x * 0.5 + uvr.y * 0.2)
        ), 1.0);
        return;
    }

    fragColor = vec4(
        vec3(0.08 + 0.04 * sin(
            (uv.y + 0.1 * (0.5 + 0.5 * sin(time)) +
             (1.0 + 0.12 * sin(time)) * uv.y * uv.x) *
            21.0 * (1.5 + 0.02 * sin(time + pi * 0.5))
        )),
        1.0
    );
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;



vec2 rotate(vec2 v, float a) {
	return vec2(cos(a)*v.x + sin(a)*v.y, -sin(a)*v.x + cos(a)*v.y);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

vec3 Zigzag(vec3 pos, float freq, float offset){
    float i = (pow(offset+abs(pos.x),.8));
    float x = freq-abs(mod(i,2.0*freq)-freq);
    pos.y+=(x);
    return pos;
}


float sdEllipsoid( in vec3 p, in vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}


float sdOctahedron( in vec3 p, in float s)
{
    p = abs(p);
    return (p.x+p.y+p.z-s)*0.57735027;
}


float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}


float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

#define PI 3.14159


vec2 kaleido(vec2 z, float axis ){
                float angleFrac = (2.0 * PI) / (2.0 * axis);

                    float phi = abs(atan(z.y, z.x)); 
                    float r = length(z.xy);

                    int count = int(phi / angleFrac);

                    phi = mod(phi, angleFrac);
                    if ( int(mod(float(count),2.0)) == 1) {
                    phi = angleFrac - phi;
                    }

                    float x = r * cos(phi);
                    float y = r * sin(phi);



                    return (vec2(x,y));
            }


float dip(vec3 p) {
    //p.x+=sin(p.y*2.0+iTime*4.0)/8.0;

    float head=sdRoundedCylinder(p+vec3(.0,.05,-.050),.375,.6,.01);

    vec3 p2 = p;p2.x=abs(p2.x);p2.y/=1.4;

    vec3 p3 = p;p3.z*=1.12;
    //p3.x-=sin(iTime*2.0)/4.0;
    //p3.x-=sin(p3.y+iTime*2.0)/8.0;
    //p3.z+=step(p.z,.0)*sin(p3.y)/8.0;
    p3.z-=smoothstep(.0,1.0,p3.y)/8.0;


    float body = sdRoundedCylinder(p3+vec3(.0,1.7,.350+sin(p3.y)/8.0),.3,1.7,.103);
	    p3.yz=rotate(p3.yz,.105);

    body = opSmoothUnion(body, sdRoundedCylinder(p3+vec3(.0,.9,.150),.25,1.7,.103),.1);

    float d=opSmoothUnion(body,head,.15);

    float horns = sdOctahedron(p2-vec3(.5,.44,.1),.5);
    horns = max(horns,-sdBox(p2-vec3(.0,.5,.0),vec3(.2,1.0,1.0)));
    d=min(d,horns);


    //d=min(d,body);




    return d;
}


float map(vec3 p){
    float t = float(iFrame)/60.0;
    float x = smoothstep(.0,1.0,sin(t)/2.0+.5);
        vec2 pos = vec2(x,.0)/2.25;
	float m = length(pos)*5.0;
    float m2 = length(p);
    //m+=abs(1.-m)*(.5+.5*sin(p.y*32.))/8.0;

    vec3 p2 = p;

    p2.z+=m/3.;
        p.z+=m/3.;   


    p2.xz=rotate(p.xz,-m/3.);
    p.xz=rotate(p.xz,m/3.);
p2.x-=m/3.;
        p.x+=m/3.;

    float d = dip(p-6.2*vec3(pos.xy,.0));
    float d2 = dip(p2+6.2*vec3(pos.xy,.0));
    float b = m*max(.0,1.1-m2*1.)*sin(p.y*32.+sin(p.y*3.3)*4.)/16.0;
	//b*=(.75+b*.5);
    d = opSmoothUnion(d,d2,.5*m+b*4.1);
    //d -= (d*(b/2.+.5)*1.9)/1.0;
    return d;
}

vec4 colorMap(vec3 pos){

        float t = float(iFrame)/60.0;

    vec2 rotor = vec2(sin(t*2.0)/2.0,cos(t));

    float eyeLook = mod(rotor.x+3.14*9.0,3.14*2.0)-3.14;
    //eyeLook=clamp(eyeLook*12.5,-2.7,2.7)/2.7;
    eyeLook=clamp(eyeLook*2.0,-1.0,1.0);

    eyeLook=smoothstep(0.0,1.0,eyeLook/2.0+.5)*2.0-1.0;
    eyeLook*=.85;
        //eyeLook=clamp(eyeLook,-1.,1.)*.6;


    vec3 p = pos;
    p.x=abs(p.x);




    vec4 fragColor = vec4(1.0, 0.0, 0.0, 1.0);

    float eyes=step(length(p+vec3(-.4,-.1,-.6)),.25);
    float blink = clamp(1.0-abs(sin(t*0.45))*16.0,.0,1.0);


    //blink rate for second blink set
    float blinkRate = .4;

    //second set of blinks for more dynamic blinking lol
    blink = max(blink,clamp(1.0-abs(cos(t*0.45*blinkRate))*16.0/blinkRate,.0,1.0));

    //squint occasionally
    blink = max(blink,clamp(.35-abs(cos(t/4.0+.15)),.0,.10));

    eyes*=step(p.y+p.x/8.0,.2-blink);

    //horizontal pupils follow camera
    float pupils =step(.0,sdBox(p+vec3(-.4-eyeLook*sign(pos.x)/6.0,-.1+p.x/8.0,-.6),vec3(.01,.1,.5)));


    //fragColor.g+=step(.0,n.y)*.25*step(-.5,r.pos.y);
    //fragColor.g+=step(.0,n.y)*.05*step(n.y,.01);

    //fragColor.rgb=n.rgb;

    fragColor.g=clamp(fragColor.g+eyes*pupils,.0,1.0);




    vec3 pm = Zigzag(pos,.11,.15);


    pm.y=mix(pos.y,pm.y*2.0+.35,clamp(1.0-abs(pm.x),.0,1.0));



    float talkFreq = mix(sin(t*16.0),sin(t*13.0)-.8,clamp((cos(t*2.0+.15)+.5)*4.0,0.0,1.0));
    float talkH=clamp(talkFreq/16.0+.1,.015,.25);
    //float talkW=smoothstep(-1.0,1.0,clamp(-cos(iTime*2.0+.15)*8.0+.5,.0,1.0)*.24)*1.15-.34;
    float talkW=smoothstep(-1.0,1.0,clamp(-cos(t*2.0+.15)*8.0+.5,.0,1.0)*.24)*.65;

    //	talkW=.42;
    float mouth = (sdEllipsoid(pm+vec3(.0,.425-pow(abs(pm).x,2.0)/1.0,-.5),vec3(talkW,talkH,.5)));



    fragColor.g+=1.0-step(-.01,mouth);
    return fragColor;

}


vec4 dipCol(vec3 p){
        float t = float(iFrame)/60.0;


    float x = smoothstep(.0,1.0,sin(t)/2.0+.5);
        vec2 pos = vec2(x,.0)/2.25;
	float m = length(pos)*5.0;

    vec3 p2 = p;

    p2.z+=m/3.;
        p.z+=m/3.;   


    p2.xz=rotate(p.xz,-m/3.);
    p.xz=rotate(p.xz,m/3.);
p2.x-=m/3.;
        p.x+=m/3.; 


    vec4 d = colorMap(p-6.2*vec3(pos.xy,.0));
    vec4 d2 = colorMap(p2+6.2*vec3(pos.xy,.0));
    d.g = max(d.g,d2.g);
	return d;
}


vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        map(vec3(p.x + EPSILON, p.y, p.z)) - map(vec3(p.x - EPSILON, p.y, p.z)),
        map(vec3(p.x, p.y + EPSILON, p.z)) - map(vec3(p.x, p.y - EPSILON, p.z)),
        map(vec3(p.x, p.y, p.z  + EPSILON)) - map(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

struct rmData{
    vec4 color;//TODO @SAM: just do the colors at end of rm() function, u dummy
    vec3 pos;
    float depth;
};


rmData rm(vec3 eye, vec3 dir, float start, float end) {
    float depth = start;
    rmData raym;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        vec3 pos = eye + depth * dir;
        //pos=round(pos*16.0)/16.0;
        //pos.x+=2.5;
        //pos.z+=2.4;
        //pos.xy=kaleido(pos.xy,16.0);
        //        pos.y-=.85;

        //pos.zx=kaleido(pos.zx,16.0);

        //        pos.z-=1.5;
        //pos.z-=2.5;        


float dist = map(pos);
        if (dist < EPSILON) {
            raym.depth=depth;
            raym.pos=pos;
            raym.color=dipCol(raym.pos);
			return raym;
        }
        depth += dist;
        if (depth >= end) {
            raym.depth=end;
            return raym;
        }
    }
    raym.depth=end;
    return raym;
}


vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


