float SIZE = 2.;

vec2 foldUVs(vec2 uvs) {
    if (uvs.x < 0.) uvs *= -1.;
    uvs.x -= .5;
    //mat2 m = mat2(0,1,-1,0);
    //float ang = 6.28*iMouse.x/iResolution.x;
    float ang = 3.141592*1./2.;
    mat2 m = mat2(cos(ang),sin(ang),-sin(ang),cos(ang));
    uvs *= m;
    uvs *= SIZE;
    return uvs;
}

