// Screen coords:
//   vec2 in "screen units" relative to the center of viewport
//   y axis pointing up, x axis pointing to the right
//   screen units are chosen so that a square [-1, 1] x [-1, 1] fits into the screen
// World coords: vec2 in units.
// Cameras: vec3, .xy is world coords of the center of the camera, .z is "zoom" (in screen units per world unit)


#define PI 3.1415926535897932384626433832795

#define CAMERA_ZOOM_MIN 0.6
#define CAMERA_ZOOM_MAX 1.0

#define N_PLAYERS 8

vec3 playersColor[N_PLAYERS];
vec3 playersBgColor[N_PLAYERS];


// Inputs of the shader.
vec2 playersPos[N_PLAYERS];  // player positions in world coordinates
vec3 globalCam;             // global camera (zoomed out camera that sees all players)
vec3 playersCam[N_PLAYERS];  // positions of cameras for each player


// *****************************************************
//  Utility functions
// *****************************************************

float atan2(float y, float x) {
	// For some reason GLSL's atan doesn't work as atan2
    // and only returns angles [0, PI).
    if (y > 0.0) {
        return atan(y, x);
    } else {
        return PI + atan(-y, -x);
    }
}

vec2 camScreen2World(vec3 cam, vec2 screenCoord) {
    return cam.xy + screenCoord / cam.z;
}

float camScreen2World(vec3 cam, float screenLength) {
    return screenLength / cam.z;
}

vec2 camWorld2Screen(vec3 cam, vec2 worldCoord) {
    return (worldCoord - cam.xy) * cam.z;
}

float camWorld2Screen(vec3 cam, float worldLength) {
    return worldLength * cam.z;
}

float screen2px(float x) {
    return x * 0.5 * min(iResolution.x, iResolution.y);
}

float camWorld2Px(vec3 cam, float worldLength) {
    return screen2px(camWorld2Screen(cam, worldLength));
}

// Weighted aggregation of the color information for overlapping objects on the same layer.
// The resulting color is a weighted average of all aggreageted colors.
void aggregateColorSiblings(inout vec4 aggColor, inout float aggWeight, vec4 addedColor) {
    if (addedColor.w > 0.0001) {
        float opacity = max(aggColor.w, addedColor.w);        
        aggWeight += addedColor.w;
        aggColor = vec4(mix(aggColor.xyz, addedColor.xyz, addedColor.w / aggWeight), opacity);
    }    
}

// *****************************************************
//  Scene rendering (demo vesion)
// *****************************************************

vec3 renderBackgroundRadialChecker(vec3 cam, vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(cam, screenCoord);

    float radius = length(worldCoord.xy);
    float angularD = atan2(worldCoord.y, worldCoord.x) * radius;

    const float rowWidthGrowthPow = 2.5;    
    const float rowRadiusScale = 100.0;
    // index of a radial checker row.
    float row = ceil(pow(radius * rowRadiusScale, 1.0 / rowWidthGrowthPow));

    float rowOuterRadius = pow(row, rowWidthGrowthPow) / rowRadiusScale;    
    float radialBorderD = abs(radius - rowOuterRadius);
    float columns = 10.0;
    const float minRow = 2.0;
    if (row < minRow + 0.1) {
        row = minRow;
        columns = 4.0;
    } else {
  		float rowInnerRadius = pow(row - 1.0, rowWidthGrowthPow) / rowRadiusScale;
        radialBorderD = min(radialBorderD, abs(radius - rowInnerRadius));

        columns = 10.0;        
    }

    // index of a concentric checker column.
    float columnWidth = (radius * 2.0 * PI) / columns;
    float col = ceil(angularD / columnWidth);

    float concentricBorderD = abs(mod(angularD / columnWidth + 0.5, 1.0) - 0.5) * columnWidth;


    const vec3 white = vec3(1.0);
    const vec3 black = vec3(0.0);
    vec3 midColor = 0.5 * (black + white);

    vec3 color;
    if (mod(row + col, 2.0) > 0.5) {
        color = white;
    } else {
        color = black;
    }

    float borderDPx = camWorld2Px(cam, min(radialBorderD, concentricBorderD));
    float aaColorWeight = smoothstep(0.0, 0.6, borderDPx);

    return mix(midColor, color, aaColorWeight);
}

vec4 renderPlayers(vec3 cam, vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(cam, screenCoord);

    // Player is a 10px at furthest possible zoom and gets bigger when gets closer.
    float radiusPx = 10.0 * max(cam.z, CAMERA_ZOOM_MIN) / CAMERA_ZOOM_MIN;
    const float radiusAaPx = 1.0;

    vec4 color = vec4(0.0);
    float weight = 0.0;
    for(int i = 0; i < N_PLAYERS; i++) {
    	float distPx = camWorld2Px(cam, length(worldCoord - playersPos[i]));

        float playerWeight = 1.0 - smoothstep(radiusPx, radiusPx + radiusAaPx, distPx);
        vec4 playerColor = vec4(playersColor[i], playerWeight);
        aggregateColorSiblings(color, weight, playerColor);
  	}    
    return color;
}

vec3 renderScene(vec3 cam, vec2 screenCoord) {
    vec3 color = renderBackgroundRadialChecker(cam, screenCoord);
    vec4 playersColor = renderPlayers(cam, screenCoord);
    color = mix(color, playersColor.xyz, playersColor.w);

    return color;
}

// *****************************************************
//  Screen partitioning
// *****************************************************

// Returns a number that is guaranteed to be higher than any
// of the distances within Global Camera.
float maxGlobalDist() {
    return camScreen2World(globalCam, 100.0);
}

int closestPlayer(vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(globalCam, screenCoord);

    int minPlayer = 0;
    float minDist = maxGlobalDist();
    for(int i = 0; i < N_PLAYERS; i++) {
    	float dist = length(worldCoord - playersPos[i]);

        if (dist < minDist) {
            minDist = dist;
            minPlayer = i;
        }
  	}

    return minPlayer;
}


