void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    uv *= 1.3;


    uv = voronoiPoints(uv, float(iTime), 100);

    vec3 col = steppyColour(uv, iTime);

    //col = vec3(sin(uv.x*10.), cos(uv.y*5.), 0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 steppyColour(vec2 uv, float t){
    float len = length(uv);
    return vec3(sin(len*5.0), sin(fract(len+t)*3.141*5.), .5*sin(len+cos(-t)));
}

vec3 uvReferenceColour(vec2 uv, float scale){
    return vec3(sin(uv.x*scale), cos(uv.y*scale), 0);
}

vec2 voronoiPoints(vec2 uv, float t, int n){
    float minn = FLT_MAX;
    vec2 ret = vec2(0);

    for(float i = 0.; i<float(n); i++){
        vec2 p = vec2(sin(t+(i*0.06)), sin(i+cos(t)));

        float tmp = minn;//hold last min value
        minn = min(distance(uv,p),minn); //perform next calculation
        tmp = tmp - minn; //get difference in values


        ret = mix(ret,uv-p,step(0.00001, tmp));//mix between old and new values
    }
    return ret;
}

float circle(vec2 positionFragment,vec2 positionObject,float radius){
    return length(positionFragment-positionObject) - radius;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //from -1 to 1
    vec2 pos=( 2.*fragCoord - iResolution.xy )/iResolution.y;

    //smooth factor
    float e=0.5;

    float value=0.;
    for(int i=0;i<3;i+=1){
        float r=radians(float(i+1)*360.*iTime*.1);
        vec2 circlePosition=vec2(cos(r),sin(r))*.5;
        value+=smoothstep(-e,e,-circle(pos,circlePosition,0.25));
    }

    //lookup
    vec3 col=texture(iChannel0,vec2(value,mod(iTime,.5)+.25)).rgb;

    //isolate
    if(value<0.5){
        col=1.-col;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

