vec2 ripple(vec2 p)
{
    float l = length(p);//length of all point to center of screen
    //ripple shape
    //sin(l) : along the l increase direction create wave pattern using sin
    //this create Concentric circles
    //-t*2 create animation

    float density = 9.;
    float speed = -2.;
    p = vec2(abs(sin(l*density+speed*t)));
    return p;
}

vec2 beam(vec2 p)
{

    float l = length(p);//length of all point to center of screen
    //more center, more align to x axis , larger value
    p = p/l;
    //animate the brightness using lifted sin
    p*=(sin(t)+1.);
    return p;
}

