// SHADERTOBER 26 Dark
// Poulet Vert 27-10-2019
// thanks iq, leon, flopine

#define VOLUME 0.001
#define PI 3.14159


float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

vec3 opRep(vec3 p, vec3 c)
{
     return mod(p+0.5*c,c)-0.5*c;
}

vec2 opU2( vec2 d1, vec2 d2 )
{
    return (d1.x < d2.x) ? d1 : d2;
}


vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}



// Scene setup
vec2 map(vec3 pos)
{
    vec2 scene = vec2(0.0, 0.0);

    scene.x = pos.y;

    float doorP = iTime * 3.;

    vec3 dp = pos + vec3(0.0, -3.0, doorP);
    dp = opRep(dp, vec3(0.0, 0.0, 5.0));
    float door = sdBox(dp, vec3(2., .2, .3));

    dp = pos + vec3(1.5 - pos.y*.1, -1.5, doorP);
    dp = opRep(dp, vec3(0.0, 0.0, 5.0));
    door = min(door,sdBox(dp, vec3(.2, 2.0, .2)));

    dp = pos + vec3(-1.5 + pos.y*.1, -1.5, doorP);
    dp = opRep(dp, vec3(0.0, 0.0, 5.0));
    door = min(door,sdBox(dp, vec3(.2, 2.0, .2)));

    // Materials
    scene = opU2(scene, vec2(door, 1.0));

    return scene;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<128 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(t, ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

float GetShadow (vec3 pos, vec3 at, float k) {
    vec3 dir = normalize(at - pos);
    float maxt = length(at - pos);
    float f = 01.;
    float t = VOLUME*50.;
    for (float i = 0.; i <= 10.0; i += .1) {
        float dist = map(pos + dir * t).x;
        if (dist < VOLUME) return 0.;
        f = min(f, k * dist / t);
        t += dist;
        if (t >= maxt) break;
    }
    return f;
}


vec3 GetNormal (vec3 p)
{
    float c = map(p).x;
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(map(p+e.xyy).x, map(p+e.yxy).x, map(p+e.yyx).x) - c);
}

float GetLight(vec3 N, vec3 lightPos)
{
    return max(dot(N, normalize(lightPos)), 0.0);
}



float GroundTexture(vec2 uv)
{   
    return sign(sin(uv.x))*sign(sin(uv.y));
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = vec3(ro + rd * t.x);

    vec3 col = vec3(0.0);
    vec3 polyCol = palette(iTime*.5, vec3(.5), vec3(.5), vec3(1.0), vec3(0.0, 0.33, 0.67));

    if(t.x == -1.0)
    {
        col = vec3(0.0);
    }
    else
    {    
        vec3 N = GetNormal(pos);

        vec3 mainL = vec3(2.0, 5.0, 0.0);
        float mainlight = GetLight(N, mainL);

        vec2 ledFreq = vec2(.15, .17);
        vec2 ledUV =  vec2(-pos.x+5.08, -pos.y-.5);

        float shade = GetShadow(pos, mainL, 4.0);


        if(t.y == 0.0) // ground
        {
            col = clamp(vec3(1.0-length(pos*.1))*.3, 0.0, 1.0);
            vec2 groundUV = pos.xz * 10.;
            groundUV.y += iTime*30.;
            col *= GroundTexture(groundUV);
            col *= shade;

        }
        else if(t.y == 1.0) // door
        {
            col = polyCol;
            col *= clamp(vec3(1.0-length(pos*.1))*.3, 0.0, 1.0);
            col *= shade;

        }
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(sin(iTime)*.3, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    vec2 screenUV = fragCoord.xy / iResolution.xy;    

    vec3 cp = vec3(sin(iTime)*.5, 1.0, 0.0);
    vec3 ct = vec3(0.0, 1.0, 5.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = Render(cp, vd, screenUV);

    col = sqrt(clamp(col, 0.0, 1.0));

    fragColor = vec4(col,1.0);
}

