#define max_steps 100
#define eps 0.001

mat2x2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2x2(c,-s,s,c);
}
vec3 rotX(vec3 ro, float a) { ro.yz = rot(a)*ro.yz; return ro;}
vec3 rotY(vec3 ro, float a) { ro.xz = rot(a)*ro.xz; return ro;}
vec3 rotZ(vec3 ro, float a) { ro.xy = rot(a)*ro.xy; return ro;}


float sphere(vec3 p, float r) {

    return length(p) - r;
}

float sub(float a, float b) {
    return max(a,-b);
}

float add(float a, float b) {
    return min(a,b);
}

vec3 recenter(vec3 p, vec3 nc) {
    return p-nc;
}

float plane(vec3 p) {
    return p.y;
}
float sdf(vec3 ro) {
    float d = 100000.0;

    float x = sin(iTime);

    float main = sphere(recenter(ro, vec3(0,1.5,0)), .5);

    vec3 sc = vec3(0, 1.5-fract(iTime*.25)*1.,0);
    d= add(d,sub(main, sphere(recenter(ro, sc), .30)));
    d= add(d,sphere(recenter(ro, sc-vec3(0,1,0)), .25));

    float sec = sphere(recenter(ro, sc), .25);

    d = add(d, sec);

    d = add(d, plane(ro));

    d = add(d,
            plane(
                rotX(recenter(ro, vec3(0,0,1)), -1.5)
            )
            );
    d = add(d,
            plane(
                rotZ(recenter(ro, vec3(1,0,0)), 1.5)
            )
            );
    d = add(d,
            plane(
                rotZ(recenter(ro, vec3(-1,0,0)), -1.5)
            )
            );
    return d;
}

vec2 render(vec3 ro, vec3 rd) {
    float total_distance = 0.;
    for(int i = 0 ; i < max_steps ; i++) {
        float d = sdf(ro + total_distance*rd);

        if(d<eps)
            return vec2(1, total_distance);

       	total_distance += d;
    }

    return vec2(0, total_distance);
}

float rand(vec2 co){
    co = floor(co*10.);
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453)*2.-1.;
}

vec3 est_normal(vec3 p) {
    return normalize(vec3(
       sdf(p+vec3(eps,0,0))-sdf(p-vec3(eps,0,0)),
       sdf(p+vec3(0,eps,0))-sdf(p-vec3(0,eps,0)),
       sdf(p+vec3(0,0,eps))-sdf(p-vec3(0,0,eps))
    )) + vec3(rand(p.yz), rand(p.xz), rand(p.yz))*.1;
}


float lighting(vec3 ro, vec3 lightPos) {
    vec3 n = est_normal(ro);
    vec3 rd = normalize(lightPos-ro);

    vec2 r = render(ro+n*eps*2., rd);
    float brightness = .5;
    if(r.y > length(lightPos - ro))
        brightness = 1.;
    return dot(n,rd) * brightness;

}


