const vec3 ConstBackGroundCol = vec3(.25);
const float ConstBodyBorderSize = .2;
const float ConstEyesBodyRatio = .3;
const vec2 ConstEyeLoc = vec2(.35, .25);
const float ConstIrisEyeRatio = .4;

const vec3 ConstBodyColor = vec3(0.);
const vec3 ConstEyesColor = vec3(1.);
const vec3 ConstIrisColor = vec3(0.);
const vec3 ConstAngryEyesColor = vec3(1., 0., 0.);
const vec3 ConstAngryIrisColor = vec3(.5, .0, 1.);

const float PI = 2.*asin(1.);

vec2 polar(in vec2 p) {
    float dist = length(p);
    float teta = dist > .0 ? asin(p.y/dist) : .0;	// -Pi/2 -> Pi/2
    if(p.x < .0)
    	teta = 3.14 - teta;

    return vec2(dist, teta);
}    

float smoothPeriodicEvent(in float period, in float duration, in float timeOffset) {
    float val = fract((iTime + timeOffset) / period);
    val = smoothstep(.0, duration/period, val);	// _.-°¨¨¨¨¨¨|_.-°¨¨¨¨¨¨|_ [...]
    val = 2.*abs(val-.5);						// ¨\_/¨¨¨¨¨¨¨¨\_/¨¨¨¨¨¨¨¨ [...]
    val = 1.-val;								// _/¨\________/¨\________ [...]

    return val;
}             

float hcos(float v) {
    // Note: approximation of bad/cool rendering cos of too high value on some chip CG
    float d = sign(mod(v, 2.*PI)/PI - 1.)
        	* max(0., fract(mod(v, PI)/PI/0.65 - 7./13.)-.3)/.7;
    return cos(PI/2. * (1.- d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = 4.*(2.*fragCoord - iResolution.xy)/iResolution.y;

    float hashedTime = 10000.+iTime;

    // tiling
    float idY = floor(uv.y/2. + .5);
    uv.x -= .5*idY;
    vec2 id = vec2(floor(uv/2. + .5));
    uv -= 2.*id ;

    vec3 col = ConstBackGroundCol;

    // angry one - sometime
    float angry = smoothPeriodicEvent(30., 3., id.x*id.y + 2. * 5.*id.x - 7.*id.y);
    if(angry >.0) {
        vec2 rumble = .05*angry*vec2(cos((1000.*iTime)), sin(1200.*iTime));
        uv+=rumble;
    }

    // body size ~= 1. (+-cos +-sin)
    {
		vec2 pol = polar(uv);
        float bsize = pol.x
            + ConstBodyBorderSize / 10.0 *
            	(   hcos(pol.y * 10. + 2. * iTime)
                  - hcos(pol.y * 7. - 1.5 * iTime));
        float b = smoothstep(1.-ConstBodyBorderSize, 1., bsize);
        if(b < 1.) col = mix(ConstBodyColor, ConstBackGroundCol, b);
    }

    // eyes
    {
        vec2 eyeMove = vec2(.0);
        vec2 eyeLoc = ConstEyeLoc + eyeMove;

        vec2 rightLeftCorrection = vec2(1.);
        if(uv.x < .0) rightLeftCorrection.x = -1.;
        vec2 correctedEyeLoc = eyeLoc * rightLeftCorrection;

        vec2 normalizedEyeUv = (uv - correctedEyeLoc) / ConstEyesBodyRatio;
        vec2 pol = polar(normalizedEyeUv);

        float e = smoothstep(.7, 1., pol.x);
        if(e < 1.)
        {
            // in the eye
            vec3 eyesColor = ConstEyesColor;
            if(angry>.0) eyesColor = mix(eyesColor, ConstAngryEyesColor, angry);
            col = mix(eyesColor, col, e);

            // iris
            {
            	vec2 irisLoc = eyeLoc;//+ 0.5*vec2(.03 * cos(iTime), .02 * sin(iTime));
                vec2 normalizedIrisUv = (uv - irisLoc * rightLeftCorrection) / (ConstIrisEyeRatio*ConstEyesBodyRatio);
                pol = polar(normalizedIrisUv);
            	e = smoothstep(.6, 1.2, pol.x);
                vec3 irisColor = ConstIrisColor;
                if(angry>.0) irisColor = mix(irisColor, ConstAngryIrisColor, angry);
                if(e < 1.) col = mix(irisColor, col, e);
            }

            // eyelid
            {
                float closing = 1.-smoothPeriodicEvent(10., .2, 10.*(sin(30.* id.x) + .5*cos(10.*id.y)));

                vec2 lidLoc = eyeLoc;
        		vec2 correctedLidLoc = lidLoc * rightLeftCorrection;
        		vec2 normalizedLidUv = (uv - correctedLidLoc) / ConstEyesBodyRatio;
                vec2 ellipsoidLidUv = normalizedLidUv * vec2(1., 1./closing);
                float b = 1.-smoothstep(1., 1.05, length(ellipsoidLidUv));
				if(b<1.) col*=b;
            }
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

// Winning shader made at EVOKE 2019 Shader Showdown,

// The "Shader Showdown" is a demoscene live-coding shader battle competition.
// 2 coders battle for 25 minutes making a shader on stage. No google, no cheat sheets.
// The audience votes for the winner by making noise or by voting on their phone.

// "Love is somebody who will do boring things with you and not later claim to be traumatised by the experience." Christian Fitness - "The brain is not a lever"

vec2 s,v,e=vec2(.00035,-.00035);float t,tt,g,de,cr,f,ff,at,g2;vec3 np,cp,pp,rp,po,no,ld,al;vec4 su=vec4(0);
float bo(vec3 p,vec3 r){p=abs(p)-r;return max(max(p.x,p.y),p.z);}
mat2 r2(float r){return mat2(cos(r),sin(r),-sin(r),cos(r));}
vec2 fb( vec3 p)
{
    p.xz*=r2(0.2-at*0.1);
    vec2 h,t=vec2(bo(abs(p)-vec3(0,0,6),vec3(1,1,2)),5);  
    h=vec2(bo(p,vec3(0.5,0.5,6)),3);  
    h.x=min(bo(abs(p)-vec3(0,0.03,3.56),vec3(10,0.1,0.1)),h.x);  
    t=t.x<h.x?t:h;
    h=vec2(bo(abs(p)-vec3(0,0,9),vec3(0.7,0.7,6)),6);  
    t=t.x<h.x?t:h;
    h=vec2(bo(abs(p)-vec3(0,0.03,3.56),vec3(10,0.1,0.0)),6);
    h.x=min(bo(p,vec3(0.1,0.55,2.5)),h.x);  
    g2+=0.1/(0.1+h.x*h.x*400.);
    t=t.x<h.x?t:h;
    t.x*=0.7;
    return t;
}
vec2 mp( vec3 p)
{
    np=p;
    at=20.-min(length(p-vec3(0,sin(tt)*40.,0))-20.,20.);  
    for(int i=0;i<8;i++){        
        np=abs(np)-vec3(3,0,3);
        if(i>5) np.x=sin(np.x*-.7);
        np.xz*=r2(.785);
        np.xy*=r2(.785);
        np.yz-=2.-at*0.06;
    }
    vec2 h,t=fb(np);    
    h=vec2(2.5*bo(abs(np*.2-vec3(sin(tt)-0.5,0,0.5))-.9,vec3(0.01,10,0.01)),6);
    g+=0.1/(0.1+h.x*h.x*100.);
    t=t.x<h.x?t:h;    
    return t;
}
vec2 tr( vec3 ro,vec3 rd)
{
    vec2 h,t=vec2(.1);
    for(int i=0;i<128;i++){
        h=mp(ro+rd*t.x);
        if(h.x<.0001||t.x>80.)break;
        t.x+=h.x;t.y=h.y;    
    }
    if(t.x>80.)t.y=0.;  
    return t;
}
float noi(vec3 p){
    vec3 f=floor(p),s=vec3(7,157,113);
    p-=f;vec4 h=vec4(0,s.yz,s.y+s.z)+dot(f,s);
    p=p*p*(3.-2.*p);
    h=mix(fract(sin(h)*43758.5),fract(sin(h+s.x)*43758.5),p.x);
    h.xy=mix(h.xz,h.yw,p.y);
    return mix(h.x,h.y,p.z);
}
float cno(vec3 p,float k){
    float f=0.; p.z+=tt*k;
    f+=0.5*noi(p);p=2.1*p;
    f+=0.25*noi(p+1.);p=2.2*p;
    f+=0.125*noi(p+2.);p=2.3*p;
    return f;
}
float cmp( vec3 p)
{  
    float t=0.8*length(abs(p)-vec3(0,abs(sin(tt+1.59))*30.,0))-(8.-sin(p.y*.2-sin(tt)*2.+1.59)*6.);  
    return t;
}
#define a(D) clamp(mp(po+no*D).x/D,0.,1.)
#define s(D) smoothstep(0.,1.,mp(po+ld*D).x/D)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord.xy/iResolution.xy-0.5)/vec2(iResolution.y/iResolution.x,1);
    tt=mod(iTime*.8,62.83);
    vec3 ro=vec3(cos(tt*.5)*33.,4.+sin(tt*.5)*30.,sin(tt*.5)*33.),
        cw=normalize(vec3(0)-ro),
        cu=normalize(cross(cw,vec3(cos(tt*0.2-0.5),1,0))),
        cv=normalize(cross(cu,cw)),
        rd=mat3(cu,cv,cw)*normalize(vec3(uv,.4)),co,fo;
    ld=normalize(vec3(0.3,0.5,-0.5));
    co=fo=vec3(0,.2,.3)+rd.y*0.3+0.5*(1.-(length(uv)-0.2));
    s=tr(ro,rd);t=s.x;  
    if(s.y>0.){    
        po=ro+rd*t;no=normalize(e.xyy*mp(po+e.xyy).x+e.yyx*mp(po+e.yyx).x+e.yxy*mp(po+e.yxy).x+e.xxx*mp(po+e.xxx).x);
        al=vec3(1,.5,0);
        if(s.y<5.) al=vec3(0);
        if(s.y>5.) al=vec3(1);
        float dif=max(0.,dot(no,ld)),fr=pow(1.+dot(no,rd),4.),
            spo=exp2(10.*cno(np.zyx,0.)),sp=pow(max(dot(reflect(-ld,no),-rd),0.),spo);
        co=mix(sp+al*(a(.2)*a(.4)+.1)*(dif+s(.4)+s(2.)),fo,min(fr,.5));
        co=mix(co,fo,1.-exp(-.00001*t*t*t));
    }
    cr=cmp(ro)-10.+fract(dot(sin(uv*476.567+uv.yx*785.951),vec2(984.156)));
    for(int i=0;i<70;i++){
        cp=ro+rd*(cr+=1./3.);
        if(su.a>.99||cr>t) break;
        de=clamp(-.5-cmp(cp)+2.*cno(cp,10.),0.,1.);
        su+=vec4(vec3(mix(1.,0.,de)*de),de)*(1.-su.a);
    }    
    co=mix(co,su.xyz*1.5,su.a*.9); //co=mix(co,mix(su.xyz*1.5,fo,1.-exp(-.00001*cr*cr*cr)),su.a*.9);
    fragColor = vec4(pow(co+g*.05+vec3(1,0.5,0)*g2*.3,vec3(.45)),1);
}

// Author: bitless
// Title: another triangular voronoi 
// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders" and inspiration
// Thanks to aiekick and iq for some code from https://www.shadertoy.com/view/ltK3WD and https://www.shadertoy.com/view/MdSGRc

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

#define PI 3.1415926

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec2 rotate2D(vec2 _st, float _angle,vec2 m){

    _st -= m;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += m;
    return _st;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec2 m = vec2(0.5,0.5); 

    if (iResolution.y > iResolution.x ) {
        st.y *= iResolution.y/iResolution.x;
        m.y *= iResolution.y/iResolution.x;
    }       
    else {
        st.x *= iResolution.x/iResolution.y;
        m.x *= iResolution.x/iResolution.y;
    }
    vec3 color = vec3(.0);


	st = rotate2D(st,PI*0.666666*((floor(mod(iTime,15.0)/5.0)-1.0)),m);  //rotation on 120 degrees
    st = rotate2D(st,PI*0.666666*pow(smoothstep(0.0,1.0,mod(iTime+1.0,5.0))*(1.0-step(1.0,mod(iTime+1.0,5.0))),2.5),m);  //animate rotation cicle
    // Scale
    st -= m;
    st *= sin(iTime*0.2)*1.0+3.0+cos(smoothstep(0.0,1.0,mod(iTime+1.0,5.0))*(1.0-step(1.0,mod(iTime+1.0,5.0)))*PI*2.0)*0.5; 

    // Tile the space
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 9.;  // minimun distance

    vec2 p; 
    vec4 f;
    f.x = 9.;

    for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
    {	
        p = vec2(x,y); //neightbour
        p += 0.5*sin(iTime*0.2+6.2831*random2(i_st+p)); //animate
		p += .5  - f_st;

        f.y = max(abs(p.x)*.866 - p.y*.5, p.y); 
        if (f.y < f.x)
        {
            m_dist = f.x;
            f.x = f.y;
            f.zw = p;
        }
        else if( f.y < m_dist )
		{
			m_dist = f.y;
		}
    }

    m_dist -= f.x;

    vec3 n = vec3(0);

    if ( (f.x - (-f.z*.866 - f.w*.5)) 	<.0001) 	n = vec3(0.940,0.860,0.907); 
	if ( (f.x - (f.z*.866 - f.w*.5))	<.0001) 	n = vec3(0.970,0.949,0.888);
	if ( (f.x - f.w)					<.0001) 	n = vec3(0.871,0.900,0.960);

    color =  n*(0.6+length(f.x)); //base color + distance field shadow
    color -= 0.45*pow(clamp(m_dist*4.0,0.0,1.0),0.2); //edges
    color *= 1.0-smoothstep(0.0,10.0,mod(length(f.x)*100.0,15.0))*0.06; //gradient stripes
    color *= 1.0-step(2.0,mod(length(f.x)*100.0,15.0))*0.05; //thin light stripes

    fragColor = vec4(color,1.);
}

