////////////////////////////////////////////////////////////////////////////////
mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float sat(float a) {return clamp(a, .0, 1.); }

float opU(float s1, float s2) { return min(s1, s2);}

vec2 opU2(vec2 d1, vec2 d2) { return (d1.x < d2.x) ? d1 : d2; }

float opS( float d1, float d2 ) { return max(-d1,d2); }

float opI( float d1, float d2 ) { return max(d1,d2); }

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

vec3 opRep(vec3 p, vec3 c)
{
    return mod(p+0.5*c, c)-0.5*c;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdSphere(vec3 p, float r)
{
    return length(p)-r;
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdLeg(vec3 p, vec3 t, int legnum, float legsize, float smoothsize)
{
    float leg = sdSphere(p, legsize);
    for(int i=0;i<legnum;i++)
    {
        vec3 dist = (t - p)*float(i)/float(legnum);
        leg = opSmoothUnion(leg, sdSphere(p+dist, legsize), smoothsize);
    }
    return leg;
}

vec2 map(vec3 pos)
{
    vec2 t = vec2(0.0,0.0);

    // ground
    t.x = pos.y;

    // poto
    vec3 potop = pos + vec3(2., -0.5, 3.0);
    potop = opRep(potop, vec3(4.0, 0.0, 6.0));
    float prep = sdCappedCylinder(potop, .5, .3);
    float pp = opI(prep, sdBox(pos+vec3(0.0), vec3(7.0, 5.0, 10.0)));
    vec2 poto = vec2(pp, 1.0);

    // *****
    // monster
    // head
    vec3 headp = pos + vec3(sin(iTime), -3., -5.0);
    float randShape = sin(pos.y*10.+iTime)*.05;
    vec3 headpFX = headp;
    headpFX.xz += randShape;
    float head = sdSphere(headpFX, 1.0);
    // eyes
    vec3 eyep = headp + vec3(0.5, -0.05, -1.0);
    eyep.y *= .5;
    float eye = sdSphere(eyep, .05);
    eyep = headp + vec3(-0.5, -0.05, -1.0);
    eyep.y *= .5;
    eye = opU(eye, sdSphere(eyep, .05));
    // legs
    int legpartnumber = 10;
    float legSize = .1;
    float legsmoothsize = .55;
    float leg1 = sdLeg(pos + vec3(2.0, -1.0, -9.0), headp+vec3(.5, .5,-.5), legpartnumber, legSize, legsmoothsize);
    float leg2 = sdLeg(pos + vec3(-2.0, -1.0, -9.0), headp+vec3(-.5,.5,-.5), legpartnumber, legSize, legsmoothsize);
    float leg3 = sdLeg(pos + vec3(2.0, -1.0, -3.0), headp+vec3(.5, .5,-.5), legpartnumber, legSize, legsmoothsize);
    float leg4 = sdLeg(pos + vec3(-2.0, -1.0, -3.0), headp+vec3(-.5,.5,-.5), legpartnumber, legSize, legsmoothsize);

    vec2 monster = vec2(head, 2.0);
    vec2 eyes = vec2(eye, 3.0);
    vec2 legs1 = vec2(leg1, 2.0);
    vec2 legs2 = vec2(leg2, 2.0);
    vec2 legs3 = vec2(leg3, 2.0);
    vec2 legs4 = vec2(leg4, 2.0);

    // merge
    t = opU2(t, poto);
    t = opU2(t, monster);
    t = opU2(t, eyes);
    t = opU2(t, legs1);
    t = opU2(t, legs2);
    t = opU2(t, legs3);
    t = opU2(t, legs4);

    return t;
}

vec2 CastRay(vec3 ro, vec3 rd)
{
    float t = 0.0;

    for(int i=0 ; i<64 ; i++)
    {
        vec2 ray = map(ro + rd * t);

        if(ray.x < (0.0001*t))
        {
            return vec2(float(i)/64., ray.y);
        }

        t += ray.x;
    }

    return vec2(-1.0, 0.0);
}

vec3 GetNormal (vec3 p) 
{ 
    vec2 e = vec2(0.01, 0.0); 
    return normalize(vec3(
        map(p+e.xyy).x-map(p-e.xyy).x,
        map(p+e.yxy).x-map(p-e.yxy).x,
        map(p+e.yyx).x-map(p-e.yyx).x
        )); 
}

vec3 Render(vec3 ro, vec3 rd, vec2 uv)
{
    vec2 t = CastRay(ro, rd);
    vec3 pos = ro+rd*t.x;
    vec3 col = vec3(0.0);
    float depth = clamp(pos.z, 0.0, 1.0);
    depth = 1.0 - sat(pos.z*.1);

    if(t.x == -1.0)
    {

        col = vec3(pos.z);

    }
    else
    {
        float glp = clamp(length(pos)*.1, 0.0, 1.0);
        vec3 N = GetNormal(ro+rd*t.x);
        vec3 L = vec3(1.0, 1.0, 0.0);
        float light = dot(N,L);

        if(t.y == 0.0) col = vec3(t.x*1.0);
        if(t.y == 1.0) col = vec3(t.x*1.4);
        if(t.y == 2.0) col = vec3(0.0);
        if(t.y == 3.0) col = vec3(1.0, .25, 0.5);
    }

    return col;
}

vec3 GetViewDir(vec2 uv, vec3 cp, vec3 ct)
{
    vec3 forward = normalize(ct - cp);
    vec3 right = normalize(cross(vec3(0.0, -1.0, 0.0), forward));
    vec3 up = normalize(cross(right, forward));

    return normalize(uv.x * right + uv.y * up + 2.0 * forward);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 cp = vec3(0.0, 2.0, 14.);
    vec3 ct = vec3(0.0, 0.0, 0.0);

    vec3 vd = GetViewDir(uv, cp, ct);

    vec3 col = vec3(length(uv));

    col = Render(cp, vd, uv);
    col.x -= (1.0-length(uv))*.5;

    fragColor = vec4(col,1.0);
}

precision highp float;

#define MAX_ITER 2000
#define VORONOI_CELL_COUNT 128
#define PI 3.141592
#define TAU 6.28318

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// Source: https://www.shadertoy.com/view/Ms2SD1
float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

// Source: https://www.shadertoy.com/view/Ms2SD1
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

vec2 complex_mul(vec2 c1, vec2 c2) {
    return vec2(
        (c1.x * c2.x) - (c1.y * c2.y),
        (c1.x * c2.y) + (c1.y * c2.x)
    );
}

// Returns the number of iterations before escaping above 2, or max_iter.
int mandelbrot(vec2 coord, int max_iter) {
    // vec2 z = vec2(0);
    vec2 z = coord;
    vec2 c = vec2(.355, .355);
    for (int i = 0; i < max_iter; i++) {
        z = complex_mul(z, z) + c;
        if (length(z) > 2.0) return i;
    }
    return max_iter;
}

// Returns the Voronoi cell index that the point falls in
int voronoi(vec2 position) {
    vec2 points[VORONOI_CELL_COUNT];
    float minDist = 100000.0;
    int nearestIndex = -1;
    for (int i = 0; i < VORONOI_CELL_COUNT; i++) {
        points[i] = random2(vec2(float(i), float(i)));
        points[i] = 0.5 + 1.0 * sin((iTime / 8.0) + TAU * points[i]);
        float dist = distance(position, points[i]);
        if (dist < minDist) {
            minDist = dist;
            nearestIndex = i;
        }
    }
    return nearestIndex;
}

vec2 rotate(vec2 coord, float angle) {
    float sinFactor = sin(angle);
    float cosFactor = cos(angle);
    mat2 rotation = mat2(cosFactor, sinFactor, -sinFactor, cosFactor);
    return coord * rotation;
}

// Takes a fragCoord and transforms it into a usable coordinate
vec2 camera(vec2 fragCoord, vec2 center, float zoom, float angle) {
    float min_dimension = min(iResolution.x, iResolution.y);
    vec2 normalized_coord = (fragCoord - 0.5 * iResolution.xy) / min_dimension;
    return rotate(normalized_coord * zoom, angle) + center;
}

vec3 subsample(vec2 mandelbrot_coord, vec2 voronoi_coord) {
    int max_iter = min(int(iTime * 8.0), MAX_ITER);
    int mandel = mandelbrot(mandelbrot_coord, max_iter);
    float mandel_norm = float(mandel) / float(max_iter);

    int voronoi_index = voronoi(voronoi_coord);
    float voronoi_norm = (float(voronoi_index) / float(VORONOI_CELL_COUNT));

    vec3 color;
    if (mandel < max_iter) {
        float variance = sin(mandel_norm * TAU * 16.0) / 8.0 + 0.4;
        color = hsv2rgb(vec3(
            sin((mandel_norm * TAU / 8.0) + (iTime * TAU / 32.0) + (voronoi_norm * TAU / 16.0)) / 2.0 + 0.5,
            variance,
            variance
        ));
    } else {
        float height = 0.0;
        for (int i = 1; i < 16; i++) {
            vec2 right_movement = vec2(-iTime / 32.0, 0);
            height += noise((voronoi_coord + vec2(1, 0) + rotate(right_movement, 0.0)) * float(i) * 32.0) / float(i);
            height += noise((voronoi_coord + vec2(0, 1) + rotate(right_movement, TAU / 3.0)) * float(i) * 32.0) / float(i);
            height += noise((voronoi_coord + rotate(right_movement, TAU / 3.0 * 2.0)) * float(i) * 32.0) / float(i);
        }
        height /= 2.0;
        height += 0.5;
        color = hsv2rgb(vec3(
            iTime / 16.0 + height / 8.,
            height / 4.0 + 0.4,
            height / 4.0 + 0.4
        ));
    }

    return color;
}

mat4x2 split_fragment(vec2 fragCoord) {
    float distToCenter = 0.25;
    return mat4x2(
        vec2(fragCoord.x - distToCenter, fragCoord.y - distToCenter),
        vec2(fragCoord.x + distToCenter, fragCoord.y + distToCenter),
        vec2(fragCoord.x + distToCenter, fragCoord.y - distToCenter),
        vec2(fragCoord.x - distToCenter, fragCoord.y + distToCenter)
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    mat4x2 subsamples = split_fragment(fragCoord);

    // vec2 mandelbrot_center = vec2(-1.483, 0);
    // vec2 mandelbrot_center = vec2(-0.09, 0.650004);
    vec2 mandelbrot_center = vec2(-0.091069, 0.65899);
    float mandelbrot_zoom = (8.0 / float(pow(1.5, iTime / 2.0)));
    float mandelbrot_angle = 0.;//iTime / 8.0;
    mat4x2 mandelbrot_coords = mat4x2(
        camera(subsamples[0], mandelbrot_center, mandelbrot_zoom, mandelbrot_angle),
        camera(subsamples[1], mandelbrot_center, mandelbrot_zoom, mandelbrot_angle),
        camera(subsamples[2], mandelbrot_center, mandelbrot_zoom, mandelbrot_angle),
        camera(subsamples[3], mandelbrot_center, mandelbrot_zoom, mandelbrot_angle)
    );

    vec2 voronoi_center = vec2(0.5);
    float voronoi_zoom = 1.0;
    float voronoi_angle = iTime / -16.0;
    mat4x2 voronoi_coords = mat4x2(
        camera(subsamples[0], voronoi_center, voronoi_zoom, voronoi_angle),
        camera(subsamples[1], voronoi_center, voronoi_zoom, voronoi_angle),
        camera(subsamples[2], voronoi_center, voronoi_zoom, voronoi_angle),
        camera(subsamples[3], voronoi_center, voronoi_zoom, voronoi_angle)
    );

    // Uncomment to apply anti-aliasing
    vec3 col = vec3(0);
    col += subsample(mandelbrot_coords[0], voronoi_coords[0]);
    //col += subsample(mandelbrot_coords[1], voronoi_coords[1]);
    //col += subsample(mandelbrot_coords[2], voronoi_coords[2]);
    //col += subsample(mandelbrot_coords[3], voronoi_coords[3]);
    //col /= 4.;

    fragColor = vec4(col, 1.0);
}

// modification of https://www.shadertoy.com/view/ldX3Wn

const float Pi = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

	for(int i=1;i<45;i++)
	{
		vec2 newp=p;
		newp.x+=(0.5/float(i))*cos(float(i)*p.y+iTime*11.0/37.0+0.03*float(i))+1.3;		
		newp.y+=(0.5/float(i))*cos(float(i)*p.x+iTime*17.0/41.0+0.03*float(i+10))+1.9;
		p=newp;
	}

	vec3 col=vec3(0.5*sin(3.0*p.x)+0.5,0.5*sin(3.0*p.y)+0.5,sin(1.3*p.x+1.7*p.y));
	fragColor=vec4(col, 1.0);
}

