#define AO_SAMPLES 50

// comment/uncomment these to turn different effects on/off
//#define LIGHTS
#define AMBIENT_LIGHT
//#define COLOURISE_AMBIENT

// hide the errors of low sample counts by varying them per-pixel
//#define DITHER

const float phi = 1.6180339887498948;
const float tau = 6.2831853071795865;

uvec2 quasi2 = uvec2(0xC13FA9A9u,0x91E10DA5u);
uvec3 quasi3 = uvec3( 3518319155, 2882110345, 2360945575 );

float sdf( vec3 p )
{
    return
        min(min(min(min(min(
        	max(max(max(
                length(p)-1.,
                .3 - length((p-vec3(.5,0,0)).xz)),
                .3 - length((p-vec3(-.25,0,-.5)).xz)),
                dot(p,normalize(vec3(-1,0,-2)))-.4 + .003*sin(p.y*100.)
            ),
            max(max(
                length(p-vec3(0,-.5,1.2))-.5,
                .45-length(p-vec3(0,-.5,1.2))),
                dot(p-vec3(0,-.5,1.2),normalize(vec3(1,2,-3)))
            )),
            length(p-vec3(0,-.5,1.2))-.3),
			max(
                length(p.xz)-.5,
                p.y+.9
            )),
            max(
                length((p-vec3(-.25,0,-.5)).xz) - .22,
                dot(p,normalize(vec3(-1,4,-2)))-.95
            )),
            p.y + 1.
        );
}


float OcclusionTrace( vec3 pos, vec3 target, float rpert )
{
    // higher quality setting, better for direct lights
    const int maxSteps = 150; const float stepSize = .5;

    vec3 toTarget = target-pos;
    float end = length(toTarget);
    vec3 ray = toTarget/end;
    float t = .05;
    float visibility = 1.;
    for ( int i=0; i < maxSteps; i++ )
    {
        float h = sdf( ray*t+pos );
        if ( t >= end ) break;
        float r = rpert*t;
        visibility = min( visibility, smoothstep( -r, r, h ) );
        if ( visibility <= 0. ) break;
        t += (h+r) * stepSize;
    }
	return visibility;
}


void mainImage( out vec4 fragColour, in vec2 _fragCoord )
{
    uint seed = uint(_fragCoord.x)*quasi2.x
        		+ uint(_fragCoord.y)*quasi2.y;
#ifndef DITHER
    seed = 0x80000000u;
#endif
    vec2 jitter = vec2( quasi2 * seed ) / exp2(32.);
    vec2 fragCoord = _fragCoord + jitter;

    vec3 ray = vec3( (fragCoord-iResolution.xy*.5)/sqrt(iResolution.x*iResolution.y), 2 );

    vec3 camPos = vec3( 0, 0, -8. );
    vec2 a = vec2(.3,iTime*.3);//-1.5);//
    vec2 d = vec2(-1,1);
    camPos.yz = camPos.yz*cos(a.x) + camPos.zy*d.xy*sin(a.x);
    camPos.zx = camPos.zx*cos(a.y) + camPos.xz*d.xy*sin(a.y);

    vec3 camTarget = vec3(0);

    vec3 camK = normalize(camTarget - camPos);
    vec3 camI = normalize(cross(vec3(0,1,0),camK));
    vec3 camJ = cross(camK,camI);

    ray = ray.x*camI + ray.y*camJ + ray.z*camK;
    ray = normalize( ray );

    float t = 0.;
    for ( int i=0; i < 200; i++ )
    {
        float h = sdf(ray*t + camPos);
        t += h;
        if ( h < .001 )
        {
            break;
        }
    }

    vec3 pos = ray*t+camPos;

    d *= .001;
    vec3 normal =
        normalize(
            sdf(pos+d.xxx)*d.xxx +
            sdf(pos+d.yyx)*d.yyx +
            sdf(pos+d.yxy)*d.yxy +
            sdf(pos+d.xyy)*d.xyy
        );

    struct Light
    {
        vec3 pos;
        vec3 col;
    };

    Light lights[] = Light[]
        (
            //Light( vec3(4,8,-6), vec3(50) ),
            //Light( vec3(.7,-.7,0), vec3(5,0,0) ) // shows multiple bugs with the shadow tech - all fixed by increasing loop limit on shadow test!
            /*Light( vec3(4,8,-6), vec3(50,0,0) ),
            Light( vec3(4,8,-5), vec3(0,50,0) ),
            Light( vec3(4,8,-4), vec3(0,0,50) )*/
            Light( vec3(.5,0,2), vec3(0,1,0) ),
            Light( vec3(2,-.5,.5), vec3(1,.3,0) ),
            Light( vec3(1,.5,-1.2), vec3(1,0,0) ),
            Light( vec3(-.25,-.5,-1), vec3(1,0,.7)*.2 ),
            Light( vec3(-1,1,.5), vec3(0,.2,1)*2. )
        );

    fragColour.rgb = vec3(0);

    for ( int i=0; i < lights.length(); i++ )
    {
        vec3 toLight = lights[i].pos - pos;
        float lightStrength = max(0.,dot(normal,normalize(toLight)));
        lightStrength /= dot(toLight,toLight);
        lightStrength *= OcclusionTrace( pos, lights[i].pos, .05 );
#ifdef LIGHTS
    	fragColour.rgb += lights[i].col*lightStrength;
#endif
    }



    // sample SDF at various points to give a blurred version of scene, and divide by expected value if we're flat
    // i.e. it's both a blur and a bunch of actual samples
//    float ao = min(0.,sdf( pos+normal*.3 )/.3)+1.;

    vec3 ambient = vec3(0);
    float weightSum = 0.;
    for ( int i=0; i < AO_SAMPLES; i++ )
    {
        vec3 rand = vec3(quasi3*(uint(i)+seed))/exp2(32.);

        // distribute randomly on hemisphere
        // n.b. I adapted this technique from somewhere but can't recall where
        // uniformly distribute on unit cylinder about z
        vec3 tap = vec3( cos(rand.x*tau), sin(rand.x*tau), rand.y*2.-1. );
        // project radially inward onto sphere - this gives uniform distribution of samples
        tap.xy *= sqrt(1.-tap.z*tap.z);
        // project perpendicular to normal onto a curved-cone pointing along normal
        float sdn = dot(tap,normal);
        float l = .5+.5*sdn;
        // ignore the height along the cone - so we get a disc (perpendicular to normal)
        // curve the cone (sqrt) so distribution is uniform on disc
        tap = normalize(tap-sdn*normal)*sqrt(l); // there's a faster way to do that normalize
        // project along normal onto hemisphere
        tap += normal*sqrt(1.-l);

        // randomize the distance of the sample
        float dist = rand.z;//(float(i)+float(seed)/exp2(32.))/float(AO_SAMPLES);//
        tap *= exp2( mix( -5., 3., dist ) ); // interesting how this trades big vs small details

//I want this to be a min AND an average, hmm...
/*yeah, I want it to approximate occlusion in this direction, and min with other samples that approximate in this direction
but add to ones in other directions
and I want to do that without paying attention to direction
=> ignore the direction and just average the samples
or, have a factor for how much to treat the samples as exclusive
what about something logarithmic, so it sort of works like a min and an add?

preferably fading confidence of samples with distance, so close ones definitely occlude, more distant ones nudge the average by less
n.b. could do 2D rand and start close
*/
//        float aotap = clamp( sdf( pos+tap )/dot(tap,normal) + 1., .001, 1. );
        //ao = min( ao, aotap );
//        ao += log2(aotap) * 2.*(1.-dist);

		// take full occlusion as the norm, then add on points which defy that

        float weight = 1.;
        float ao = clamp( sdf( pos+tap )/dot(tap,normal),
                        0., // pushing this below 0 darkens shadows
                        1.3 // amp them up if they're very positive! - this gives a fake drybrushing effect that I quite like
                       );

#ifdef COLOURISE_AMBIENT
	    vec3 ambientColour = mix( vec3(1,.3,0), vec3(0,.3,1), normalize(tap).x*.5+.5 );
#else
    	vec3 ambientColour = vec3(.7);
#endif

        ambient += ao*ambientColour;

        weightSum += weight;
    }
    ambient /= weightSum;



#ifdef AMBIENT_LIGHT
    fragColour.rgb += ambient;
#endif

    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));

//    fragColour.rgb = sin(fragColour.rgb*6.-1.+iTime*.3)*.5+.5; // AWESOME

    fragColour.a = 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 Colors[4];

    Colors[0] = vec4(1.0,0.0,0.0,1.0);
    Colors[1] = vec4(0.0,1.0,0.0,1.0);
    Colors[2] = vec4(0.0,0.0,1.0,1.0);
    Colors[3] = vec4(1.0,0.5,0.0,1.0);


    // Normalized pixel coordinates (from 0 to 1)
    vec2 UV = fragCoord/iResolution.xy;
    UV.t = 1.0-UV.t;

    vec4 LinearResult = mix(mix(Colors[0],Colors[1],UV.s),mix(Colors[2],Colors[3],UV.s),UV.t);
    vec4 BarycentricResult;

    if ( (1.0-UV.s)>UV.t )
    {
        float Areas[3];
        Areas[0] = 0.5*length(cross(vec3(1.0,0.0,0.0),vec3(UV,0.0)));
        Areas[1] = 0.5*length(cross(vec3(0.0,1.0,0.0),vec3(UV,0.0)));
        Areas[2] = 0.5-Areas[0]-Areas[1];

        float Weights[3];       
        Weights[0] = Areas[0]/0.5;
        Weights[1] = Areas[1]/0.5;
        Weights[2] = Areas[2]/0.5;

    	BarycentricResult = Weights[0]*Colors[2]+Weights[1]*Colors[1]+Weights[2]*Colors[0];
    }
    else
    {
        float Areas[3];
        Areas[0] = 0.5*length(cross(vec3(1.0,0.0,0.0),vec3(vec2(1.0)-UV,0.0)));
        Areas[1] = 0.5*length(cross(vec3(0.0,1.0,0.0),vec3(vec2(1.0)-UV,0.0)));
        Areas[2] = 0.5-Areas[0]-Areas[1];

        float Weights[3];       
        Weights[0] = Areas[0]/0.5;
        Weights[1] = Areas[1]/0.5;
        Weights[2] = Areas[2]/0.5;

    	BarycentricResult = Weights[0]*Colors[1]+Weights[1]*Colors[2]+Weights[2]*Colors[3];
    }

    fragColor = mix(LinearResult,BarycentricResult,pow(0.5+0.5*cos(2.0*iTime),4.0));
    //fragColor = BarycentricResult;
}

#define MAX_MARCH_STEPS 100
#define MAX_MARCH_DIST 120.
#define D_SURF_DIST 0.001

float df_movingBlob(vec3 point, vec4 sphere)
{
    float m = 3.0;
    point = mod(point, m) - 0.5*m;
	return 0.06*sin((point.x+iTime*0.51)*15.)+length(point - sphere.xyz) - sphere.w;
}

float getDist(vec3 point)
{
    vec4 sphere = vec4(0.,0.,1.5,.5); // w component is the radius.
    float sphereDist = df_movingBlob(point,sphere);
    return sphereDist;
}

vec3 getNormalAtPoint( in vec3 p )
{
    const float eps = 0.0001;
    const vec2 h = vec2(eps,0);
    return normalize( vec3(getDist(p+h.xyy) - getDist(p-h.xyy),
                           getDist(p+h.yxy) - getDist(p-h.yxy),
                           getDist(p+h.yyx) - getDist(p-h.yyx) ) );
}

float rayMarch(vec3 rayOrigin, vec3 rayDirection, out int steps)
{
    float d0=0.;
    steps = MAX_MARCH_STEPS;
	for(int i=0; i < MAX_MARCH_STEPS; ++i)
    {
        vec3 currPos = rayOrigin + rayDirection*d0;
        float dist = getDist(currPos);
        d0+=dist;
        if(d0 > 10. + abs(120.*sin(iTime*.5)) || dist < D_SURF_DIST)
        {
            steps = i;
            break;
        }
    }
    return d0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5*iResolution.x)/iResolution.y;

    vec3 camPos = vec3(sin(iTime*2.5),cos(iTime*1.5),iTime*4.4);
    vec3 rayDir = normalize(vec3(uv.x, uv.y, 1));

    int steps;
    float distanceToScene = rayMarch(camPos, rayDir, steps);

    vec3 p = camPos + rayDir * distanceToScene;
    float dli = float(steps)/float(MAX_MARCH_STEPS);
    vec3 col = vec3(0,dli,dli);

    fragColor = vec4(col,1.0);
}

const vec3 lightPos  = vec3(0.,4.,-4.);


vec3 palette(float i) {
    float time = iTime;
    float T = 1400. + 1300.*i; 
    vec3 L = vec3(4.4, 5.6 + sin(time)/2., 6.4 + sin(time/2.)); 
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    return 1.0-exp(-5e8/L); 
}    

vec3 hash33(vec3 p) { 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

float voronoi(vec3 p) {
	vec3 b, r, g = floor(p);
	p = fract(p); 
	float d = 1.; 
	for(int j = -1; j <= 1; j++) {
	    for(int i = -1; i <= 1; i++) {
		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));	
	    }
	}
	return d;
}

float noiseLayers(in vec3 p) {
        float time = iTime;
    vec3 t = vec3(0., 0., p.z+time*1.7); 
    float tot = 0., sum = 0., amp = 1.; 
    for (int i = 0; i < 11; i++) {
        tot += voronoi(p + t) * amp; 
        p *= 2.0; 
        t *= 1.5; 
        sum += amp; 
        amp *= 0.5; 
    }
    return tot/sum; 
}


float sdBox( vec3 p, vec3 b ){
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

float dot2( in vec2 v ) { 
  return dot(v,v); 
}

float sdCappedCone( in vec3 p, in float h, in float r1, in float r2 ){
    vec2 q = vec2( length(p.xz), p.y );
    vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

vec2 rotate(vec2 p, float ang) {
    float c = cos(ang), s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec2 repeatAng(vec2 p, float n) {
    float ang = 2.0*3.14/n;
    float sector = floor(atan(p.x, p.y)/ang + 0.5);
    return rotate(p, sector*ang);
}

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float spikes(vec3 p, float r, float count) {
    p.xz = repeatAng(p.xz, count);
    return sdCappedCone(p - vec3(0., 1.27, r), .07, .06, .01);
}

float legs(vec3 p) {
    p.xz = repeatAng(p.xz, 3.);
    return sdBox(p+vec3(0., 1.03, -1.05), vec3(.16,.2,.15));
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h ) {
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

vec2 map(vec3 p) {
     float time = iTime;
 // pepelatz body
    float d = sdCappedCone(p, 1.2, 1.2, 1.);
    d = smin(d, legs(p), 0.01);
    d = smin(d, sdCappedCone(p+vec3(0.,-1.35,0.), .16, .2, .187), 0.01);

    // door
    vec3 pd = vec3(p.x,rotate(p.yz, -0.08));
    d = smin(d, sdBox(pd+vec3(.0, 0.2, 1.), vec3(0.34,.7,.08)), 0.03);
    vec3 pd1 = vec3(p.x,rotate(p.yz, 1.57));
    d = smin(d, sdCappedCone(pd1 + vec3(0.,-1.,-0.3), .09, .15, .15), 0.1);
    d = max(d, -sdCappedCone(pd1 + vec3(0.,-1.,-0.3), .12, .13, .13));
    d = smin(d, sdCappedCone(pd+vec3(-0.1,0.9,1.1), .16, .02, .02), 0.02);
    d = smin(d, sdCappedCone(pd+vec3( 0.0,0.9,1.1), .16, .02, .02), 0.02);
    d = smin(d, sdCappedCone(pd+vec3( 0.1,0.9,1.1), .16, .02, .02), 0.02);

    // roof spikes
    d = smin(d, spikes(p,0.94,50.), 0.01);
    d = smin(d, spikes(p,0.80,45.), 0.01);
    d = smin(d, spikes(p,0.65,35.), 0.01);
    d = smin(d, spikes(p,0.45,25.), 0.01);

    // antenna
    vec2 r = rotate(p.xz, time*3.);
    vec3 pa = vec3(r.x, p.y-1.55, r.y);
    vec3 pa1 = vec3(pa.x,rotate(pa.yz, 1.57));
    pa1.z -= 0.2;
    d = smin(d, sdBox(pa, vec3(0.01,.1,.1)), 0.01);
    d = smin(d, sdBox(pa, vec3(0.1,.01,.7)), 0.01);
    d = smin(d, sdCappedCone(pa1-vec3(0.,0.05,0.), .2, .1, .1), 0.1);
    d = max(d, -sdCappedCone(pa1, .30, .09, .09));
    d = max(d, -sdBox(pa1-vec3(-0.08,0.,-0.1), vec3(0.08,.3,.09)));  



    return vec2(d, 2.5);
}

vec3 rayDirection(float fieldOfView, vec2 size) {
    vec2 xy = gl_FragCoord.xy - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
	vec3 f = normalize(center - eye);
	vec3 s = normalize(cross(f, up));
	vec3 u = cross(s, f);
	return mat4(
		vec4(s, 0.0),
		vec4(u, 0.0),
		vec4(-f, 0.0),
		vec4(0.0, 0.0, 0.0, 1)
	);
}

vec2 castRay( vec3 ro, vec3 rd ) {
    float tmin = 1.0;
    float tmax = 32.0;
    float t = tmin;
    float m = -1.0;
    for ( int i=0; i<64; i++ ) {
	    float precis = 0.0001*t;
	    vec2 res = map( ro+rd*t );
        if ( res.x<precis || t>tmax )
            break;
        t += res.x;
	    m = res.y;
    }
    if ( t>tmax )
        m=-1.0;
    return vec2( t, m );
}

const float NORMAL_EPSILON = 0.0005;

vec3 estimateNormal(vec3 pos) {
    vec2 e = vec2(1.0,-1.0)*0.5773*NORMAL_EPSILON;
    return normalize( e.xyy*map( pos + e.xyy ).x +
					  e.yyx*map( pos + e.yyx ).x +
					  e.yxy*map( pos + e.yxy ).x +
					  e.xxx*map( pos + e.xxx ).x );
}

const vec3 K_a = vec3(.5, .5, .5);
const vec3 K_d = vec3(.5, .5, .5);
const vec3 K_s = vec3(.0, .0, .0);

const float shininess = 3.5;
const vec3 lightIntensity = vec3(1., 1., 1.);

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, 
                          vec3 p, vec3 N, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));
    float dotLN = dot(L, N);
    float dotRV = dot(R, V);
    if (dotLN < 0.0) 
        return vec3(0.0, 0.0, 0.0);
    if (dotRV < 0.0) 
        return lightIntensity * (k_d * dotLN);
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, 
                       float alpha, vec3 p, vec3 nor, 
                       vec3 eye, vec3 materialColor) {
    vec3 color = materialColor * k_a;
    color += phongContribForLight(k_d, k_s, alpha, p, nor, eye, 
                                  lightPos, lightIntensity);
    return color;
}

vec3 decodeMaterial(float m) {
    return vec3(1.-m/3.);
}

vec3 phong(vec3 p, vec3 nor, vec3 eye, float material) {
    vec3 materialColor = decodeMaterial(material);
    return phongIllumination(K_a, K_d, K_s, shininess, p, nor, eye, materialColor);
}

float softShadow( in vec3 ro, in vec3 rd, in float mint, in float tmax ){
	  float res = 1.0;
    float t = mint;
    for( int i=0; i!=16; i++ ) {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float ao( vec3 pos, vec3 nor ){
	  float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i!=5; i++ ){
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}


float fbm( vec3 p, vec3 n )
{
	p *= 0.25;
	float x = noise(  p.yz );
	float y = noise(  p.zx );
	float z = noise(  p.xy );
	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}


vec3 doBumpMap( in vec3 pos, in vec3 nor ){
    float e = 0.0015;
    float b = 0.01;

	float ref = fbm( 28.0*pos, nor );
    vec3 gra = -b*vec3( fbm(28.0*vec3(pos.x+e, pos.y, pos.z),nor)-ref,
                        fbm(28.0*vec3(pos.x, pos.y+e, pos.z),nor)-ref,
                        fbm(28.0*vec3(pos.x, pos.y, pos.z+e),nor)-ref )/e;

	vec3 tgrad = gra - nor * dot ( nor , gra );
    return normalize ( nor - tgrad );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float time = iTime;
    float r = sin(time)*7. + 15.;
    vec3 eye = vec3(sin(time)*r,5.,cos(time)*r);
	vec3 lookAt = vec3(0.,2.,0.);

	vec3 direction = rayDirection(60.0, iResolution.xy);
    mat4 viewToWorld = viewMatrix(eye, lookAt, vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(direction, 0.0)).xyz;
    vec2 dist = castRay(eye, worldDir);
    if (dist.x > 32.) {
        vec2 uv = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
        uv += vec2(sin(time/7.)*0.1,cos(time/3.)*0.15);
    float cs = cos(time/5.0), 
        si = sin(time/2.0);
    uv.xy *= mat2(cs, -si, si, cs); 
        vec3 rd = normalize(vec3(uv.x, uv.y, 3.1415/(75.+sin(time)*32. + cos(time*10.)*3.)));
        float d = noiseLayers(rd*2.);
        fragColor = vec4(sqrt(palette(d)), 1.); 
        fragColor /= sqrt(sqrt(dot(uv, uv)));
    } else {
        vec3 pt = eye + dist.x * worldDir;
        vec3 nor = estimateNormal( pt );
          nor = doBumpMap(pt, nor);
        float occ = ao( pt, nor );
        float shadow = softShadow( pt, normalize(lightPos-pt), 0.1, 22.2);
        vec3 color = phong(pt, nor, eye, dist.y)*sqrt(occ);
        color += color * shadow;
        fragColor = vec4(color, 1.0);
    }

}

/*

	Hexagon Grid Pattern
	--------------------

    I see variations on this particular pattern all over the place, and it's pretty
	easy to make, so I figured I'd recreate one. If it's not obvious, the idea is to
	partition space into hexagonal cells, then render six smaller overlapping hexagons 
	in each of the cell's six corners. Commenting in the "SHOW_GRID" define should 
    make it more clear. 

	On a side note, there are countless other interesting hexagonal stock imagery 
    patterns on the internet that I'd love to see on Shadertoy, if anyone feels like 
	making any. :)


    Other Hexagonal Pattern Examples:


	Shadertober Day 10 - Pattern -- BackwardsCap
	https://www.shadertoy.com/view/tsV3Rd

    Hexagon Pattern -- plabatut 
	https://www.shadertoy.com/view/Wdt3WN

    Impossible Chainmail -- BigWIngs 
    https://www.shadertoy.com/view/td23zV

*/

//#define SHOW_GRID

#define FLAT_TOP


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.617, 57.743)))*43758.5453); }


// Flat top hexagon, or pointed top.
#ifdef FLAT_TOP
const vec2 s = vec2(1.732, 1);
#else
const vec2 s = vec2(1, 1.732);
#endif

// Hexagonal bound: Not technically a distance function, but it's
// good enough for this example.
float getHex(vec2 p){

    // Flat top and pointed top hexagons.
    #ifdef FLAT_TOP
    return max(dot(abs(p.xy), s/2.), abs(p.y*s.y));
    #else   
    return max(dot(abs(p.xy), s/2.), abs(p.x*s.x));
    #endif
}

// Hexagonal grid coordinates. This returns the local coordinates and the cell's center.
// The process is explained in more detail here:
//
// Minimal Hexagon Grid - Shane
// https://www.shadertoy.com/view/Xljczw
//
vec4 getGrid(vec2 p){

    vec4 ip = floor(vec4(p/s, p/s - .5));
    vec4 q = p.xyxy - vec4(ip.xy + .5, ip.zw + 1.)*s.xyxy;
    return dot(q.xy, q.xy)<dot(q.zw, q.zw)? vec4(q.xy, ip.xy) : vec4(q.zw, ip.zw + .5);
    //return getHex(q.xy)<getHex(q.zw)? vec4(q.xy, ip.xy) : vec4(q.zw, ip.zw + .5);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){


    // Aspect correct screen coordinates.
    float res = min(iResolution.y, 800.);
    vec2 uv = (fragCoord.xy - iResolution.xy*.5)/res;

    // Scaling and translation.
    const float sc = 3.;
    vec2 p = uv*sc - vec2(-1, -.5)*iTime/2.;

    // Smoothing factor.
    float sf = 1./res*sc;


    // Hexagonal grid coordinates.
    vec4 p4 = getGrid(p);


    // Hexagon vertex IDs. They're useful for neighboring edge comparisons, etc.
    // Multiplying them by "s" gives the actual vertex postion.
    #ifdef FLAT_TOP
    // Vertices: Clockwise from the left.

	vec2[6] vID = vec2[6](vec2(-1./3., 0), vec2(-1./6., .5), vec2(1./6., .5), 
                          vec2(1./3., 0), vec2(1./6., -.5), vec2(-1./6., -.5)); 

    //vec2[6] eID = vec2[6](vec2(-.25, .25), vec2(0, .5), vec2(.25, .25), 
                         // vec2(.25, -.25), vec2(0, -.5), vec2(-.25, -.25));

    #else
    // Vertices: Clockwise from the bottom left. -- Basically, the ones 
    // above rotated anticlockwise. :)
    vec2[6] vID = vec2[6](vec2(-.5, -1./6.), vec2(-.5, 1./6.), vec2(0, 1./3.), 
                          vec2(.5, 1./6.), vec2(.5, -1./6.), vec2(0, -1./3.));

    //vec2[6] eID = vec2[6](vec2(-.5, 0), vec2(-.25, .25), vec2(.25, .25), vec2(.5, 0), 
                          //vec2(.25, -.25), vec2(-.25, -.25));

    #endif


    // The scene color.
    vec3 col = vec3(1);


    // Rendering the six overlapping hexagons within each cell.
    for(int i = 0; i<6; i++){


        // Corner hexagon.
        vec2 q = abs(p4.xy - vID[5-i]*s*.5);
        float hx = getHex(q) - .265;
        float oHx = hx;

        // Using the neighboring hexagon to chop out one third. This way, the final
        // hexagon will look like it's tucked in behind the previous one... Comment
        // out the third (hx) line to see what I mean. By the way, you don't have to
        // do this, but I prefer this particular look.
        q = abs(p4.xy - vID[(5-i + 5)%6]*s/2.);
        float hx2 = getHex(q) - .27;
        hx = max(hx, -hx2);

        // Using the triangle wave formula to render some concentric lines on each
        // hexagon.
        float pat = (1. - abs(fract(oHx*16. + .2) - .5)*2.) - .55;
        pat = smoothstep(0., .2, pat);

        // Rendering the chopped out hexagon and a smaller white center.
        col = mix(col, vec3(1)*pat, 1. - smoothstep(0., sf, hx));  
        col = mix(col, vec3(1), 1. - smoothstep(0., sf, max(oHx + .22, -hx2)));
        // A colorful center, if preferred.
        //col = mix(col, vec3(1, .05, .1), 1. - smoothstep(0., sf, max(oHx + .22, -hx2))); 

        // Applying a shadow behind the hexagon. I thought it added more visual interest, 
        // but for something like wallpaper, or whatever, you could comment it out.
        vec3 sh = mix(col, vec3(0), (1. - smoothstep(0., sf, hx)));
        col = mix(col, sh, (1. - smoothstep(0., sf*8., max(max(hx, hx2), - hx2)))*.5);

    }


    // Rendering the grid boundaries, or just some black hexagons in the center.
    float gHx = getHex(p4.xy);

    #ifdef SHOW_GRID 
    // Grid lines.
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, abs(gHx - .5) - .035));  
    col = mix(col, vec3(1, .05, .1)*1.5, 1. - smoothstep(0., sf, abs(gHx - .5) - .0075));  
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*5., gHx - .02 - .025))*.5);
    // Colored center hexagon.
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, gHx - .02 - .025));   
    col = mix(col, vec3(1, .05, .2), 1. - smoothstep(0., sf, gHx - .015));   
    #else
    // Small shadowed center hexagon.
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*5., gHx - .02))*.5);   
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, gHx - .02));  
    #endif

    // Vignette.
    //uv = fragCoord/iResolution.xy;
    //col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .0625);

    // Rough gamma correction.
    fragColor = vec4(sqrt(max(col, 0.)), 1);;
}

float lengthSq(vec2 v)
{
    return dot(v, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / vec2(min(iResolution.x, iResolution.y)) * 2.;

    vec2 a = vec2(-.5, sin(iTime * .3));
    vec2 b = vec2(0. , sin(iTime * .5));
    vec2 c = vec2( .5, sin(iTime * .7));

    vec3 col = vec3(lengthSq(a - uv),
                    lengthSq(b - uv),
                    lengthSq(c - uv));

    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tme = iTime*.3;
    float scale = 0.125;

    vec2 adjVec = vec2((sin(tme)*.5), (cos(tme)*.5));
    vec2 uv = fragCoord/iResolution.xy;    
    vec2 uvM;
    float posMod;
    float normX;
    float normY;

    //INTERACTIVE: Mouse X controls flight speed, Mouse Y controls depth
    if(iMouse.y>0. || iMouse.x>0.){
    	normX = ((iMouse.x)-1.)/iResolution.x;
    	normY = ((iMouse.y)-1.)/iResolution.y;
    }
    else{
        normX = 0.2;
        normY = 0.2;
    }
    float zoomMult = pow(max(normY,0.001)*5., .5);
    float posModGradA;
    float posModGradB;
    posModGradA = (cos(tme*1.1)*.9 + .1)/2.+.5;
    posModGradB = (cos(tme*.9)*.9 + .1)/2.+.5;
    //uv = vec2(pow((uv.x-.5)*length(normalize(uv-0.5))*5., -.05)*posMod, pow((uv.y-.5)*length(normalize(uv-0.5))*5., -.05)*posMod);
    //uv = vec2(pow((uv.x-.5)*length(uv-0.5)*5., -.07)*posMod*2., pow((uv.y-.5)*length(uv-0.5)*5., -.07)*posMod*2.);
    uv = vec2(pow(abs(uv.x-.5)+length(uv-.5), -.07)*zoomMult*2., pow(abs(uv.y-.5)+length(uv-.5), -.07)*zoomMult*2.);
    uvM = mod(uv+fract((tme)*normX)*5.*(normY*.75+.25), scale);

    uv = normalize(uvM-vec2(scale/2.))*(adjVec+vec2(.5));

    if(mod(floor((uv.x)/scale),2.) == 0.)
            uvM.x = scale - uvM.x;
    if(mod(floor((uv.y)/scale),2.) == 0.)
            uvM.y = scale - uvM.y;

    uvM += adjVec*length(uvM + adjVec - vec2(0.5));

    // Time varying pixel color
    /*vec3 col = vec3(0.5 + 0.5*tan((cos(tme)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.)))),
                    0.5 + 0.5*tan((sin(tme*1.2)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.)))),
                    0.5 + 0.5*tan((cos(tme*1.3)+3.)*8.*(uvM.x/sin(uvM.y+0.2+(sin(tme)/2.+1.)))));*/

    // With uvM and uv gradients
    vec3 col = vec3(0.5 + 0.5*tan((cos(tme)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.))))*(((cos(length(uv*posModGradA*100.))*.4+.6)+(cos(length(uvM*posModGradB*100.))*.4+.6))/2.),
                    0.5 + 0.5*tan((sin(tme*1.2)+2.)*8.*(uvM.x/sin(uvM.y+0.2+(cos(tme)/2.+1.))))*(((cos(length(uv*posModGradA*100.))*.4+.6)+(cos(length(uvM*posModGradB*100.))*.4+.6))/2.),
                    0.5 + 0.5*tan((cos(tme*1.3)+3.)*8.*(uvM.x/sin(uvM.y+0.2+(sin(tme)/2.+1.)))))*(((cos(length(uv*posModGradA*100.))*.4+.6)+(cos(length(uvM*posModGradB*100.))*.4+.6))/2.);
    // Output to screen
    fragColor = vec4(col,1.0);
}

// Author: bitless
// Title: Colormixing Metaballs
// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders" and inspiration 

// translate color from HSB space to RGB space
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float rand (vec2 st) {
    float f = fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
    return max(f,0.2);
}

vec3 random_color (vec2 p){
    return hsb2rgb(vec3(rand(p),0.6,1.0));
 // blood cells bellow
 // return hsb2rgb(vec3(0.02,clamp(rand(p),0.7,0.8),clamp(rand(p),0.4,1.0)));

}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.xy;
    st -= 0.5;
    if (iResolution.y > iResolution.x ) 
        st.y *= iResolution.y/iResolution.x;
    else 
        st.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(.0);

    // Scale
    float scale = sin(iTime*0.2)*2.0 + 5.0;
   	st *= scale;

    //st += noise(st*.005*scale)*100.05/scale;
    //st += random2(st)*0.02;


    // Tile the space
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    vec3 c = vec3(0.0);
    vec3 c1;

    float m_dist = 10.;  // minimun distance
    float meta_dist = 10.;  // minimun distance
    vec2 cl_point;
    vec2 cl_dist;

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random2(i_st + neighbor);

			// Animate the point
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f_st;

            // Distance to the point
            float dist = length(diff);

            if (m_dist>dist) {
                cl_point = neighbor +i_st;
                cl_dist = diff;
            }

            // mix color with neighbors
            c = mix (c,random_color(neighbor+i_st),1.0-smoothstep(.0,1.0,dist*0.90));

            // Keep the closer distance
            m_dist = min(m_dist, dist);
            meta_dist = min(meta_dist, meta_dist*dist*1.0);

        }
    }


    // Draw the min distance (distance field)
	// base color
    color = mix(random_color(cl_point),c,1.0);
    // inner borders of cells
    color -= smoothstep(-0.1,1.0,m_dist)*0.24;
    // borders of metaballs
    color = (color)*abs(1.0-smoothstep(0.7,1.0,meta_dist*0.4)*0.3-smoothstep(0.95,1.0,meta_dist*0.4));

    fragColor = vec4(color,1.);
}

