// The MINIMIZED version of https://www.shadertoy.com/view/Xl2XWt


const float MAX_TRACE_DISTANCE = 10.0;           // max trace distance
const float INTERSECTION_PRECISION = 0.0001;        // precision of the intersection
const int NUM_OF_TRACE_STEPS = 300;
const float EPS_NORMAL = 0.001;
const float SCALE_DIST = 0.2;

const float PI = 3.14159265359;

// Hash without Sine
// Creative Commons Attribution-ShareAlike 4.0 International Public License
// Created by David Hoskins.
// https://www.shadertoy.com/view/4djSRW

#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

// Spectrum colour palette
// IQ https://www.shadertoy.com/view/ll2GD3
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 spectrum(float n) {
    return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}



//----
// Camera Stuffs
//----
mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

// checks to see which intersection is closer
// and makes the y of the vec2 be the proper id
vec2 opU( vec2 d1, vec2 d2 ){

	return (d1.x<d2.x) ? d1 : d2;

}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdUnion( float d0, float d1 ) {
    return min( d0, d1 );
}

float sdInter( float d0, float d1 ) {
    return max( d0, d1 );
}

float sdSub( float d0, float d1 ) {
    return max( d0, -d1 );
}

float sdUnion_s( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sfDisp( vec3 p ) {
    return sin(p.x)*sin(p.y)*sin(p.z) ;
}

vec3 sdTwist( vec3 p, float a ) {
    float c = cos(a*p.y);
    float s = sin(a*p.y);
    mat2  m = mat2(c,-s,s,c);
    return vec3(m*p.xz,p.y);
}

vec3 sdRep( vec3 p, vec3 c ) {
    return mod(p,c)-0.5*c;
}


float opOnion( in float sdf, in float thickness )
{
    return abs(sdf)-thickness;
}

vec3 carToPol(vec3 p) {
    float r = length(p);
    float the = acos(p.z/r);
    float phi = atan(p.y,p.x);
    return vec3(r,the,phi);
}


//--------------------------------
// Modelling 
//--------------------------------
vec2 map( vec3 pos ){  
    vec3 pol = carToPol(pos);


    float d1 = opOnion(sdSphere( pos, 1.1 ), 0.0001);
    float wave = -0.1+0.5*sin(8.*(pol.y+0.2*iTime))*sin(8.*(pol.z));
    float d2 = d1 + wave;

    float d = sdSub(d1,d2);

	vec2 res = vec2(d, 1.0);

 	//vec2 res = vec2( sdSphere( pos - vec3( .0 , .0 , .0 ) , 1.1 ) , 1. ); 
    //res = opU( res , vec2( sdBox( pos- vec3( -.8 , -.4 , 0.2 ), vec3( .4 , .3 , .2 )) , 2. ));

    return res;

}



vec2 calcIntersection( in vec3 ro, in vec3 rd ){


    float h =  INTERSECTION_PRECISION*2.0;
    float t = 0.0;
	float res = -1.0;
    float id = -1.;

    for( int i=0; i< NUM_OF_TRACE_STEPS ; i++ ){

        if( h < INTERSECTION_PRECISION || t > MAX_TRACE_DISTANCE ) break;
	   	vec2 m = map( ro+rd*t );
        h = m.x;
        t += h*SCALE_DIST;
        id = m.y;

    }

    if( t < MAX_TRACE_DISTANCE ) res = t;
    if( t > MAX_TRACE_DISTANCE ) id =-1.0;

    return vec2( res , id );

}



// Calculates the normal by taking a very small distance,
// remapping the function, and getting normal for that
vec3 calcNormal( in vec3 pos ){

	vec3 eps = vec3( EPS_NORMAL, 0.0, 0.0 );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}




vec3 render( vec2 res , vec3 ro , vec3 rd ){


  vec3 color = vec3( 0.5 );

  vec3 lightPos = vec3( 1. , 4. , 3. );


  if( res.y > -.5 ){

    vec3 pos = ro + rd * res.x;
    vec3 norm = calcNormal( pos );

    vec3 lightDir = normalize( lightPos - pos );

    float match = max( 0. , dot( lightDir , norm ));

   	color = vec3( 1. , 1., 1.) * match * 0.3 ;

    vec3 pol = carToPol(pos);
    vec3 selfColor = spectrum(1.0*pol.z/PI/2.0+0.5*pol.y/PI);

    color += selfColor * 01.0;
  }

  return color;


}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 p1 = fragCoord; 
    p1 /= iResolution.xy;
    vec4 o4 = vec4(0);

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    o4 += step(( ( (sin(iTime)+1.)/2.)*.5+.25 ), fract(exp2(ceil(-log2(p1.y))) * p1.x / 2.));

    vec3 ro = vec3( 3.0*cos(0.5*iTime), 0.0, 3.0*sin(0.5*iTime));
    vec3 ta = vec3( 0. , 0. , 0. );



    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.5*sin(iTime) );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

        	// rotate camera
	mat3 rot = rotationXY( ( iMouse.xy - iResolution.xy * 0.5 ).yx * vec2( 0.01, -0.01 ) );
	rd = rot * rd;
	ro = rot * ro;


    vec2 res = calcIntersection( ro , rd  );


    vec3 color = o4.rgb;
    vec3 color1 = render( res , ro , rd );

    if (!(color1 == vec3(.5)))
        color = color1;

	fragColor = vec4(color,1.0);



}

#define AA 4.
#define PERIOD 5.
#define COLOR vec3(1., .8, .5)
#define SOLID 0

#define PI 3.14159265359

vec4 hash(float p) {
    return fract(cos(p) * vec4(12345.6, 23456.7, 34567.8, 45678.9));
}

void tRotate(inout vec2 p, float angle) {
    float s = sin(angle), c = cos(angle);
	p *= mat2(c, -s, s, c);
}

void tTwirl(inout vec2 p, float size) {
    const float r1 = .5;
    const float r2 = .3;
    const float r3 = .2;
    if (p.x < 0.) {
        vec2 p2 = p - vec2(0, size * (2. * r1 - r2));
        float y2 = length(p2) - size * r2;
        if (abs(p.y) > abs(y2)) {
            p = vec2(((atan(-p2.x, p2.y) / PI) * r2 + r1) * PI * size, y2);
        }
    } else {
        vec2 p1 = p - vec2(0, size * r1);
        vec2 p2 = p - vec2(0, size * (2. * (r1 - r2) + r3));
        float y1 = length(p1) - size * r1;
        float y2 = length(p2) - size * r3;

        p = abs(y1) < abs(y2)
            ? vec2((atan(-p1.x, p1.y) / PI + 1.) * PI * r1 * size, y1)
            : vec2(((atan(-p2.x, p2.y) / PI + 1.) * r3 + r1 + r2) * PI * size,
                   y2 + sign(y2) * smoothstep(-PI, 0., atan(-p2.x, p2.y)) * size / 10.);
    }
}

// not exact, but we're on a deadline here
float sdStroke(vec2 p, vec2 dim) {
    p = abs(p);
    return p.x > dim.x ? distance(p, vec2(dim.x, 0.)) : p.y - smoothstep(dim.x, 0., p.x) * dim.y;
}

float sdLine(vec2 p, vec2 direction, float amount) {
    float s = dot(p, direction);
    return abs(s) - amount;
}

float opU(float a, float b) {
    return min(a, b);
}

float opS(float a, float b) {
    return max(a, -b);
}

float ground(float x) {
    return sin(x * 5.) / 15.;
}

float sdBackground(vec2 p) {
    p.y += ground(p.x);
	#if ! SOLID
    p.y = abs(p.y);
    #endif
    return p.y - .015 - (sin(p.x * 5. + 4.5) + 1.) * .005;
}

float sdBall(vec2 p, float pos) {
    p += vec2(-pos, ground(pos));
    p.y -= .12;
    tRotate(p, pos * PI * 2.);

    float a = atan(p.x, p.y);
    float l = length(p);

    float d = l - .12 + sin(a * 3.) * .006;

    #if ! SOLID
    d = opS(d, l - .08 + sin(a * 3. + 2.) * .004);
    #endif

    for (float i = 0.; i < 25.; ++i) {
        vec4 h = hash(i) - vec4(.5, .5, .5, -.5);
        d = opU(d, sdLine(p + vec2(h.x * .2, 0), normalize(h.yz), h.w * .01));
    }
    return d + abs(l - .10) * .3;
}

float sdWind(vec2 p, float t) {
    t -= .5;
    tTwirl(p, .1);
    return sdStroke(p - vec2(t, 0), vec2(.3, .01));
}

float sdWinds(vec2 p, float t) {
    t -= .65;
    t = t * 6.;
    tRotate(p, -.15);
    float w1 = sdWind(vec2(p.x, p.y - .02), t),
          w2 = sdWind(vec2(p.x + .12, - p.y - .04), t),
          w3 = sdWind(vec2(p.x - .06, - p.y - .02), t - .4),
          w4 = sdWind(vec2(p.x - .16, p.y), t - .6);
    return opU(opU(w1, w2), opU(w3, w4));
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    float color = 0.;

    float res = min(iResolution.y, iResolution.x);
    vec2 uv = (fragCoord - iResolution.xy / 2.) / res;

    if (abs(uv.x) > .45 || abs(uv.y) > .45) {
        color = 0.;
    } else {
        float t = fract(iTime / PERIOD);
        vec2 p = uv;
        tRotate(p, -PI / 4.);
        p.x -= .1 - smoothstep(.1, .4, abs(.5 - abs(t - .25))) * .25;
        p.y += .15;

        float pos = .1 + sin((t + .1 * cos(t * PI * 2.)) * PI * 2.) * .35;

        float d = sdBackground(p);
        d = opU(d, sdBall(p, pos));
        d = opU(d, sdWinds(p - vec2(-.5, .21), t));
        float w = fwidth(d);
        color = smoothstep(-w, 0., d);
    }

	fragColor = vec4(mix(vec3(0), COLOR, color), 1);
}

// fork of https://www.shadertoy.com/view/3dVGDW

const float PARTITIONS = 10.;

float dtoa(float d, float amount){
    return 1. / clamp(d*amount, 1., amount);
}

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float sdroundedthing(vec2 uv, float size) {
    float ret = length(uv)-size;
    if (uv.y < 0.) {
        ret = min(ret, max(uv.x-size, -uv.x-size));
    }
    return ret;
}
// helps movement of ghosts. probably a cheaper way to accomplish this.
float smoothsquare(float t, float f)
{
    const float pi = atan(1.)*4.;
    const float delta = .03;// smoothness
    const float A = 1.;// amp
    float y = (A/atan(1./delta))*atan(sin(2.*pi*t*f)/delta);
    return y;
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    float t = (iTime+1e2)*.2;
    vec2 uv = fragCoord/iResolution.xy-.5;
    vec2 N = uv;
    uv.x *= R.x / R.y;
    uv.x += .5;
    vec2 uvghost = uv;

    uv.y += t*.3;

    // behind the glass...
    float sdghost = 1e6;
    const float ghosts = 9.;
    for (float i = 0.;i < ghosts; ++ i) {
        vec4 h = hash42(vec2(i+2e2));
        vec2 uvg2 = uvghost;
    	uvg2.x -= (fract(t*h.x+smoothsquare(t+h.z*2., .5)*.2)-.5)*3.;
        uvg2.y *= sign(h.w-.5);// ceiling
        uvg2.y += h.y*.5;
        sdghost = min(sdghost, sdroundedthing(uvg2, .0));
    }

    o = vec4(mix(1.,smoothstep(.0,.4,sdghost), .9));
    //return;

    N *= .98;// oob artifact quickfix

    vec2 cellUL = floor(uv);
    vec2 cellBR = cellUL + 1.5;
    vec2 seed = cellUL;// top-level cell ID

    for(float i = 1.; i <= PARTITIONS; ++ i) {
        vec4 h = hash42(seed+(vec2(cellBR.x, cellUL.y)+10.));
        float dl = abs(uv.x - cellUL.x);// distance to edge of cell, left edge
        dl = min(dl, length(uv.y - cellUL.y));// bottom (inv y)
        dl = min(dl, length(uv.x - cellBR.x));// right
        dl = min(dl, length(uv.y - cellBR.y));// top

        float r = max(fract(N.x-.5), fract(.5-N.x));
        r = max(r, fract(.5-N.y));
        r = max(r, fract(N.y-.5));
        r = 1.-r;
        float col2 = 1.5-dtoa(dl, (h.z+.05)*6000.*pow(r, 1.5));
        vec3 col = h.xyz;
        o.rgb *= col2;
        if (h.w < .1)// sometimes color a window
        	o.rgb *= mix(col, vec3(col.r+col.g+col.b)/3.,.8);

        h.y = mix(.5, h.y, .2);// favor dividing evenly
        vec2 pt = mix(cellUL, cellBR, h.y);

        if (uv.x < pt.x) {// descend into quadrant
            if (uv.y < pt.y) {
                cellBR = pt.xy;
            } else {
              	cellUL.y = pt.y;
              	cellBR.x = pt.x;
            }
        } else {
            if (uv.y > pt.y) {
                cellUL = pt.xy;
            } else {
                cellUL.x = pt.x;
                cellBR.y = pt.y;
            }
	    }
    }

    o = clamp(o,0.,1.);
    o = pow(o,o-o+.7);
    o.a = 1.;
}

