#define PI 3.1415926535897932384626433832795
#define RENDER_DEPTH 800
#define CLOSE_ENOUGH 0.00001

#define GRADIENT(pt, func) vec3( \
    func(vec3(pt.x + 0.0001, pt.y, pt.z)) - func(vec3(pt.x - 0.0001, pt.y, pt.z)), \
    func(vec3(pt.x, pt.y + 0.0001, pt.z)) - func(vec3(pt.x, pt.y - 0.0001, pt.z)), \
    func(vec3(pt.x, pt.y, pt.z + 0.0001)) - func(vec3(pt.x, pt.y, pt.z - 0.0001)))

//Array of lights in our scene
const vec3 LIGHT_POS[] = vec3[](vec3(5.0));

//---------------------------------------------------------------------------------------
// Transformations
//---------------------------------------------------------------------------------------

mat4 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, s, 0, 0),
        vec4(-s, c, 0, 0),
        vec4(0, 0, 1.0, 0),
        vec4(0, 0, 0, 1.0)
    );
}

mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0.0, 1.0, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1.0)
    );
}

//---------------------------------------------------------------------------------------
// Signed Distance Functions
//---------------------------------------------------------------------------------------

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float scene(vec3 p) {
    return sphere(p, 2.0);
}

//---------------------------------------------------------------------------------------
// Auxiliary helper functions
//---------------------------------------------------------------------------------------

vec3 getNormal(vec3 pt) {
    return normalize(GRADIENT(pt, scene));
}

//---------------------------------------------------------------------------------------
// Illumination and Shading
//---------------------------------------------------------------------------------------

float shade(vec3 eye, vec3 pt, vec3 n) {
    float val = 0.0;

    //Ambient Lighting
    val += 0.2;

    //Cast ray towards our light sources and add it to the illumination depending
    //on the angle with the normal to the surface
    for (int i = 0; i < LIGHT_POS.length(); i++) {
        vec3 l = normalize(LIGHT_POS[i] - pt); 
        val += max(dot(n, l), 0.0);
    }

    return val;
}

vec3 illuminate(vec3 camPos, vec3 rayDir, vec3 pt) {
    vec3 c, n;
    //Using the SDF normal approximation
    n = getNormal(pt);

    //Everything is white
    c = vec3(1.0);
    return shade(camPos, pt, n) * c ;
}

//---------------------------------------------------------------------------------------
// Ray Marching Algorithm
//---------------------------------------------------------------------------------------

vec3 castRay(vec3 camPos, vec3 rayDir) {
    //Using the ray marching approach
    int step = 0;
    float t = 0.0;

    for (float d = 1000.0; step < RENDER_DEPTH && abs(d) > CLOSE_ENOUGH; t += abs(d)) {
        d = scene(camPos + t * rayDir)/2.0;
        step++;
    }

    if (step == RENDER_DEPTH) {
        return vec3(0.0, 0.0, 0.0); //getBackground(rayDir);
    } else {
        return illuminate(camPos, rayDir, camPos + t * rayDir);
    }
}

//---------------------------------------------------------------------------------------
// Set-up
//---------------------------------------------------------------------------------------

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    //The direction in which the camera is pointing
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

//---------------------------------------------------------------------------------------
// Output
//---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //Normalize the 2d coordinates [-1, 1]
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;

    //Fix the x axis to original resolution
    p.x *= iResolution.x / iResolution.y;

    //Setting the camera - origin, lookat
    vec3 origin = vec3(10.0*sin(iTime), 1.8, 10.0*cos(iTime));
    vec3 look = vec3(0.0);


    mat3 camera = setCamera(origin, look, 0.0);
    vec3 direction = camera * normalize(vec3(p.xy, 2.0));

    vec3 color = castRay(origin, direction);

    fragColor = vec4(color, 0.0);
}

#define MAX_STEPS 100
#define SURFACE_DIST .01
#define FOG_START 4.
#define FOG_END 10.
#define MAX_DIST FOG_END + 1.
#define PI 3.14159265359
#define LIGHT vec4(0., 5.+sin(iTime*4.3)*.2, -2. + iTime, .03)

// Noise 3 to 1
float N31(vec3 id) {
 id = fract(id * vec3(244.224, 441.211, 521.198));
 id += dot(id, vec3(63.6, 9.1, 55.3))/6.6;
 return fract(id.x * id.y * id.z);
}

// Noise 3 to 3
vec3 N33(vec3 id) {
 id = fract(id * vec3(266.234, 881.211, 572.598));
 id += dot(id, vec3(67.6, 981.1, 5.3));
 return vec3(fract(id.x * id.y), fract(id.y * id.z), fract(id.z * id.x));
}

// Gets distance from point p to capsule a to b with radius r.
float d_capsule(vec3 p, vec3 a, vec3 b, float r, float pinch) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + ab*t;

    // Make capsule thinner at center
    float mx = 1.-(cos(t*2.*PI)*.5+.5);

    return length(p-c)-mix(r, r*.2, mx*pinch);
}

// Gets a random corner for the cube.
vec3 RandomCorner(vec3 id) {
    float s = .65;
    float r = N31(id);
    if (r < .25) {
        return vec3(-s, -s, -s);
    } else if (r < .5) {
        return vec3(-s, s, -s);
    } else if (r < .75) {
        return vec3(-s, s, s);
    } else {
        return vec3(s, s, -s);
    }
}

// Get distance to scene
float GetDist(vec3 p, vec3 rd, out float lightdist) {
    // Get direction so that we don't search in wrong direction
    float ox = (rd.x > 0.) ? 1. : 0.;
    float oy = (rd.y > 0.) ? 1. : 0.;
    float oz = (rd.z > 0.) ? 1. : 0.;

    float sphere_d = length(p-LIGHT.xyz)-LIGHT.w;


    // square id
    vec3 id = floor(p / 2.);

    // Domain repetition
    p = vec3(mod(p.x, 2.)-1.,mod(p.y, 2.)-1., mod(p.z, 2.)-1.);//, );

    float capsule_d = MAX_DIST;
    // Search in xyz offset by direction.
    for(float x = -1.+ox;x<=ox;x++) {
        for(float y = -1.+oy;y<=oy;y++) {
            for(float z = -1.+oz;z<=oz;z++) {
                // Get random corner for this square
                vec3 v = RandomCorner(id + vec3(x, y, z));

                // Get capsule distance
                float d = d_capsule(p, v + vec3(x, y, z) * 2., -v + vec3(x, y, z) * 2., .3, 1.);//mh_length(p-sphere.xyz)-sphere.w;

                // Calc min
                capsule_d = min(capsule_d, d);
            }
        }
    }
    lightdist = sphere_d;
    return min(capsule_d, sphere_d);
}





// March ray, returns distance and iterations
float RayMarch(vec3 ro, vec3 rd, out int its, out float minlightdist) {
	float d_o = 0.;  
    its = 0;
    minlightdist = MAX_DIST;

    for(int i=0;i<MAX_STEPS;i++) {
     	vec3 c_p = ro + (rd * d_o);
        float lightdist = 0.;
        float d_s = GetDist(c_p, rd, lightdist);
        minlightdist = min(minlightdist, lightdist);
        d_o+= d_s;
        its++;
        if ((d_s < SURFACE_DIST) || (d_o > MAX_DIST)) break;
    }

    return d_o;
}


// Guess normal at position p
vec3 GetNormal(vec3 p) {
    vec2 e = vec2(SURFACE_DIST, 0.);
    float lightdist = 0.;
    float d = GetDist(p, vec3(0.), lightdist);
    vec3 n = d - vec3(
        GetDist(p-e.xyy, vec3(-1., 0., 0.), lightdist),
        GetDist(p-e.yxy, vec3(0., -1., 0.), lightdist),
        GetDist(p-e.yyx, vec3(0., 0., -1.), lightdist)
    );
    return normalize(n);
}

// Get light level at position p
float GetLight(vec3 p) {
    // light position
    vec3 lv = normalize(LIGHT.xyz-p);//lightvector
    vec3 nv = GetNormal(p);//normalvector
    int its = 0;
    float lightdist = 0.;
    // light cutoff
    float lightlevel = smoothstep(.3,.7,clamp(dot(lv, nv), 0., 1.));
    float shadow = (RayMarch(p+(nv*SURFACE_DIST*2.), lv, its, lightdist) < (length(LIGHT.xyz-p)-(LIGHT.w*2.))) ? 0. : 1.;
    return 0.3 + ((lightlevel * shadow) * .7);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.);
    // Ray origin, Ray direction
    vec3 ro = vec3(cos(iTime*0.8)*.4, 5.+sin(iTime*0.8)*.4, -6. + iTime);
    vec3 rd = normalize(vec3(uv.x-cos(iTime*0.8)*.1, uv.y-sin(iTime*0.8)*.1, 1.));

    // get iterations and distance to scene
	int its = 0;
    float lightdist = 0.;
    float d = RayMarch(ro, rd, its, lightdist);

    // Get the minimum distance a ray came to a light and create add var.
    float light_add = pow(smoothstep(LIGHT.w*100., 0., lightdist), 20.);// * (0.7 + ((sin(atan(uv.x, uv.y)*16.)*.5+.5)*0.3));

    // Dont bother if beyond fog reach
    if(d<FOG_END) {

        // Make more iterations darker
        float outline = smoothstep(.75, .65, float(its)/40.);

        // Get hit position
        vec3 pos = ro + (rd * d);

        bool at_light = (length(pos-LIGHT.xyz) < LIGHT.w*2.);

        // Get random color at position
   		vec3 posid = floor(pos / 2.);
    	vec3 col_at_pos = at_light ? vec3(1.) :  (.2+N33(posid)*.8);

        // Calculate final pixel color
        col = col_at_pos * (at_light ? 1. : GetLight(pos)) * smoothstep(FOG_END, FOG_START, d) * outline;
    }

    fragColor = vec4(mix(col+light_add, vec3(1.), light_add),1.0);
}

#define PI 3.1415926535897932384626433832795

float fOverDerF(float t, vec3 p)
{

	float sint = sin(t);
    float cost = cos(t);

    float sin2t = 2.0*sint*cost;//sin(2.0*t);
    float cos2t = cost*cost - sint*sint;//cos(2.0*t);

    float sin4t = 2.0*sin2t*cos2t;//sin(4.0*t);

    float sin3t = sin(3.0*t);
    float cos2tSq = cos2t*cos2t;
    float cos3t = cos(3.0*t);

    return (2.0*p.x*sint + p.x*sin2t + 2.0*p.x*sin4t - 3.0*p.y*cos3t - 2.0*p.z*cost - 4.0*p.z*cos2tSq + p.z*cos2t + 2.0*p.z - 6.0*sin3t)/(2.0*p.x*cost + 16.0*p.x*cos2tSq + 2.0*p.x*cos2t - 8.0*p.x + 9.0*p.y*sin3t + 2.0*p.z*sint - 2.0*p.z*sin2t + 8.0*p.z*sin4t - 16.0*cos2tSq*cos2t + 12.0*cos2t - 18.0*cos3t + 4.0*cos(6.0*t));


    //return (2.0*p.x*sin(t) + p.x*sin(2.0*t) + 2.0*p.x*sin(4.0*t) - 3.0*p.y*cos(3.0*t) - 2.0*p.z*cos(t) - 4.0*p.z*pow(cos(2.0*t), 2.0) + p.z*cos(2.0*t) + 2.0*p.z - 6.0*sin(3.0*t))/(2.0*p.x*cos(t) + 16.0*p.x*pow(cos(2.0*t), 2.0) + 2.0*p.x*cos(2.0*t) - 8.0*p.x + 9.0*p.y*sin(3.0*t) + 2.0*p.z*sin(t) - 2.0*p.z*sin(2.0*t) + 8.0*p.z*sin(4.0*t) - 16.0*pow(cos(2.0*t), 3.0) + 12.0*cos(2.0*t) - 18.0*cos(3.0*t) + 4.0*cos(6.0*t));
}

vec3 torusKnot(float t)
{
    return vec3((cos(3.0*t) + 2.0)*cos(t), sin(3.0*t), (cos(3.0*t) + 2.0)*sin(t));
}
vec3 torusKnotDer(float t)
{
    return vec3(sin(t)*(-(cos(3.*t) + 2.)) - 3.*sin(3.*t)*cos(t), 3.*cos(3.*t), cos(t)*(cos(3.*t) + 2.) - 3.*sin(t)*sin(3.*t));
}

float lengthSq(vec3 p)
{
    return dot(p, p);
}

float oldTorusKnotDist(vec3 p, out vec3 closestPoint)
{
    int samplePointsNum = 12;

    float jump = 2.0 * PI / float(samplePointsNum);

    float minDistSq = -1.0;
    float minT;

    for(int i = 0; i < samplePointsNum; i++)
    {
        float cT = jump * float(i);

        cT -= fOverDerF(cT, p);

        float cDistSq = lengthSq(torusKnot(cT) - p);

        if(minDistSq == -1.0 || cDistSq < minDistSq)
        {
            minDistSq = cDistSq;
            minT = cT;
        }
    }

    minT -= fOverDerF(minT, p);


    closestPoint = torusKnot(minT);

    return distance(closestPoint, p);
}


vec2 cMult(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}
vec2 cPow(vec2 a, int n)
{
    vec2 r = vec2(1, 0);
    for(int i = 0; i < n; i++)
    {
        r = cMult(r, a);
    }

    return r;
}

float torusDist(vec3 p, out vec3 closestPoint, float r1, float r2)
{
    float d = sqrt(pow(p.y, 2.0) + pow(length(p.xz) - r1, 2.0));

    closestPoint = p;
    closestPoint.xz -= p.xz * r1 / length(p.xz);
    closestPoint *= r2 / d;
    closestPoint.xz += p.xz * r1 / length(p.xz);

    return d - r2;
}
float fastTorusKnotDist(vec3 p, out vec3 closestPoint, int n, float r1)
{
    vec2 pa = normalize(p.xz);
    vec2 rot = cPow(pa, n);
    p.xz -= pa * rot.x;
    p.y -= rot.y;

    float d = torusDist(p, closestPoint, r1, 0.);

    closestPoint.xz += pa * rot.x;
    closestPoint.y += rot.y;

    return d;
}

float pulse(float x, float a, float b)
{
    return pow(1./(pow(abs(x), a) + 1.), b);
}

float getT(vec3 closestPoint)
{
    return atan(closestPoint.z, closestPoint.x);
}
float getS(float t, vec3 p, vec3 closestPoint)
{
    vec3 normal = vec3(cos(3.0*t)*cos(t), sin(3.0*t), cos(3.0*t)*sin(t));
    vec3 cpToP = normalize(p - closestPoint);

    float angle = acos(clamp(dot(normal, cpToP), -1., 1.));


    float angSign = sign(dot(cross(normal, cpToP), torusKnotDer(t)));

    return angle * angSign;
}

float getBumps(vec3 p, vec3 lastClosestCurvePoint)
{
    float t = getT(lastClosestCurvePoint);
    float s = getS(t, p, lastClosestCurvePoint);

    float time = -2.*0.3*iTime;


    float bump = PI / 24.;

    float modT = t + bump + time;

    modT = mod(modT, 2.*bump);

    modT -= bump;


    modT *= 10.;
    s *= 2.;


    return 0.5 * (cos(3.*t)+1.) * exp(-modT*modT -s*s);
}

float spikyTorusKnotDist(vec3 p, out vec3 closestPoint)
{
    float tnd = oldTorusKnotDist(p, closestPoint) - 0.5;
    //float tnd = fastTorusKnotDist(p, closestPoint, 3, 2.) - 0.75;

    return tnd - getBumps(p, closestPoint);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx - (iResolution.xy/iResolution.xx)/2.0;

    float time = 0.1 * iTime;


    float az = (2.*PI*time);
    float po = PI/2. * sin(2.*PI*time);


    vec3 camera = vec3(cos(po) * cos(az), sin(po), cos(po) * sin(az));
    vec3 cz = -camera;
    vec3 cy = vec3(sin(-po) * cos(az), cos(-po), sin(-po) * sin(az));
    vec3 cx = cross(cy, cz);
    camera *= 14.;

    float zoom = 1.;




    vec3 sp = normalize(uv.x * cx + uv.y * cy + zoom * cz);

    vec3 p = camera;

    float hitDistance = 0.2;


    vec3 lastClosestCurvePoint;
    float totalDist = 0.0;
    float lastDist = -1.0;
    for(int i = 0; i < 30; i++)//30
    {
        p += sp * lastDist;

        lastDist = spikyTorusKnotDist(p, lastClosestCurvePoint);

        //lastDist *= 0.35;


        totalDist += lastDist;

        //if(lastDist < 0.2)break;
    }

    totalDist -= 9.;

    vec3 col = (4./(totalDist*totalDist) + 0.2) * mix(hsv2rgb(vec3(time + 0.5, 1, 1)), hsv2rgb(vec3(time, 1, 1)), getBumps(p, lastClosestCurvePoint));

    col *= smoothstep(0.2, 0., lastDist);

    fragColor = vec4(col,1.0);
}

