//------------------------------------------------------------------------
// Main
//------------------------------------------------------------------------

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    p.x /= 1.25;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime);

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( vec3(p.xy,1.0) ); // lens length
    rd.y -= 1.0;

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground();

	// raymarch
    vec3 vos, dir;
    int mat;
    float t = calcIntersection( ro, rd, vos, dir, mat );
    if( t>0.0 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = -dir*sign(rd);

        // materials
        vec3 mal = doMaterial( pos, vos );

        col = doLighting( pos, nor, rd, t, mal, vos, dir);
	}


    col *= 1.5;
    col -= .03;

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.5) );

    // saturation
	//col = saturation(col, 2.0);   
    //col *= exp(-.01*t*t);

    // toon
    col *= vec3(6.);
    float colFract = abs(sin(fract(col.r) * 3.14 * 1.0));
    col = floor(col);


    // pallete
    col = mix(getColor(int(col.r-1.)), getColor(int(col.r)), colFract);

    fragColor = vec4( col, 1.0 );
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
  vec2 i = floor(_st);
  vec2 f = fract(_st);

  // Four corners in 2D of a tile
  float a = random(i);
  float b = random(i + vec2(1.0, 0.0));
  float c = random(i + vec2(0.0, 1.0));
  float d = random(i + vec2(1.0, 1.0));

  vec2 u = f * f * (3.0 - 2.0 * f);

  return mix(a, b, u.x) +
    (c - a)* u.y * (1.0 - u.x) +
    (d - b) * u.x * u.y;
}

mat2 rotation ( in float _angle ) {
    return mat2(
        cos(_angle), sin(_angle),
    	-sin(_angle), cos(_angle)
    );
}

#define NUM_OCTAVES 4

#define PI 3.14159

float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = rotation(0.5);
    for (int i = 0; i < NUM_OCTAVES; ++i) {
      v += a * noise(_st);
      _st = rot * _st * 2.0 + shift;
      a *= 0.5;
    }
    return v;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{   
    float time = iTime * 1.0;

    float minwh = min(iResolution.x, iResolution.y);
    float aspect = iResolution.x / iResolution.y;

	vec2 st = rotation(PI * 1.75) * vec2(
        fragCoord.x / minwh - 0.5 * aspect,
        fragCoord.y / minwh - 0.5
    );

    vec3 yellow = vec3(191.0 / 255.0, 191.0 / 255.0, 0.0);

    vec4 planetColor;

    float mixRatio;

    float blur = 1.0 - smoothstep(0.2, 0.24, length(st));

    if (length(st) < 0.24) {
        mixRatio = 0.85;
        planetColor = mix(vec4(0.1, 0.1, 0.1, 1.0), vec4(
            yellow * (
                0.7 +
                0.3 *
                sin(
                    2.2 +
                    10.0 *
                    distance(st, vec2(0.1, 0.1)) *
                    distance(st, vec2(0.03, -0.16)) *
                    distance(st, vec2(-0.03, 0.16)) *
                    (1.0 + 0.1 * sin(time * 1.0)) * 30.0
                )
            ),
            1.0
        ), blur);
    } else {
        mixRatio = 0.0;
        planetColor = vec4(0.0, 0.0, 0.0, 0.0);
    }

    // Smoke
    vec2 st1 = st * 6.0;
    vec3 color = vec3(0.0);

    vec2 q = vec2(0.0);
    q.x = fbm(st1 + 0.00 * time);
    q.y = fbm(st1 + vec2(1.0));

    vec2 r = vec2(0.0);

    r.x = fbm( st1 + 1.0 * q + vec2(1.7, 9.2) + 0.6 * sin(0.2 * time) );
    r.y = fbm( st1 + 1.0 * q + vec2(8.3, 2.8) + 0.6 * cos(0.2 * time) );

    float f = fbm(0.5 * st1 + 2.0 * r);

    vec3 color1 = vec3(0.1, 0.6, 0.6);
    vec3 color2 = vec3(0.6, 0.6, 0.5);

    color = mix(
        color1,
        color2,
        clamp((f*f)*4.0,0.0,1.0)
    );

    color = mix(
        color,
        vec3(0.3, 0.3, 0.3),
        clamp(length(q),0.0,1.0)
    );

    color = mix(
        color,
        vec3(0.2, 0.2, 0.2),
        clamp(length(r.x), 0.0, 1.0)
    );

    vec4 smokeColor = vec4(
      (f * f * f + 0.6 * f * f + 0.5 * f) * color,
      1.0
    );

    fragColor = mix(smokeColor, planetColor, mixRatio);
}
