#define Size 0.192
#define ScreenL 4.686
#define SphereOpacity 0.006
#define Color vec3(1,0.38,0.38)
#define R1 5.475
#define G1 6.954
#define B1 6.313
#define time iTime
#define resolution iResolution.xy

float sincostex(vec3 p,float s){
  p*=s;
  return cos(p.x)*sin(p.y)*cos(p.z)*0.5+0.5;
}

vec4 of(vec3 p){
  p*=(sin(time*2.0)*0.5+1.5);
  float ct=cos(time);float st=sin(time);
  p.xz*=mat2(ct,-st,st,ct);
  ct=cos(time*0.5);st=sin(time*0.5);
  p.xy*=mat2(ct,-st,st,ct);

  vec3 op=p;
  vec3 a1=p*(1.0+sin(op.yzx*4.0)*0.4);
  vec3 a2=p*(1.0+sin(op*4.0)*0.4);
  p=mix(a1,a2,sin(time*0.1)*0.5+0.5);

  vec3 n=normalize(p);
  p*=(0.5+sincostex(n,sin(floor(time))*10.0+10.0));
  float a=length(p)<(sincostex(n,Size*10.0)+2.0)?SphereOpacity:0.0;

  return vec4(vec3(sin(p*vec3(R1,G1,B1))*0.5+0.5)*Color,a);  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

  vec2 uv=iResolution.xy;

  uv=(gl_FragCoord.xy-uv*.5)/uv.y;

  vec3 ro=vec3(0,0,5);
  vec3 r=normalize(vec3(uv,-1));

  vec4 color;
  float f=1.0;
  for(int i=0;i<400;i++){
    vec3 p=ro+f*r;
    vec4 d=of(p);
    color+=vec4(d.xyz*d.w,d.w);
    if (f>8.0) break;
    if (color.w>1.0) break;
    f+=0.02;
  }

  fragColor=vec4(color.xyz*ScreenL,1);

}

precision highp float;

float rand(vec2 coords)
{
	return fract(sin(dot(coords, vec2(56.3456f,78.3456f)) * 5.0f) * 10000.0f);
}

float noise(vec2 coords)
{
	vec2 i = floor(coords);
	vec2 f = fract(coords);

	float a = rand(i);
	float b = rand(i + vec2(1.0f, 0.0f));
	float c = rand(i + vec2(0.0f, 1.0f));
	float d = rand(i + vec2(1.0f, 1.0f));

	vec2 cubic = f * f * (3.0f - 2.0f * f);

	return mix(a, b, cubic.x) + (c - a) * cubic.y * (1.0f - cubic.x) + (d - b) * cubic.x * cubic.y;
}

float fbm(vec2 coords)
{
	float value = 0.0f;
	float scale = 0.5f;

	for (int i = 0; i < 5; i++)
	{
		value += noise(coords) * scale;
		coords *= 4.0f;
		scale *= 0.5f;
	}

	return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y * 2.0f;

    float Pixels = 1024.0;
    float dx = 10.0 * (1.0 / Pixels);
    float dy = 10.0 * (1.0 / Pixels);

    vec2 Coord = vec2(dx * floor(uv.x / dx),
                          dy * floor(uv.y / dy));
	vec2 motion = vec2(fbm(Coord + iTime * 0.05f));


    float final = fbm(Coord + motion);

	fragColor = vec4(mix(vec3(-0.2f), vec3(0.45, 0.4f, 0.6f) + vec3(0.6f), final), 1);
}

float wave;
float fft;

float vmax(vec2 v) {
	return max(v.x, v.y);
}

float vmax(vec3 v) {
	return max(max(v.x, v.y), v.z);
}

float vmax(vec4 v) {
	return max(max(v.x, v.y), max(v.z, v.w));
}


float sphere(in vec3 pos) {
	vec3 cen = vec3(0.,0.2,0.);
	return length(cen* fft*5.-pos) -.35*fft*3.5;
}

float fBox(vec3 p, vec3 b) {
    vec3 cen = vec3(-0.4,1.-2.*fft,-.7);
	vec3 d = abs(p)+cen - b;
	return length(max(d, vec3(.3))) + vmax(min(d, vec3(0.)));
}



float map(vec3 p) {

p = mod(p+2.,4.) - 2.;
//return length(p) -.35;
    float s = sphere(p); 
	float b = fBox(p, vec3(1.));
    return min(s,b);
}

vec3 GetNormal(vec3 p) 
{
	vec2 e = vec2(.001,0);
    float d = map(p);
    vec3 n = d - vec3(
    	map(p-e.xyy),
        map(p-e.yxy),
        map(p-e.yyx)
    );
    return normalize(n);
}

float CastRay(in vec3 ro, vec3 rd) {

    float t = 0.0;

    for (int i = 0; i < 120; i++) {

        vec3 pos = ro + rd * t;

        float h = map(pos);

        if(h<.001) break;
        t+=h;
        if(t>20.0) break;
    }
    return t;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord -iResolution.xy)/iResolution.y;

    //from https://www.shadertoy.com/view/Xds3Rr
    int tx = int(p.x/3.);
	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	 fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

    vec3 ro = vec3(
    0., 
    2.,
    iTime*3.4);
     vec3 ta = vec3(0.,0.,0.);

    vec3 ww = normalize(ta - ro); //forward

    vec3 uu = normalize (cross(ww,vec3(0,1,0))); // right?

    vec3 vv = normalize(cross(uu,ww) ); // up?
    vec3 rd = normalize(p.x*uu + p.y*vv + 1.5*ww);

    vec3 col = (vec3(0.65,0.75,0.9)-0.5*rd.y)/1.5;


	float t = CastRay(ro,rd);




    if(t<20.) {

        //base 
        col = vec3(.99*p.x+fft,.36,p.y+fft/8.);

    	vec3 pos = ro + t * rd;


        vec3 sun_dir = vec3(1,1,0);
    	vec3 nor = GetNormal(pos);
        float sun_dif = clamp(dot(nor,sun_dir),0.,1.);
        col += sun_dif;
        //col+= vec3(.3+fft*2.,.3+fft*.5,.3+fft*2.);

    }

    //prettier without faux-gamma
    //col = pow(col, vec3(.4545));
