// just trying to find the singularity in my computer
// *best viewed full screen*

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y*2.0;

    float a = atan(uv.y, uv.x);
    float l = length(uv) * 0.5f;

    float t = iTime*0.975;
    float f = float(iFrame)*0.975f;

    float x = (t+25.0f)*sin(1.0f+f)*1.61803398875f*(l-0.333+sin(f*2.0f)*0.06125);
    float c = (cos(x*2.0)/x)*max(0.0,(1.75-(x*0.001*(0.5*sin(iTime)*0.5))));
    float d = 0.0;

    d += smoothstep(0.0f, sqrt(c), tan(a*1.0+t*0.5));
    d += smoothstep(d, d*d, tan(a*2.0-t*1.2));
    d += smoothstep(d*d, d*d*d, tan(a*3.0+t*1.5));
    d += smoothstep(d*d*d, d*d*d*d, tan(a*2.0-t*1.7));
    d += smoothstep(d*d*d*d, d*d*d*d*d, tan(a*1.0+t*3.8));
    d += smoothstep(d*d*d*d*d, d*d*d*d*d*d, tan(a*2.0+t*4.7));
    d += smoothstep(d*d*d*d*d*d, d*d*d*d*d*d*d, tan(a*0.5+t*6.8));
    d += smoothstep(d*d*d*d*d*d*d, d*d*d*d*d*d*d*d, tan(a*0.25+t*9.8));
    float amount = c/(1.0f + d*d*d);
    amount = max(d, amount) + min(d, amount);
    amount = min(c, d*d);
    vec3 col = smoothstep(vec3(0), vec3(1), vec3(1.0,0.0,0.2)*(0.05+amount*d*0.03f));
    vec3 bar = pow(col, vec3(9.4f));
    bar = pow(abs(col - bar), vec3(4.0f));
    col = bar + col * col;
    fragColor = vec4(pow(col, vec3(1.0f/2.2f)),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/vec2(iResolution.y);
    int i = 0;
    const int SquarCount = 5;
    float freq = 50.0;
    float yCal = 0.0;
    float Fade = 1.48;

    yCal = iResolution.x/iResolution.y;

    vec2 CirCen0[9];
    CirCen0[0] = vec2(0.9-(cos((iTime+5.0)*0.27)*0.9),0.5+(0.5*cos(iTime*.33)));
    CirCen0[1] = vec2(1,-1) * CirCen0[0];
    CirCen0[2] = CirCen0[1]+vec2(0,2);
    CirCen0[3] = vec2(-1,1) * CirCen0[0];
    CirCen0[4] = CirCen0[3]+vec2((yCal)*2.0,0);

    vec2 CirCen1[9];
    CirCen1[0] = vec2(0.9-(cos(iTime*0.59)*0.9),0.5+(0.5*cos((iTime+5.0)*.41)));
    CirCen1[1] = vec2(1,-1) * CirCen1[0];
    CirCen1[2] = CirCen1[1]+vec2(0,2);
    CirCen1[3] = vec2(-1,1) * CirCen1[0];
    CirCen1[4] = CirCen1[3]+vec2((yCal)*2.0,0);

    float MyDist = 0.0;
    float Darkness = 0.0;
    float Adj = float(SquarCount) * 2.0;

    for(int i=0;i<SquarCount;i++)
    {
        MyDist = distance(CirCen0[i],uv);
        if (MyDist < Fade)
        {    
        	Darkness += (cos((MyDist*freq)-iTime*10.0)/(Adj))*(Fade-MyDist);
        }
    }
    for(int i=0;i<SquarCount;i++)
    {
        MyDist = distance(CirCen1[i],uv);
        if (MyDist < Fade)
        {    
        	Darkness += (cos((MyDist*freq)-iTime*10.0)/(Adj))*(Fade-MyDist);
        }

    }

	Darkness = Darkness+0.5;
    fragColor = vec4(vec3(Darkness),1.0);
}

