#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float rand(float k) {
  int x = FK(k);int y = FK(cos(k));
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

vec3 rand3(float k) {
  float t1 = rand(k);
  float t2 = rand(k+t1);
  float t3 = rand(t1+t2);
  return vec3(t1,t2,t3);
}

vec3 spherecords(vec2 p) {
  float phi = acos(p.x);
  float lamb = p.y*acos(-1.);
  return vec3(cos(phi), sin(phi)*cos(lamb), sin(phi)*sin(lamb));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(p,ax)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

float smin( float a, float b, float k ) {
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

float comp(vec3 p, vec3 off, vec3 rot) {
  float scale = 0.6 + sqrt(sin(iTime*2.)*0.5+0.5)*0.1;
  vec3 ax = spherecords(rot.xy);
  p = erot(p, ax, rot.z*acos(-1.));
  p+=off;
  p = (fract(p/scale)-0.5)*scale;
  return length(p) - scale*0.3;
}

vec3 mapp(vec3 p) {
  for (int i = 0; i < 8; i++) {
    vec3 off = spherecords(rand3(2.4*cos(float(i))).xy);
    vec3 rot = rand3(5.1*float(i));
    vec3 ax = spherecords(rot.xy);
    off = erot(off, ax, iTime*0.1);
    float offscale = 1. + sin(iTime*0.5);
    p+=off*offscale;
    p -= min(dot(off,p),0.)*off*2.; 
    p-=off*offscale;
  }
  return p;
}

float jort = 0.;
float scene(vec3 p) {
  p = mapp(p);

  float dist = 100000.;
  for (int i = 0; i < 5; i++) {
    float d = comp(p, rand3(float(i)), rand3(cos(float(i))));
    if (d < dist) jort = float(i);
    dist = smin(d,dist,0.1);
  }
  return (dist + max(length(p)-1.5,0.)*0.25);
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.01);
  return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

float sharp(float k) {
  return smoothstep(0.0,1.,k);
}

float fakereflection(vec3 p, vec3 cam) {
  vec3 n = norm(p);
  vec3 inc = reflect(cam, n);
  float s1 = scene(p);
  float avg = 0.;
  float cum = 0.;

  for (int i = 0; i < 3; i++) {
    float stp = 0.045*(float(i)+1.);
    vec3 stped = p+inc*stp;
    vec3 n2 = norm(stped);
    stped = mapp(stped);
    float brt = min(length(stped),2.)/2.;
    avg += mix(0., mix(dot(n2,normalize(mapp(p)))*0.5+0.5,1.,brt*0.8), brt*0.3+0.7);//((s2-s1)/stp)*0.5+0.5
    cum += 1.;
  }
  return sharp(avg/cum*0.95);
}

vec3 RED = vec3(0.9,0.2,0.2);
vec3 BLUE = vec3(0.2,0.6,0.9);

bool cros(vec2 p) {
  p *= asin(sin(iTime/60.*140.*4.))*0.15+1.15;
  p = abs(p);
  vec2 p2 = vec2(min(p.y,p.x),max(p.y,p.x));
  float sc = 1.5;
  if (p2.x < 0.05*sc && p2.y < 0.15*sc) return true;
  return false;
}

vec3 backgroundcol(vec2 uv) {
  uv*=4.;
  vec2 sc = vec2(0.4,0.7)*2.;
  vec2 id = floor(uv/sc);
  float off = iTime;
  if (mod(id.x,2.)==mod(floor(off*0.5),2.)) {
    uv.y+=0.5*sc.y;
  } else {
    uv.y;
  }
  uv = (fract(uv/sc)-0.5)*sc;

  return cros(uv) ? RED : vec3(1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = normalize(vec3(0.5,uv));
  vec3 init = vec3(-8.+sin(iTime*0.7),0.,0.)+cam*0.8;
  float ro = iTime;
  cam = erot(cam, vec3(0,1,0), sin(ro)/7.);
  init = erot(init, vec3(0,1,0), sin(ro)/7.);
  cam = erot(cam, vec3(0,0,1), ro/3.);
  init = erot(init, vec3(0,0,1), ro/3.);
  vec3 p = init;
  bool hit = false;
  float k = 1.;
  for (int i = 0; i < 200; i++) {
    float dist = scene(p);
    if (i == 0) k = sign(dist);
    dist *= k;
    if (abs(dist) < 0.001) { hit = true; break; }
    if (distance(p, init) > 100.) {hit=false;break;}
    p += dist*cam;
  }
  float j = jort;
  vec3 col = vec3(1);


  if (j == 0.) {
    col = RED;
  }
  if (j == 1.) {
    col = BLUE;
  }
  //hit = false;

  fragColor.xyz = hit ? col*vec3(fakereflection(p, cam)) : backgroundcol(uv);

}

