// ##########################
// ### A trip to the void ###
// ##########################
//
// As the name implies, this is a trippy demo I made messing
// with raymarching. This code could be way more optimized.
//
// IF you want to know how it works, just mess with the flags
// below and read the program.
//
// ----
// 1.
//   This is a lattice of spheres we are moving through
// 2.
//   I apply a hue shift based on how far the ray march goes
// 3.
//   I ray march, but each iteration i rotate the ray direction slightly
//   the extent of this rotation is a sine function over time
// 4.
//   I mess with the FOV with a tan(mod(x, 3.14/2.)) function that makes
//   the zoomy looking effect.


#define CAM_ROTATE
#define RAY_ROTATE
#define WHACK_FOV
#define WHACK_COLOR

#define ITER 100
#define BASE_FOV 2.0

// distance function
// returns distance from point p to nearest sphere
// in infinite lattice
float dist(vec3 p) {
    p = fract(p) * 2.0 - 1.0;
    return length(p) - (0.8);
}

// ray marching algorithm, with a modification:
// if the RAY_ROTATE flag is defined, we can
// rotate the ray's direction after each iteration.
// *this is the primary source of "whack" in this shader*
float march(vec3 o, vec3 r) {
    float t = 0.0;
    vec3 p = o;
    for(int i = 0; i < ITER; i++) {
        #ifdef RAY_ROTATE
        float the = 0.04*sin(iTime/2.);
        r.xy *= mat2(cos(the),-sin(the),sin(the),cos(the));
        #endif
        p = o+r*t;
        t += dist(p)*0.5;
    }
    return t;
}

// hue2rgb function I ripped off.
#ifndef saturate
#define saturate(v) clamp(v,0.,1.)
#endif
vec3 hue2rgb(float hue){
	hue=fract(hue);
	return saturate(vec3(
		abs(hue*6.-3.)-1.,
		2.-abs(hue*6.-2.),
		2.-abs(hue*6.-4.)
	));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = uv*2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    // this technically isn't FOV, it's the initial distance
    // the ray is pushed outwards into the z axis before being
    // normalized and marched.
    // 0 values of X mean 180 degrees, infinite means 0 degrees.
    float x = 1.0;
    #ifdef WHACK_FOV
    x = 0.1*tan(mod(iTime/10., 3.14/2.));
    #endif

    // raycast with optional camera rotation
    vec3 ray;
    ray = normalize(vec3(uv,BASE_FOV*x));
    float the = iTime/2.;
    #ifdef CAM_ROTATE
    ray.xy *= mat2(cos(the),-sin(the),sin(the),cos(the));
    #endif

    vec3 origin = vec3(0,0,iTime);

    float t = march(origin, ray);

    float fog = 1.0 / (1.0 + t*t*0.1);

    vec3 col = vec3(fog);

    // psychedelics ensue
    #ifdef WHACK_COLOR
    col*=hue2rgb(t*2.);
    #endif

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define FDIST 0.7
#define PI 3.1415926


#define GROUNDSPACING 0.5
#define GROUNDGRID 0.05

#define SPHERESPACING 0.25
#define SPHEREGRID 0.01

#define RADIUS 1.

#define ABSORPTION_RATE vec3(0.9, 0.8, 0.8)
#define IOR 1.33
#define LOW_SCATTER 0.001
#define HIGH_SCATTER 0.03
#define SAMPLES 25
#define REFLECTIONS 3
#define RAYMARCH_STEPS 15
#define RAYMARCH_TOL 0.005

#define TIME_T 6.
#define TIME_H 6.
#define TIME_L 6.


float oscillate(float t_low, float t_high, float t_transition, float t_offset) {
    float t_osc = 0.5*(t_high+t_low)+t_transition;
    float h_l = 0.5*t_low/t_osc;
    float h_h = (0.5*t_low+t_transition)/t_osc;
    return smoothstep(0., 1., (clamp(abs(mod(iTime + t_offset, t_osc*2.)/t_osc-1.), h_l, h_h) - h_l) / (h_h - h_l));
}

/*vec2 rand2d(in vec2 uv) {
    return fract(mat2(-199.258, 457.1819, -1111.1895, 2244.185)*sin(mat2(111.415, -184, -2051, 505)*uv));
}*/

vec2 box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 ni, out vec3 no) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pin = - k - n;
    vec3 pout =  k - n;
    float tin = max(pin.x, max(pin.y, pin.z));
    float tout = min(pout.x, min(pout.y, pout.z));
    if (tin > tout) return vec2(-1.);
    ni = -sign(rd) * step(pin.zxy, pin.xyz) * step(pin.yzx, pin.xyz);
    no = sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    return vec2(tin, tout);
}

float sphere(in vec3 ro, in vec3 rd, in float r, in bool entering, out vec3 n) {
	float pd = dot(ro, rd);
    float disc = pd*pd + r*r - dot(ro, ro);
    if (disc < 0.) return -1.;
    float tdiff = sqrt(disc);
    float t = -pd + (entering ? -tdiff : tdiff);
    n = normalize(ro + t * rd);

    return t;
}

float spheresdf(in vec3 ro, in float r) {
    float mag = 0.02 * oscillate(TIME_L*.5, TIME_H*.5, TIME_T*0.5, -2.);
    return length(ro) - r + mag*(sin(ro.y*20. + iTime*1.32)+1.);
}

vec3 spheregrad(in vec3 ro, in float r) {
    vec2 diff = vec2(RAYMARCH_TOL, 0.);
    float dx = spheresdf(ro + diff.xyy, r) - spheresdf(ro - diff.xyy, r);
    float dy = spheresdf(ro + diff.yxy, r) - spheresdf(ro - diff.yxy, r);
    float dz = spheresdf(ro + diff.yyx, r) - spheresdf(ro - diff.yyx, r);
    return normalize(vec3(dx, dy, dz));
}

float hybridsphere(in vec3 ro, in vec3 rd, in float r, in bool entering, out vec3 n) {
    float t = sphere(ro, rd, r, entering, n);
    if (t > 0.) {
        for (int i=0; i<RAYMARCH_STEPS; i++) {
            float dist = spheresdf(ro + t*rd, r);
            t += (entering ? dist : -dist);
            if (dist < RAYMARCH_TOL) {
                n = spheregrad(ro + t*rd, r);
                return t;
            }
        }
    }
    return -1.;
}

vec3 bgcol(in vec3 rd) {
    return mix(vec3(0., 0., 1.), vec3(0.6, 0.8, 1.), 1.-pow(abs(rd.z), 2.));
}

vec3 z_to_vec(in vec3 d, in vec3 z) {
    vec3 u = normalize(cross(vec3(0., 0., 1.), d));
    vec3 v = cross(d, u);
    return u * z.x + v * z.y + d * z.z;
}

//raytrace the exterior surroundings
vec4 background(in vec3 ro, in vec3 rd) {
    float t = (-1. - ro.z)/rd.z;
    vec3 col1;
    if (t < 0.) {
        t = 1000.;
        col1 = bgcol(rd);
    } else {
        vec2 uv = ro.xy+t*rd.xy;
        if (max(abs(uv.x), abs(uv.y)) > 8.) col1 = bgcol(rd);
        else {
            vec2 checkers = smoothstep(vec2(GROUNDGRID*0.75), vec2(GROUNDGRID), abs(mod(uv, vec2(GROUNDSPACING))*2.-GROUNDSPACING));
            float aofac = smoothstep(0., 1.25, length(uv));
            col1 = mix(vec3(0.2), vec3(0.8), min(checkers.x,checkers.y)) * (1.-0.5*(1.-aofac));
        }
    }

    vec3 ni;
    float voffset = abs(0.8*sin(1.4*iTime));
    float t2 = sphere(ro - vec3(0.6 + RADIUS, 0., -0.4 + voffset), rd, 0.6, true, ni);
    vec3 spherero = t2 * rd + ro - vec3(0., 0., voffset);
    vec3 spherech = smoothstep(vec3(SPHEREGRID*0.75), vec3(SPHEREGRID), abs(mod(spherero, vec3(SPHERESPACING))*2.-SPHERESPACING));
    float tea = mix(t, t2, step(0., t2));
    return vec4(mix(col1, mix(vec3(1., 0., 0.), mix(vec3(1.), vec3(0.5, 0.6, 0.8), spherero.z+1.), min(spherech.x, min(spherech.y, spherech.z))), step(0., t2)), tea);
}

float seed;
float GetRandom(){return fract(sin(seed++)*43758.5453123);}
vec2 GetRandom2(){return vec2(GetRandom(), GetRandom());}

vec3 randnorm(vec2 seed) {
    vec2 utheta = GetRandom2() * 2. - 1.;
    utheta.y *= PI;
    float rho = sqrt(1.-utheta.x * utheta.x);
    return vec3(cos(utheta.y)*rho, sin(utheta.y)*rho, utheta.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    seed = iTime*sin(iTime) + (fragCoord.x+iResolution.x*fragCoord.y)/(iResolution.y);
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    float mouseY = iMouse.y < 1. ? 0.5 : (1.0-1.15*iMouse.y/iResolution.y) * 0.5 * PI;
    float mouseX = iMouse.x < 1. ? iTime*0.25 : -(iMouse.x/iResolution.x) * 2. * PI;
    vec3 eye = 4.*vec3(cos(mouseX) * cos(mouseY), sin(mouseX) * cos(mouseY), sin(mouseY));
    vec3 w = normalize(-eye);
    vec3 up = vec3(0., 0., 1.);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);

    vec3 rd = normalize(w * FDIST + uv.x * u + uv.y * v);

    vec3 ni;
    float t = hybridsphere(eye, rd, RADIUS, true, ni);
    vec3 ro = eye + t * rd;
    vec2 coords = ro.xy * ni.z + ro.yz * ni.x + ro.zx * ni.y;

    vec4 bgg = background(eye, rd);

    if (t > 0. && t < bgg.w) {

        float osc = oscillate(TIME_L, TIME_H, TIME_T, 0.);
        float SCATTER_FACTOR = mix(LOW_SCATTER, HIGH_SCATTER, osc);
        vec3 col = vec3(0.);
        float R0 = (IOR-1.)/(IOR+1.);
        R0*=R0;
        for (int i=0; i<SAMPLES; i++) {

            vec3 n = randnorm(coords + float(i) * vec2(1., 0.) * vec2(104., -30.6));
            // reflection
            float fresnel = R0 + (1.-R0) * pow(1.-dot(-rd, ni), 5.);
            vec3 rdr = mix(reflect(rd, ni), normalize(n + ni), SCATTER_FACTOR);
            vec3 reflcol = background(ro, rdr).xyz;

            // refraction, absorption and internal reflection
            vec3 rd_refr = mix(refract(rd, ni, 1./IOR), normalize(n-ni), SCATTER_FACTOR);

            vec3 insidecol = vec3(0.);
            float accum = 1.;
            vec3 transmission = vec3(1.);
            vec3 ro_refr = ro;

            for (int j=0; j<REFLECTIONS; j++) {

                vec3 ni2, no2;
                float tout = hybridsphere(ro_refr, rd_refr, RADIUS, false, no2);
                ro_refr = ro_refr + tout * rd_refr;
                vec3 randno2 = normalize(n + no2);
                vec3 rd_refr_out = mix(refract(rd_refr, -no2, IOR), randno2, SCATTER_FACTOR);

                float fresnel2 = R0 + (1.-R0) * pow(1.-dot(rd_refr_out, no2), 5.);

            	rd_refr = mix(reflect(rd_refr, -no2), randno2, SCATTER_FACTOR);
                transmission *= pow(ABSORPTION_RATE, vec3(tout));
            	insidecol += accum * (1.-fresnel2) * background(ro_refr, rd_refr_out).xyz * transmission;
                accum *= fresnel2;
            }    


            col += mix(insidecol, reflcol, fresnel);
        }
        col /= float(SAMPLES);

        fragColor = vec4(col, 1.);
    } else {
		fragColor = vec4(bgg.xyz, 1.);
    }
}

