#define MAX_STEPS 64
#define EPSILON 0.001

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
// Credit to the awesome iq for sdf-related functions

/*** HELPER FUNCTIONS ***/

float smoothUnion(in float iSDF1, in float iSDF2, in float iTransitionSize) {

 	float h = clamp(0.5 + 0.5*(iSDF2 - iSDF1)/iTransitionSize, 0.0, 1.0);
    return mix(iSDF2, iSDF1, h) - iTransitionSize*h*(1.0 - h);   

}

float roundedConeSDF(in vec3 iPoint, in float iSmallRadius, in float iBigRadius, in float iHeight) {

    vec2 q = vec2(length(iPoint.xz), iPoint.y);

    float b = (iBigRadius - iSmallRadius)/iHeight;
    float a = sqrt(1.0 - b*b);
    float k = dot(q, vec2(-b, a));

    if (k < 0.0) return length(q) - iBigRadius;
    if (k > a*iHeight) return length(q - vec2(0.0, iHeight)) - iSmallRadius;

    return dot(q, vec2(a, b)) - iBigRadius;

}

/*** MUSHROOM FUNCTIONS ***/

float stemSDF(in vec3 iPoint, vec2 iPosition, in float iRadius, in float iStemHeight) {

    // The stem is a capped cylinder with a varying radius    
    float d = length(iPoint.xz - iPosition) - iRadius*(1.0 + 0.05*cos(2.0*iPoint.y + iPoint.z));
    d = max(d, -iStemHeight + abs(iPoint.y));
    return d;

}

float capSDF(in vec3 iPoint, vec2 iPosition, in float iRadius, in float iStemHeight, in float iTiltingAngle) {

    // We first apply the inverse of the slight tilting rotation we want to the point

    float c = cos(0.3*iTiltingAngle);
    float s = sin(0.5*iTiltingAngle);

    mat3 tiltingMatrix = transpose(mat3(vec3(c, s, 0.0), vec3(-s, c, 0.0), vec3(0.0, 0.0, 1.0)));   

    vec3 tiltedPoint = tiltingMatrix*iPoint;

    // A cap is a smooth union between a rounded cone and a flattened half sphere

    float coneSdf = roundedConeSDF(tiltedPoint - vec3(iPosition.x, iStemHeight, iPosition.y), 0.2*iRadius, 0.4*iRadius, 2.0*iStemHeight);

    float sphereSdf = length(tiltedPoint - vec3(iPosition.x, 0.01*iStemHeight, iPosition.y)) - iRadius;

    float d = smoothUnion(coneSdf, sphereSdf, 2.0);

    // We apply a small displacement on the surface of the cap

    vec2 normalizedPoint = normalize(tiltedPoint.xz - iPosition);
    float cosTheta = normalizedPoint.x;
    float sinTheta = normalizedPoint.y;
    float theta = acos(cosTheta)*sign(sinTheta);

    d -= max(0.0, 0.38*abs(cos(8.0*theta) - 0.5) - 0.5);
    d = max(d, iStemHeight - tiltedPoint.y);

    return d;

}

float mushroomSDF(in vec3 iPoint, vec2 iPosition, in float iStemRadius, in float iStemHeight, in float iCapRadius, in float iTiltingAngle) {

    float d = min(stemSDF(iPoint, iPosition, iStemRadius, iStemHeight),
				capSDF(iPoint, iPosition, iCapRadius, iStemHeight, iTiltingAngle));
    return d;

}

float allMushroomsSDF(in vec3 iPoint) {

    float d = mushroomSDF(iPoint, vec2(-0.5, -3.0), 0.6, 2.0, 4.0, 0.1);
    d = min(d, mushroomSDF(iPoint, vec2(-8.0, 3.0), 1.1, 1.8, 4.0, -0.2));   
    d = min(d, mushroomSDF(iPoint, vec2(4.0, 1.0), 1.0, 1.6, 3.0, 0.3));
    d = min(d, mushroomSDF(iPoint, vec2(-7.5, -7.0), 0.5, 1.0, 2.0, 0.1));
	d = min(d, mushroomSDF(iPoint, vec2(-2.5, -9.0), 0.25, 1.0, 0.8, -0.15));
    return d;

}

float rayMarchMushrooms(in vec3 iOrigin, in vec3 iRay) {

 	float t = 0.0;
    vec3 p = iOrigin;
    for (int i = 0; i < MAX_STEPS; i++) {
        p = iOrigin + t*iRay;
     	float sdist = allMushroomsSDF(p);
        t += sdist;
        if (abs(sdist) < EPSILON) {
            break;
        }
    }
    return t;

}

vec3 computeMushroomDiffuseColor(in vec3 iPoint) {

    vec3 softPink = vec3(0.9, 0.6, 0.9);
    vec3 softBlue = vec3(0.5, 1.0, 1.0);    
    vec3 color = mix(softPink, softBlue, iPoint.y/0.9);
    color *= 2.4*texture(iChannel0, (iPoint.xz - 10.0)*0.1).xyz;
    return color;

}

/*** FLYING ORB FUNCTIONS ***/

vec3 orbPosition0;
vec3 orbPosition1;
vec3 orbPosition2;
vec3 orbPosition3;

float orbSDF(in vec3 iPoint, in vec3 iPosition, in float iRadius) {

    // An orb is a simple sphere    
	return length(iPoint - iPosition) - iRadius;   

}

float allOrbsSDF(in vec3 iPoint) {

    float d = orbSDF(iPoint, orbPosition0, 0.15);
    d = min(d, orbSDF(iPoint, orbPosition1, 0.1));
    d = min(d, orbSDF(iPoint, orbPosition2, 0.05));
    d = min(d, orbSDF(iPoint, orbPosition3, 0.1));
    return d;

}

/*** GROUND FUNCTIONS ***/

float groundSDF(in vec3 iPoint) {

    // Using a texture as a height map to roughen up the ground    
    float height = texture(iChannel1, (iPoint.xz + vec2(10.0))*0.2).x;
    return iPoint.y + 1.1 + 0.1*height;

}

vec3 computeGroundDiffuseColor(in vec3 iPoint) {

    return texture(iChannel0, (iPoint.xz + vec2(10.0))*0.05).xyz;

}

/*** COMPUTE NORMALS ***/

float sceneSDF(in vec3 iPoint) {

    float d = min(allMushroomsSDF(iPoint), allOrbsSDF(iPoint));
    d = min(d, groundSDF(iPoint));
    return d;

}

vec3 computeNormal(in vec3 iPoint) {

    return normalize(vec3(
        sceneSDF(vec3(iPoint.x + EPSILON, iPoint.y, iPoint.z)) - sceneSDF(vec3(iPoint.x - EPSILON, iPoint.y, iPoint.z)),
        sceneSDF(vec3(iPoint.x, iPoint.y + EPSILON, iPoint.z)) - sceneSDF(vec3(iPoint.x, iPoint.y - EPSILON, iPoint.z)),
        sceneSDF(vec3(iPoint.x, iPoint.y, iPoint.z  + EPSILON)) - sceneSDF(vec3(iPoint.x, iPoint.y, iPoint.z - EPSILON))
    ));

}

/*** RAY MARCHING ***/

float rayMarch(in vec3 iOrigin, in vec3 iRay, inout int ioObjectHit) {

 	float t = 0.0;
    vec3 p = iOrigin;

    for (int i = 0; i < MAX_STEPS; i++) {
        p = iOrigin + t*iRay;
     	float m = allMushroomsSDF(p);
     	float o = allOrbsSDF(p);
     	float g = groundSDF(p);

		float depth = min(min(o, m), g);
		// We hit an orb
		if (o < m && o < g) {
			depth = o;
			ioObjectHit = 2;
		} 
		// We hit a mushroom
		if (m < o && m < g) {
			depth = m;
			ioObjectHit = 1;
		}
		// We hit the ground
		if (g < o && g < m) {
			depth = g;
			ioObjectHit = 0;
		}		
        t += depth; 
        if (abs(depth) < EPSILON) {
            break;
        }
    }

    return t;

}

/*** LIGHTING AND SHADOWS ***/

// The scene's 2 static lights

vec3 lightPosition0 = vec3(15.0, 25.0, -15.0);
vec3 lightColor0 = vec3(0.4, 0.7, 1.0);

vec3 lightPosition1 = vec3(-15.0, 15.0, 15.0);
vec3 lightColor1 = vec3(0.8, 0.0, 0.8);

// Computes the shadow created by a particular light
vec3 computeShadow(in vec3 iPoint, in vec3 iLightPosition) {

    vec3 ray = normalize(iLightPosition - iPoint);
    float d = rayMarchMushrooms(iPoint, ray);
    return vec3(1.0 - 1.0/(1.0 + 0.03*d*d));

}

// Only mushrooms cast shadows
// Only static lights create shadows
// Only the ground receives shadows
vec3 computeAllShadows(in vec3 iPoint) {

    vec3 shadow = computeShadow(iPoint, lightPosition0);
    shadow *= computeShadow(iPoint, lightPosition1);
    return shadow;

}

// Basic Phong-type lighting
// Compute the lighting of a particular light
vec3 computeLighting(in vec3 iLightPosition, in vec3 iLightColor, in vec3 iDiffuseColor, in vec3 iPoint, in vec3 iNormal, in vec3 iOrigin, in bool iAttenuated) {

    vec3 pointToLight = iLightPosition - iPoint;
    float distanceToLight = length(pointToLight);
    vec3 pointToLight_n = normalize(pointToLight);
    vec3 reflected = reflect(-pointToLight_n, iNormal);
    vec3 pointToOrigin_n = normalize(iOrigin - iPoint);
    float lambertian = max(dot(pointToLight_n, iNormal), 0.0);        

	float specular = 0.0;

	if(lambertian > 0.0) {
    	float angle = max(dot(reflected, pointToOrigin_n), 0.0);
    	specular = pow(angle, 16.0);
	}

    // We soften the lighting if it's too harsh
    if (iAttenuated) 
		return 20.0*iLightColor*vec3(lambertian*iDiffuseColor + specular*vec3(1.0, 1.0, 1.0))/(1.0 + distanceToLight*distanceToLight);          
	else
  		return iLightColor*vec3(lambertian*iDiffuseColor + specular*vec3(0.0, 1.0, 1.0));          

}

// Computes the lighting of the scene using all the light sources: 4 orbs and 2 static lights
vec3 computeAllLighting(in vec3 iPoint, in vec3 iNormal, in vec3 iOrigin, in vec3 iDiffuseColor) {

    vec3 yellow = vec3(1.0, 1.0, 0.0);

    vec3 col = computeLighting(orbPosition0, yellow, iDiffuseColor, iPoint, iNormal, iOrigin, true)
        + computeLighting(orbPosition1, yellow, iDiffuseColor, iPoint, iNormal, iOrigin, true)
        + computeLighting(orbPosition2, yellow, iDiffuseColor, iPoint, iNormal, iOrigin, true)
        + computeLighting(orbPosition3, yellow, iDiffuseColor, iPoint, iNormal, iOrigin, true)
        + computeLighting(lightPosition0, lightColor0, iDiffuseColor, iPoint, iNormal, iOrigin, false)
        + computeLighting(lightPosition1, lightColor1, iDiffuseColor, iPoint, iNormal, iOrigin, false);

    return col;

}

/*** VIEWPOINT ***/

// Computing the matrix needed to transform the ray in world space
mat4 viewToWorldMatrix(in vec3 iEyePosition, in vec3 iTarget, in vec3 iUpDirection) {

    vec3 f = -normalize(iTarget - iEyePosition);
	vec3 s = normalize(cross(f, iUpDirection));
	vec3 u = cross(s, f);
	return mat4(
		vec4(s, 0.0),
		vec4(u, 0.0),
		vec4(-f, 0.0),
		vec4(0.0, 0.0, 0.0, 1.0)
	);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;
	uv = 2.0*uv - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    // Defining the origin and ray of the raymarching algorithm
    vec3 target = vec3(-3.0, -3.0, 8.0);
    vec3 origin = vec3(5.0, 25.0, -40.0);    
    mat4 rayMatrix = viewToWorldMatrix(origin, target, vec3(0.0, 1.0, 0.0));    
    vec3 ray = vec3(rayMatrix*vec4(normalize(vec3(uv, 5.0)), 0.0));

    // Defining orb parameters
    orbPosition0 = vec3(4.0*cos(iTime) - 2.5, -0.5, 6.0*sin(iTime));
    orbPosition1 = vec3(5.5*cos(iTime + 2.0) - 2.5, 8.0, 6.0*sin(iTime + 2.5));
    orbPosition2 = vec3(5.0*cos(iTime + 5.0) - 2.5, 7.0, 6.0*sin(iTime + 5.0));
    orbPosition3 = vec3(13.0*cos(iTime + 3.0) - 2.5, 2.5, 9.0*sin(iTime + 3.0));

    // Color of the point hit by the ray
    vec3 diffuseColor;

	// This represents the object hit
	// 0: ground
	// 1: mushroom
	// 2: orb
	int objectHit = -1;

    // Depth of the closest point hit by the ray
	float depth = rayMarch(origin, ray, objectHit);

	vec3 point = origin + depth*ray;    
    vec3 normal = computeNormal(point);

    // Determining the color
	switch (objectHit) {
		case 0:
			diffuseColor = computeGroundDiffuseColor(point);
			diffuseColor *= computeAllShadows(point);
			break;
		case 1:
			diffuseColor = computeMushroomDiffuseColor(point);
			break;
		case 2:
		default:
			diffuseColor = vec3(1.0, 1.0, 0.0);
			break;
	}

    // Shading
    vec3 finalColor = computeAllLighting(point, normal, origin, diffuseColor);

    // Output to screen
    fragColor = vec4(finalColor,1.0);

}

