float sdCylinder( vec3 p, float r, float h ) {
	vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r, h);
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdCube(vec3 p, float b) {
	vec3 d = abs(p) - b;
	return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

vec3 opRepeat(vec3 p, vec3 spacing) {
    return mod(p, spacing) - 0.5 * spacing;
}

vec3 opRepeatLimited(vec3 p, vec3 spacing, vec3 limit) {
    return p - spacing * clamp(round(p / spacing), -limit, limit);
}

float opSub(float d1, float d2) {
    return max(d1, -d2);
}

float opAdd(float d1, float d2) {
    return min(d1, d2);
}

float opInt(float d1, float d2) {
    return max(d1, d2);
}

vec3 rX(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.y = c * p.y - s * p.z;
    q.z = s * p.y + c * p.z;

    return q;
}

vec3 rY(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.x = c * p.x + s * p.z;
    q.z = -s * p.x + c * p.z;

    return q;
}

vec3 rZ(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.x = c * p.x - s * p.y;
    q.y = s * p.x + c * p.y;

    return q;
}

float sdTriPrism( vec3 p, vec2 h ) {
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

// -----------------

float planeDistance(vec3 position) {
    vec3 wingP = position.xzy * vec3(2.0,1.5,2.0);
    wingP.x = abs(wingP.x);
    wingP.z -= 0.3 * wingP.x; // tilt by skewing the space

    float wingDistance = sdTriPrism(wingP, vec2(1.0,0.02));

    vec3 bodyP = position.yzx * vec3(4.0,1.5,1.0);

    float bodyDistance = max(sdTriPrism(bodyP, vec2(1.0, 0.01)), dot(position, vec3(0.0,1.0,0.0)));

    return min(wingDistance, bodyDistance);
}

float d(vec3 position) {
    vec3 warpedPosition = position + sin(position.x * 2.0 + iTime * 2.3) * 0.1;
    return planeDistance(rZ(opRepeat(warpedPosition, vec3(2.0)), cos(iTime * 1.2) * 0.15));
}

vec3 gradient(vec3 p, float v) {
    const vec3 eps = vec3(0.001, 0.0, 0.0);
    return normalize((vec3(d(p + eps.xyy), d(p + eps.yxy), d(p + eps.yyx)) - v) / eps.x);
}

vec4 march(vec3 from, vec3 towards, float prec) {
    vec3 lastSamplePosition = from;
    float lastDistance = 0.0;
    for(int i = 0; i < 100; i++) {
        vec3 samplePosition = lastSamplePosition + max(lastDistance * 0.4, prec) * towards;
        float cDist = d(samplePosition);

        lastSamplePosition = samplePosition;
        lastDistance = cDist;

        if (cDist < 0.0) {
            return vec4(samplePosition, cDist);
        }
    }
    return vec4(1.0);
}

vec3 lightSurface(vec3 position, vec3 normal, vec3 toEye) {
    vec3 toLight = normalize(vec3(0.3, 0.9, 0.9));
    float ndotL = max(0.0, dot(normal, toLight));
    float ndotV = max(0.0, dot(normal, toEye));
    float ndotH = max(0.0, dot(normal, normalize(toEye + toLight)));
    const float diffuse = 0.8;
    vec3 ambience = mix(vec3(0.2,0.25,0.3), vec3(0.2,0.4,0.6), dot(normal, vec3(0., 1., 0.)) * -0.5 + 0.5);
    const float specular = 0.6;
    vec3 color = vec3((ndotL * diffuse + pow(ndotH, 2.) * specular) + ambience);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * (fragCoord.xy / iResolution.xy - 0.5);
    uv.x *= iResolution.x / iResolution.y;
    const vec3 cameraLookAt = vec3(0.0, -0.05, 0.0);
    vec3 cameraPosition = vec3(0.75,0.6,1.5);
    vec3 cameraForward = normalize(cameraLookAt - cameraPosition);
    vec3 cameraRight = cross(cameraForward, vec3(0.0, 1.0, 0.0));
    vec3 cameraUp = cross(cameraRight, cameraForward);
	vec3 rayDirection = normalize(uv.x * cameraRight + uv.y * cameraUp + 3.0 * cameraForward);

    cameraPosition += iTime * vec3(0.0,-0.2,-1.0);

    vec4 marchResult = march(cameraPosition, rayDirection, 0.001);
    vec3 backgroundColor = mix(vec3(0.4,0.75,1.0), vec3(0.2,0.5,0.9), (uv.y + 0.5));
    if (marchResult.w > 0.0) {
        fragColor = vec4(backgroundColor, 1.0); // ‚Äúsky‚Äù color
    } else {
        vec3 position = marchResult.xyz;
        float fogDistance = max(0., length(position - cameraPosition) - 4.);
        vec3 litColor = lightSurface(position, gradient(position, marchResult.w), -rayDirection);

        fragColor = vec4(mix(litColor, backgroundColor, 1.0 - exp(-fogDistance * 0.1)), 1.0);
    }
}

#define AA 1
vec2 r(vec2 ax, float a){return mat2(cos(a),-sin(a),sin(a),cos(a))*ax;}

float sdOctahedron( vec3 p, float s)
{
  p.xz =  r(p.xz,(-p.y*9.*sin(iTime)));
  p.xz =  r(p.xz,sin(iTime)*6.);
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

float checkers(vec2 p)
{
    vec2 w = fwidth(p) + .001;
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5/sin(iTime*0.1))-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    return 0.5 - 0.5*i.x*i.y;
}


float sdTorus( vec3 p, vec2 t )
{
	//p.yz = r(p.yz, .45+iTime);
  	vec2 q = vec2(length(p.xz)-t.x,p.y);  
    return length(q)-t.y;
}

float gimbal(vec3 pos)
{
    vec3 t1p = pos, t2p = pos, t3p = pos; 
    t1p.xy = r(t1p.xy,.45+sin(iTime*3.)*t1p.x*1.3+iTime);
    t2p.yz = r(t2p.yz,.45-cos(iTime*3.)*t2p.y*1.5-iTime);
    t3p.xy = r(t3p.xy,.45+cos(iTime*3.)*t3p.z*1.7-iTime);
    t3p.xy = r(t3p.xy,.45+sin(iTime*3.)*t3p.x*1.7+iTime);
    float torus1 = sdTorus(t1p,vec2(.27,.0125));
    float torus2 = sdTorus(t2p,vec2(.25,.0125));
    float torus3 = sdTorus(t3p,vec2(.2,.0125));    
    float gimbal = min(torus1, min(torus2, torus3));
    return gimbal;
}


vec2 map(vec3 pos) 
{
    vec3 inf = mod(pos+1.5,3.)-1.5;
    float plane = 1.4+pos.y+sin((pos.x)/2.333+iTime+cos((pos.z)/2.333+iTime));
	//float plane = 3.+pos.y;
    float oct = sdOctahedron(pos,.12);

    float g = gimbal(pos);
    float scene = oct;
    float MAT = 0.;
    scene = min(scene,plane);
	scene = min(scene,g);
 //   	scene = min(scene,k);
    if(scene == oct) MAT = 1.; 
    else if(scene == plane) MAT = 0.;
    else if(scene == g) MAT = 2.;
    return vec2(scene, MAT);
}


vec2 RM(vec3 ro, vec3 rd) 
{	float MAT = 0.;
    float dO = 0.0;
    for(int i = 0; i < 128; i++) 
    {
    	vec2 t = map(ro + rd* dO);
        if (abs(t.x) < .001) break;        
        dO+=t.x;
        MAT = t.y;
        if(dO > 20.) break;
    }    
    return vec2(dO,MAT);
}

vec3 n(vec3 pos)
{
    vec2 d = map(pos);
    vec2 e = vec2(.001,.0);
    vec3 pn = d.x - vec3(
        map(pos-e.xyy).x,
        map(pos-e.yxy).x,
        map(pos-e.yyx).x
        );
    return normalize(pn);
}
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}
// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 l(vec3 pos)
{
    vec3 nor = n(pos);
	vec3 total = vec3(0.);
    vec3 sundir = normalize(vec3(3.,15.,0.));
    float AO = calcAO(pos,nor);
    total+= clamp(pow(dot(nor,sundir),.2),.0,1.)*vec3(.9,.7,.5) *AO; //sun
	total+= clamp(dot(nor,vec3(.0,1.,0.)),.0,1.)*vec3(.3,.5,.7)*.15*AO; //sky
	total+= clamp(dot(nor,-sundir),.0,1.)*vec3(.9,.7,.5)*.01*AO;//bounce
    total*= calcSoftshadow( pos, sundir, 0.02, 2.5 );
    return(total);
}

