mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float a = iResolution.x/iResolution.y;


    vec3 col = vec3(0);
    float time = iTime;

    float pw = 2./iResolution.y;
    // comment the next two lines to get the grid without animation
  	uv.y += sin((uv.x/a)*3.14*5.+10.*sin(time*2.))*0.1;
    uv *= rot(time*0.1);

    vec2 gv = (fract(uv*2.)-0.5)/2.;

    col += smoothstep(0., 2.*pw, abs(abs(gv.y)-0.25));
    col *= smoothstep(0., 2.*pw, abs(abs(gv.x)-0.25));
    if(uv.x > 0. && uv.y > 0.) {
        col *= vec3(0.95, 0.5, 0.5); 
    }

    if(uv.x < 0. && uv.y < 0.) {
        col *= vec3(234, 255, 208)/256.0;
    }

    if(uv.x > 0. && uv.y < 0.) {
        col *= vec3(149, 225, 211)/256.0;
    }

    if(uv.x < 0. && uv.y > 0.) {
        col *= vec3(252, 227, 138)/256.0;
    }


    col = pow(col, vec3(1./2.2) );

    fragColor = vec4(col, 1);
}

