#define ITER 64.
#define PI 3.141592
#define time iTime

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

// iq website https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float ellipse( vec3 p, vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float vcaps( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length(p) - r;
}

float od (vec3 p, float d)
{return dot(p, normalize(sign(p)))-d;}

#define anim(t, sp, st) (fract(t*sp)*st*(1.-(fract(t*sp))))

float head (vec3 p)
{
    float y = anim(time, 4.,3.);
    float v =  vcaps(p-vec3(0.,y*0.2,0.), 0.4, 0.2);

    return v;
}

float eyes (vec3 p)
{
    float y = anim(time, 4.,3.);
    p.y -= 0.6;
    p.z += 0.1;
    p.x = abs(p.x)-0.15;

    return length(p+vec3(0.,y*0.2,0.))-0.05;
}

float mouth (vec3 p)
{
    float y = anim(time, 4.,3.);
    p.y -= 0.5;
    p.z += 0.18;
    return od (p+vec3(0.,y*0.2,0.), 0.05);
}

float body (vec3 p)
{
    p.y -=.5;
    float y = anim(time,4.,3.);

    float sy = 0.6+y;
    float sxz = 1./sy;
    return ellipse(p+vec3(0.,y*0.5,0.), vec3(0.5*sxz,0.5*sy,0.5*sxz));
}

float feets (vec3 p)
{
    p.y += 1.4;
    p.x = abs(p.x)+0.2;
    p.x -= anim(p.y,1., anim(time, 4., 18.))*0.2+.3;
    return vcaps(p, 1.,0.05);
}

float baguette (vec3 p)
{

    float per = 0.6;
    p.x = abs(p.x);
    p.x -= 2.;
    p.xz *= rot(time*5.);
    vec3 pp = p;
    p.z += 0.2;
    p.y = mod(p.y, per)-per*0.5;
    float c = length(p.yz)-(0.15-abs(p.x)*0.1);

    p = pp;
    p.y += 1.1;
    return max(-c,vcaps(p,2.2, .2));
}

int mat_id;
float SDF (vec3 p)
{
    float f = feets(p);
    float b = body(p+vec3(0.,.45,0.));
    float h = head(p);
    float e = eyes(p);
    float m = mouth(p);
    float ba = baguette(p);

    float d = min(min(e,m),min(f,min(h, b)));
    d = min(d, ba);

    if (d == f || d == m) mat_id = 1;
    if (d == b || d == h) mat_id = 2;
    if (d == e) mat_id = 3;
    if (d == ba) mat_id = 4;

    return d;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01, 0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float lighting (vec3 n, vec3 l)
{
    return max(0.,dot(n, l));
}

float background (vec2 uv)
{
    float per = .9;

    vec2 id = floor(uv/per);
    uv = mod(uv,per)-per*0.5;
    uv.x = abs(uv.x)-0.05;
    uv *= rot(-PI/4.);

    return step(length(uv*vec2(1.,0.5)),clamp(sin(length(id*2.)-time),0.05,0.15));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.,0.,-4.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        l = normalize(vec3(1.,2.,-3.)),
        col = mix(vec3(0.97),vec3(.95,0.,0.),background(uv*8.));

    float shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER;i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            hit = true;
            shad = i/ITER;
            break;
        }

        p += d*rd*0.5;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        float light = floor(lighting(n, l)*3.)/3.;
        if (mat_id == 1) col = mix(vec3(0.5,0.3,0.),vec3(0.8,0.8,0.),light);
        if (mat_id == 2) col = mix(vec3(0.2,0.2,0.3),vec3(0.5,0.5,0.6),light);
        if (mat_id == 3) col = vec3(0.); 
        if (mat_id == 4) col = mix(vec3(0.5,0.2,0.1),vec3(0.8,0.8,0.5),light);
        col  *= vec3(1.-shad);
    }

    float dit = texture(iChannel0, fragCoord.xy/8.).r;

    float detail = 4.;
    col += dit/detail;
    col = floor(col*detail)/detail;

    fragColor = vec4(sqrt(col),1.);
}

//
// Map functions.
//
// From https://github.com/msfeldstein/glsl-map.
//

float map(float value, float inMin, float inMax, float outMin, float outMax) {
  return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

vec2 map(vec2 value, vec2 inMin, vec2 inMax, vec2 outMin, vec2 outMax) {
  return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

vec3 map(vec3 value, vec3 inMin, vec3 inMax, vec3 outMin, vec3 outMax) {
  return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

vec4 map(vec4 value, vec4 inMin, vec4 inMax, vec4 outMin, vec4 outMax) {
  return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

float safeMap(float value, float inMin, float inMax, float outMin, float outMax)
{
    if (value > inMax)
        return outMax;
    else if (value < inMin)
        return outMin;
    else
        return map(value, inMin, inMax, outMin, outMax);
}


//
// Random functions
//

uint hash(uint x) 
{
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

float random(uint hashed_value) 
{
    const uint mantissaMask = 0x007FFFFFu;
    const uint one          = 0x3F800000u;

    hashed_value &= mantissaMask;
    hashed_value |= one;

    float  r2 = uintBitsToFloat(hashed_value);
    return r2 - 1.0;
}


float random(float f) 
{    
    return random(hash(floatBitsToUint(f)));
}

float random(vec2 v)
{
    uint hashed =
        hash(
            floatBitsToUint(v.x) 
            ^ hash(floatBitsToUint(v.y)));
    return random(hashed);
}

float random(vec3 v)
{
    uint hashed =
        hash(
            floatBitsToUint(v.x) 
            ^ hash(floatBitsToUint(v.y))
            ^ hash(floatBitsToUint(v.z)));
    return random(hashed);
}

//
// Utility functions.
//

float noise(float x) 
{
    float 	i = floor(x), 
        	f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);    
    return 2.0 * mix(random(i), random(i + 1.0), u) - 1.0;
}

//
// Advanced functions.
//

void getSkyColor(vec2 uv, inout vec3 color)
{
    float h = max(0.0, 1.4 - uv.y - pow(abs(uv.x - 0.5), 3.0));
    color.r = pow(h, 3.0);
    color.g = pow(h, 7.0);
    color.b = 0.2 + pow(max(0.0, h - 0.1), 10.0);    
}

float terrain(float x)
{   
    x += 0.1;
    float y = 0.0;
    float k;
    for (int octave = 0; octave < 7; ++octave)
    {
        k = pow(2.0, float(octave));
        y += noise(x * k) / k;
    }
	return y * 0.6 + 0.8;
}

float water(float x)
{   
    x += 0.5;
    float y = 0.0;
    float k;
    for (int octave = 0; octave < 10; ++octave)
    {
        k = pow(2.0, float(octave));
        y += noise(x * k) / k;
    }
	return y * 0.05 + 0.4;
}

float tree(float x, float h)
{

    float k;
    float y = noise(x * 81.5);

    return safeMap(h, 0.45, 0.55, 0.0, 1.0) * max(0.0, y * 0.2);      
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float shift = 0.09 * iTime + 0.2;
    float x = uv.x + shift;

    vec3 col;

    float h = max(water(x), terrain(x));
    h += tree(x, h);

    if (uv.y < h)
    {
        col = vec3(0.0);
    }
    else
    {
    	getSkyColor(uv, col);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// This is a 'weaved' version of my 4.8^2 semi-regular truchet tiling, which
// you can see here: https://www.shadertoy.com/view/MlyBRG

const float pi = 3.141592653589;
const float r = .02;
const float r2 = .005;
const float slope = .1;
const vec2 dir = vec2(.1, slope);
const float r3 = (r + r2 * 0.15) / (dir.x / length(dir));

// Pixel width for anti-aliasing.
float w;


float hash(vec2 p)
{
    return texelFetch(iChannel0, ivec2(p) & 255, 0).r;
}

// (via comments on https://www.shadertoy.com/view/wdBSRm):
// adx's considerably more concise version of the method above.
// On a side note, if you haven't seen it before, his "Quake / Introduction" 
// shader is well worth the look: https://www.shadertoy.com/view/lsKfWd
void solveCircle(vec2 a, vec2 b, out vec2 o, out float r){

    vec2 m = a + b;
    o = dot(a, a)/dot(m, a)*m;
    r = length(o - a);

}

float arcDistance(vec2 p, vec2 a, vec2 b)
{
    if(abs(a.x * b.y - a.y * b.x) < 1e-5)
        return distance(p, mix(a, b, clamp(dot(p - a, b - a) / dot(b - a,b - a), 0., 1.)));

    vec2 o;
    float r;
    solveCircle(a, b, o, r);
    return abs(distance(p, o) - r);
}

// Draws a line or arc connecting a and b inside a polygon.
float l(vec2 p, vec2 a, vec2 b)
{
    float d = arcDistance(p, a, b);
    return smoothstep(0.03, 0.03 - w * 2., d - .04);
}

// Based on https://www.shadertoy.com/view/llGSzw
uint hash1( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return n;
}


void sdRope(vec2 p, out float mask_d, out float outline_d)
{    
    float pd = dot(p, vec2(dir.y, -dir.x)) / ((r + r2 * 0.15) * 2. * length(dir));

    p.y += 2. * r3 * floor(pd) + r3;

    vec2 q = dir * clamp(dot(p, dir) / dot(dir, dir), -.5, +.5);

    float d = distance(p, q) - r;

    mask_d = d - r2;
    outline_d = min(max(-d, abs(p.x) - r * 3.), abs(d) - r2);
}

vec4 arcDistanceRope(vec2 p, vec2 a, vec2 b)
{
    float mask_d;
    float outline_d;
    float mask2;

    float sr = r3 * 2.;

    if(abs(a.x * b.y - a.y * b.x) < 1e-5)
    {
    	float ld = distance(p, mix(a, b, clamp(dot(p - a, b - a) / dot(b - a,b - a), 0., 1.)));

        vec2 n = normalize(b - a);

        float da = dot(a, n);
        float db = dot(b, n);
        float d = dot(p, n);

        vec2 q = vec2(dot(p, -vec2(n.y,-n.x)), mix(round(da / sr) * sr, round(db / sr) * sr, (d - da) / (db - da)));

        sdRope(q + vec2(0., r3), mask_d, outline_d);

        mask2 = ld-.05;
    }
    else
    {    
        vec2 o;
        float r;
        solveCircle(a, b, o, r);

        vec2 nm = normalize((a + b) / 2. - o);

        vec2 na = normalize(a - o);
        vec2 nb = normalize(b - o);
        vec2 np = normalize(p - o);

        float tha = asin(na.x * nm.y - na.y * nm.x) * r;
        float thb = asin(nb.x * nm.y - nb.y * nm.x) * r;
        float th = asin(np.x * nm.y - np.y * nm.x) * r;

        vec2 q = vec2(length(p - o) - r, mix(round(tha / sr) * sr, round(thb / sr) * sr, (th - tha) / (thb - tha)));

        sdRope(q + vec2(0., r3), mask_d, outline_d);

        mask2 = abs(distance(p, o) - r)-.05;
    }

    vec3 col = vec3(mix(vec3(.02), vec3(.8), smoothstep(0.0, w, outline_d)));

    return vec4(col, 1. - smoothstep(0.0, w, mask2 - 0.05));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.y;

    fragColor = vec4(1);

    vec2 p = uv.xy * 6. + iMouse.xy / iResolution.xy * 10.;

    w = max(length(dFdx(p)), length(dFdy(p)));

    p.x += iTime / 3.;

    float el = tan(2. * pi / 16.);
    float hyp = sqrt(1. - el * el);
    float d = cos(2. * pi / 16.) * hyp;

    vec2 ip = floor(p - .5) * 2.;
    vec2 fpa = abs(fract(p) - .5);
    vec2 fp = fract(p - .5) - .5;

    vec4 a = vec4(vec3(.8), 0.);
    float b = hash(ip);
    float c = 0.;

    uint num_inds = 0U;
    float th = 0., thoffset = 0.;
    float r = 1.;

    if(fpa.x + fpa.y < (1. - sqrt(2.) / 2.))
    {
        // Square.
        ip = floor(p) * 2. +1.;
        b = hash(ip);
        fp = fract(p) - .5;
        if(b > .5)
            fp = fp.yx * vec2(1, -1);

        num_inds = 4U;
        th = pi * 2. / 4.;
        r = .5 * d;
        thoffset = th / 2.;

        c = max(c, smoothstep((1. - sqrt(2.) / 2.) - w * sqrt(2.) * 2., (1. - sqrt(2.) / 2.), max(abs(fp.x + fp.y), abs(fp.y - fp.x))));
    }
    else
    {
        // Octagon.
        th = floor(mod(b,.25)/.25*8.) * pi * 2. / 8.;
        fp *= mat2(cos(th), sin(th), -sin(th), cos(th));

        th = pi * 2. / 8.;

        num_inds = 8U;

        float d = max(abs(fp.x + fp.y) / sqrt(2.), abs(fp.y - fp.x) / sqrt(2.));
        c = max(c, smoothstep(.5 - w * 2., .5, max(d, max(abs(fp.x), abs(fp.y)))));
    }


    // Shuffle the indices by using the Fisher-Yates algorithm
    // https://en.wikipedia.org/wiki/Fisher%E2%80%93Yates_shuffle

    uint inds[8];

	inds[0] = 0U;

    uint seed = uint(ip.x + ip.y * 8192.)*319U;
    for(uint j = 1U; j < num_inds; ++j)
    {
        seed = hash1(seed);
        uint k = seed % j;
        uint temp = inds[k];
        inds[k] = j;
        inds[j] = temp;
    }

    // Draw the arcs.

    for(uint j = 0U; j < num_inds; j += 2U)
    {
        uint ia = inds[j];
        uint ib = inds[j + 1U];
        vec2 pa = vec2(cos(th * float(ia) - thoffset), sin(th * float(ia) - thoffset)) * r / 2.;
        vec2 pb = vec2(cos(th * float(ib) - thoffset), sin(th * float(ib) - thoffset)) * r / 2.;
        vec4 d = arcDistanceRope(fp, pa, pb);
        a = mix(a, d, d.a);
    }


    // Shade.
    vec3 col = mix(vec3(1), vec3(.3), c);

    float l = 1. - (min(fract(iTime / 6.) * 2., 2. - 2. * fract(iTime / 6.)) - .5) * 16.;

    fragColor.rgb = mix(a.rgb, a.rgb * col, (1. - a.a) * smoothstep(0., 1., l));

    // Gamma etc.
    fragColor.rgb = pow(fragColor.rgb, vec3(1. / 2.2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = fragCoord/iResolution.xy;
  uv -= 0.5;

  float a = iTime / 15.0 + cos(iTime/3.0 + cos(iTime)/4.0)/7.0 + cos(length(uv) * 5.0 - iTime/3.0)/10.0;
  float s = sin(a);
  float c = cos(a);
  mat2 m = mat2(c, -s, s, c);



  uv = m * uv;


  float c1 =  cos(uv.x * 40.0 * (6.0 + cos(iTime/3.0 + 1.0)) / (2.0 + sin(uv.y*4.0+ iTime)/4.0) + iTime);
  float c2 =  cos((uv.y * 40.0 * (6.0 + cos(iTime/3.0)) + 20.0 * sin(uv.x/3.0 + iTime/4.0) * 3.0) / (2.0 + sin(uv.x*2.0+ iTime)/4.0) + iTime);

  fragColor.x = c1;
  fragColor.z = (1.0 - c1 + c2) / 2.0;
  fragColor.y = c2;

  fragColor = round(fragColor * 4.0) / 4.0;



}

