    vec2 uv = fragCoord/iResolution.xy;


    uv *= vec2(9.0, 5.0);

    float limit = (sin(iTime + uv.x*uv.y)*0.25+0.75) * 0.3;
    vec2 sub = vec2(mod(uv.x,1.0), mod(uv.y,1.0));

    vec2 c = sub * 2.0 - 1.0;
    float dsq = c.x*c.x + c.y*c.y;

    vec3 col = vec3(0.2,0.6,1.0) * (dsq <limit*limit && dsq > limit*limit*0.3 ? sin(uv.x + uv.y*1.5)*0.25+0.75 : 0.2);


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define AA 2.
#define R iResolution.xy

mat2 rot(float a) {
	float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float sdSphere(vec3 p, float r) {
	return length(p) - r;    
}

vec2 map( in vec3 pos, float time )  
{
    float id = 0.;
    float sphere = sdSphere(pos, 0.5);
    float plane = pos.y + 0.5;

    if(plane < sphere) {
    	id = 1.;    
    }

    float d = min(sphere, plane);

    return vec2(d, id);
}

vec3 calcNormal( in vec3 pos, float t ) 
{
    vec2 e = vec2(0.001, 0.0);
    return normalize( vec3(map(pos+e.xyy,t).x-map(pos-e.xyy,t).x,
                           map(pos+e.yxy,t).x-map(pos-e.yxy,t).x,
                           map(pos+e.yyx,t).x-map(pos-e.yyx,t).x ) );
}

float castShadow( in vec3 ro, vec3 rd, float time )
{
    float res = 1.0;
    float t = 0.00;
    for( int i=0; i< 100; i++ )
    {
        vec3 pos = ro + t*rd;
        float h = map( pos, time ).x;
        res = min( res, 16.0*h/t );
        if ( res<0.001 ) break;
        t += h;
        if( t > 10.0 ) break;
    }

    return clamp(res,0.0,1.0);
} 

vec2 castRay( in vec3 ro, vec3 rd, float time )
{
    float m = -1.0;
    float t = 0.01;
    int i=0;
    for( i=0; i<100; i++ )
    {
        float precis = 0.001 * t;
        vec3 pos = ro + t*rd;

        vec2 h = map( pos, time );
        m = h.y;
        if( h.x<precis )
            break;
        t += h.x;
        if( t>20.0 )
            break;
    } 
    if( t>20.0 ) m=-1.0;
    return vec2(t, m);
}

void mainImage( out vec4 O, in vec2 U )
{
    float time = iTime/4.;

    vec3 col = vec3(0);
    vec3 res = vec3(0);

    for(float aax=0.; aax < AA; aax++)
    for(float aay=0.; aay < AA; aay++)
    {
        vec2 p = (2.*(U + vec2(aax, aay) / AA)-R)/R.y;

        vec3 ta = vec3(0,0,0);
        vec3 ro = vec3(0, 1.0 + (-sin(time) * .5 + .5), -1);
        ro.xz *= rot(time);

        vec3 ww = normalize( ta-ro );
        vec3 uu = normalize( cross(ww, vec3(0,1,0)) );
        vec3 vv = normalize( cross(uu,ww) );

        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        vec3 col = vec3(0.0);

        vec2 tm = castRay(ro, rd, time); 

        if( tm.x < 20. )
        {
            float t = tm.x;
            float id = tm.y;
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos, time);

            vec3 mate = vec3(0.6); 

            if(id < 0.5) { // sphere

            	mate = vec3(.8); 

                //calculate uv coordinates for the sphere:
                // https://en.wikipedia.org/wiki/UV_mapping

                // posn is the normalized vector from P to the sphere center
                // which in this example is at (0, 0, 0).
                vec3 posn = normalize(pos);
                float u = 0.5 + atan(posn.z, posn.x) / (2.*3.1415);
                float v = 0.5 - asin(posn.y) / 3.1415;

                // trick from Fabrice to avoid the discontinuity at Greenwich.
                vec2 uv = vec2(u,v),
                     df = fwidth(uv);
               	if(df.x > 0.5) df.x = 0.;

                mate = textureLod(iChannel0, uv, log2(max(df.x, df.y)*1024.)).xyz;

                // reuse mate 
                nor += 0.05*length(mate);

            }
            else if(id < 1.5) { // floor

            	mate = vec3(0.);   
                float s = 12.;
                float w = 0.05;

                if(fract(pos.x*s) < w) {
                	mate = vec3(.3);    
                }

                if(fract(pos.z*s) < w) {
                	mate = vec3(.3);    
                }
            }

            vec3  light1 = vec3( 0, 1, 1 );
            vec3  light2 = vec3( 1, 3, 1 );

            float key = clamp( dot( light1, nor ), 0.0, 1.0 );
            float bac = clamp( 0.2 + 0.8*dot( light2, nor ), 0.0, 1.0 );
            float amb = (0.7+0.3*nor.y);
            float shadow = clamp(castShadow(pos + nor*.1, light1 - pos, time), 0., 1.);

            vec3 brdf  = 1.0*vec3(0.40,0.40,0.40)*amb*shadow;
            brdf += 1.0*vec3(1.00,1.00,1.00)*key*shadow;
            brdf += 1.0*vec3(0.40,0.40,0.40)*bac;

            col = mate * brdf * exp(-0.6*t);
        }

        res += clamp(col, 0.0, 1.0);
    }

    col = pow( res/(AA*AA), vec3(0.4545) );

    O = vec4(col, 1.0);
}

