#define Max_Steps 100
#define Max_Dist 100.
#define Surf_Dist 0.01

// 
float GetDist(vec3 p)
{
    vec4 s = vec4(0,1,6,1);// ÁêÉÁöÑ‰ΩçÁΩÆ(s.xyz)ÂíåÂçäÂæÑÔºàs.wÔºâ
    float sphereDist = length(p-s.xyz)-s.w;// PÁÇπÂà∞ÁêÉÈù¢ÁöÑË∑ùÁ¶ª
    float planeDist  = p.y;// PÁÇπÂà∞Âπ≥Èù¢ÁöÑË∑ùÁ¶ª

    float d = min(sphereDist,planeDist);
    return d;
}

// 
float RayMarch(vec3 ro, vec3 rd)
{
    float d0 = 0.;
    for(int i = 0; i < Max_Steps; i++)
    {
        vec3 p = ro + rd*d0;
        float ds = GetDist(p);
        d0+=ds;
        if(d0>Max_Dist || ds < Surf_Dist) 
            break;           
    }

    return d0;     
}

// È°∂ÁÇπÁöÑÊ≥ïÁ∫ø
vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(0.01,0);

    vec3 n = d-vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));
    return normalize(n);
}

// ËÆ°ÁÆóÁÅØÂÖâÔºåÁÇπÂÖâÊ∫ê
float GetLight(vec3 p)
{
    vec3 lightPos = vec3(0,5,6);
    lightPos.xz += vec2(sin(iTime),cos(iTime))*2.0;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n,l),0.,1.);

    float d = RayMarch(p + n*Surf_Dist*2.0,l);
    if(d<length(lightPos-p))dif*=0.1;
    return dif;
}

