#define PI 3.14159

vec4 over( in vec4 a, in vec4 b ) {
    return mix(a, b, 1.-a.w);
}

float nCap(vec2 uv, float angle, float r, float r1, float n) {
	float r2 = 1./r1;
    float a = atan(uv.y, uv.x)+angle;
    float b = a/PI*n/2.+PI*r2*2.0;
    float f = fract(b);
    float l = length(uv);
    float d = sin(f*PI*r2) * step(f, r1);
    return (1.-d*0.05)*r-l;
}

float line( in vec2 p, in vec2 a, in vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-iResolution.xy*0.5)/-iResolution.y*2.0;

    float av = sin(iTime)*PI*5./6.; // angle value

    mat2 m = mat2(cos(av), sin(av), -sin(av), cos(av));

    float a = atan(uv.y, uv.x);
    float l = length(uv);
	float g = -1. + smoothstep(.5, -.5, uv.y) * 2.;

    vec4 col = vec4(vec3(.04), 1.); // background
    vec2 la = vec2(.0, -.24)*m;
    vec2 lb = vec2(.0, -.41)*m;

    float d = .0;
    float e = .0;
    for(float q = 0. ; q < 11. ; q += 1.) {
        float aq = -PI*1./3.+q*PI*1./6.;
        vec2 v = vec2(.66, .0) * rot(aq);
        d += smoothstep(.01, .0, distance(uv, v)-.002);
        e += smoothstep(.02, .0, distance(uv, v)-.008);
    }
    col = over(vec4(vec3(e*e), d*d), col); // divs

    col = over(vec4(vec3(.20+g*.08-(l-.48)), smoothstep(.02, .0, l-.48)), col); // bottom
    col = over(vec4(vec3(.16), smoothstep(.002, .0, l-.40)), col); // bottom to cap
    col = over(vec4(vec3(.36 - smoothstep(.2, .8, l)*0.4), smoothstep(.02, .0, l-.38)), col); // cap
    col = over(vec4(vec3(.50), max(0., (1.-pow(abs(uv.x), .25)))*smoothstep(.02, 0., abs(l-.37))), col); // cap bevel

    col = over(vec4(vec3(1.), smoothstep(.01, .0, line(uv, vec2(.0, -.2)*m, vec2(.0, -.35)*m)-.01))*.8, col);


	fragColor = col;
}

