#define ITER 100.
#define PI 3.141592

float hash21 (vec2 x)
{return fract(sin(dot(vec2(42.1, 48.4),x))*1245.5);}

vec2 moda (vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a,per)-per*0.5;
    return vec2(cos(a),sin(a))*l;
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float cyl(vec2 p, float r)
{return length(p)-r;}

// iq's formula http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float g1 = 0.;
float gem (vec3 p)
{
    p.y += sin(iTime)*2.-1.5;
    p.yz *= rot(iTime);
    p.xy *= rot(iTime);
    float d = dot(p,normalize(sign(p)))-.8;
    g1 += 0.1/(0.1+d*d);
    return d;
}

float prim1(vec3 p)
{return sdCapsule(p, vec3(0.,-3.,0.), vec3(0.,3.,0.), 1.4+p.y*0.3);}

float cocoon(vec3 p)
{
     p.z += texture(iChannel0, p.yx*0.04*sin(length(p.xy)-iTime)).r*length(p)*0.5;
    p.xy *= rot(p.z*0.3);
    vec3 pp = p;
    p.x += sin(p.y*0.5);
    p.y += 1.;
    float cap = prim1(p);

    p.xz *= rot(iTime);
    p.xz *= rot(p.y*0.5);
    p.xz = moda(p.xz, 2.*PI/3.);
   	p.x -= (2.-abs(p.y-1.)*0.1);
    float branch1 = cyl(p.xz, 0.4);

    p = pp;

    p.xy *= rot(PI/3.);
    p.xz *= rot(iTime*0.2);
    p.xz *= rot(p.y*0.4);
    p.xz = moda(p.xz, 2.*PI/2.);
   	p.x -= (1.+abs(p.y)*0.1);
    float branch2 = cyl(p.xz, 0.3+abs(p.y)*0.03);

    p = pp;

    p.xy *= rot(-PI/3.);
    p.xz *= rot(iTime*0.2);
    p.xz *= rot(p.y*0.4);
    p.xz = moda(p.xz, 2.*PI/2.);
   	p.x -= (1.+abs(p.y)*0.1);
    float branch3 = cyl(p.xz, 0.1+abs(p.y)*0.03);

    return smin(branch3,smin(branch2,smin(cap,branch1,.5), .5),.5);
}

float SDF (vec3 p)
{
	return min(gem(p),cocoon(p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(0.001,0.001,-8.),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.);

    float shad = 0.;
    bool hit = false;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            // phantom mode from lsdlive 
			// https://www.shadertoy.com/view/XsGfDD
            d = max(abs(d),0.2);
        }
        d *= 0.2+dither*0.2;
        p += d*rd;

    }

    if (hit)
    {col = vec3(1.-shad);}

    col += g1*vec3(clamp(length(uv),0.,1.),0.1,0.6)*0.3;
    fragColor = vec4(sqrt(col),1.0);
}

