#define ITER 64.
#define PI 3.141592
#define tt (iTime*6.)

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.4,16.1)))*1245.4);}

float moda (inout vec2 p, float rep)
{
    float per = (2.*PI)/rep;
    float a = atan(p.y,p.x);
    float l = length(p);
    float id = floor(a/per);
    a = mod(a,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
    if (abs(id)>= rep*0.5) id = abs(id);
    return id;
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float g1 = 0.;
float box (vec3 p, vec3 c)
{
    float d = length(max(abs(p)-c,0.));
    g1 += 0.1/(0.1+d*d);
    return d;
}

float g2;
float cyl (vec2 p, float r)
{
    float d = length(p)-r;
    g2 += 0.1/(0.1+d*d);
    return d;
}

vec2 path(float t) 
{
	float a = sin(t*.2+1.5), b = sin(t*.2);
	return vec2(a, a*b);
}

float cid;
float SDF (vec3 p)
{
    p.xy -= path(p.z)*3.;
    p.xy *= rot(p.z*0.15);

    vec3 pp = p;

    p += sin(p.yzx - cos(p.zxy));
    p += sin(p.yzx/1.5 + cos(p.zxy)/2.)*.5;
    float t = -length(p.xy)+6.;

    p = pp;
    moda(p.xy, 6.);
    p.x -= 3.;
    float b = box(p,vec3(.2,0.2,1e10));

    p = pp;
    p.xy *= rot(PI/6.);
    cid = moda(p.xy, 6.);
    p.y += sin(p.z)*0.5;
    p.x -= 2.;
   	float c = cyl(p.xy, 0.3);
    return min(min(c,t),b);
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*t+d));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(0.,0.,-5.+tt),
        ta = vec3(0.,0.,tt);

    ro.xy += path(ro.z)*3.;
	ta.xy += path(ta.z)*3.;

    vec3 rd = getcam(ro,ta, uv),
    	p = ro,
    	col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            shad = i/ITER;
            break;
        }
         d *= 0.6+dither*0.1;
        p += d*rd;
    }

    float t = length(ro-p);
	col = vec3(shad)*0.5;


    col += g2 * 0.09* palette(cid, 
                        vec3(0.5),
                        vec3(0.5),
                        vec3(0.1+cid), 
                        vec3(0.,0.3,0.45)); 
    col -= g1*0.25;

    fragColor = vec4(pow(clamp(col,0.,1.),vec3(0.4545)),1.0);
}

#define PI 3.14159

#define FAR_PLANE 10.
#define MAX_SAMPLES 60

mat3 Rotate(vec3 angles)
{
    vec3 c = cos(angles);
    vec3 s = sin(angles);

    mat3 rotX = mat3( 1.0, 0.0, 0.0, 0.0,c.x,s.x, 0.0,-s.x, c.x);
    mat3 rotY = mat3( c.y, 0.0,-s.y, 0.0,1.0,0.0, s.y, 0.0, c.y);
    mat3 rotZ = mat3( c.z, s.z, 0.0,-s.z,c.z,0.0, 0.0, 0.0, 1.0);

    return rotX*rotY*rotZ;
}



/* Noise functions */
float hash(float h) {
	return fract(sin(h) * 43758.5453123);
}

float noise(vec3 x) {
    //return hash(x.x);
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f * f * (3.0 - 2.0 * f);

	float n = p.x + p.y * 157.0 + 113.0 * p.z;
	return mix(
			mix(mix(hash(n + 0.0), hash(n + 1.0), f.x),
					mix(hash(n + 157.0), hash(n + 158.0), f.x), f.y),
			mix(mix(hash(n + 113.0), hash(n + 114.0), f.x),
					mix(hash(n + 270.0), hash(n + 271.0), f.x), f.y), f.z);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

//https://www.shadertoy.com/view/Ms2SD1
float noise( vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix(mix( rand( i + vec2(0.0,0.0) ), 
                     rand( i + vec2(1.0,0.0) ), u.x),
                mix( rand( i + vec2(0.0,1.0) ), 
                     rand( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm(vec2 v) {
	float sum = 0.;
    for (int i = 0; i < 8; i++) {
        float e = pow(2., float(i));
        sum += noise(v * vec2(e)) / e;
    }
    return sum;
}

float marble(vec2 v) {
    float c = 0.;
    for (int i = 0; i < 3; i++) {
     	c = fbm(v + c * 2.) + fbm(v)* 1.7;   
    }
    return c;
}


/* Boolean operatorns */
vec2 opUnion(vec2 d1, vec2 d2) {
    float k = 8.;
    float res = exp2( -k*d1.x ) + exp2( -k*d2.x );
    float d =-log2( res )/k;
    return vec2(d, abs(d - d1.x) < abs(d - d2.x) ? d1.y : d2.y);

 	return d1.x < d2.x ? d1 : d2;   
}

vec2 opIntersect(vec2 d1, vec2 d2) {
 	return d1.x > d2.x ? d1 : d2;   
}

vec2 opComp(vec2 d1, vec2 d2) {
    return d1.x > -d2.x ? d1 : d2;
}

/* Distance functions */
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdSphere(vec3 p) {
 	return length(p) - 1.;   
}

float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float tunnel(vec3 p) {
    p = Rotate(vec3(0, p.y*1., 0)) * p;
    p.y = mod(p.y, 1.) - .5;
 	float d1 = sdBox(p, vec3(.5));
    float d2 = sdBox(p, vec3(.4, .4, 1.));
    float d3 = sdBox(p, vec3(.4, 1., .4));
    float d4 = sdBox(p, vec3(1., .4, .4));

    float d = max(d1, -d2);
    d = max(d, -d3);
    d = max(d, -d4);

    return d;
}


/* Sphere tracing */
vec2 map(vec3 p) {
    //p = Rotate(vec3(-0.5, -.5, -PI/2.)) * p;
    vec2 d = vec2(100.);

    float y = p.y;
    for (float i = 1.; i <= 5.; i += 1.) {      
        vec2 off = vec2(noise(vec2(iTime + y + float(i)*50.)), 0.);
        float dd = .5 * abs(noise(vec2(iTime + y))) + .3;
        vec3 pd = vec3(p.x + dd * sin(i + off.x), 0., p.z + dd * cos(i + off.x));

     	d = opUnion(d, vec2(sdBox(Rotate(vec3(0., y + off.x, 0.)) * pd, vec3(.1)), float(i)));  
    }
    d = opUnion(d, vec2(tunnel(p/2.)*2., 10.));
    d = opUnion(d, vec2(p.x +2., 11.));
    d = opUnion(d, vec2(-p.x + 2., 11.));

    return d;
}

vec2 strace(vec3 o, vec3 r) {
    vec2 d = vec2(0.);
    for (int i = 0; i < MAX_SAMPLES; i++) {
     	vec3 p = o + d.x * r;
        vec2 res = map(p) * 1.;
        d.x += res.x;
        d.y = res.y;
    }

    return d;
}

/* Shading helpers */
vec3 normal(vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x,
                          map(p + e.yxy).x - map(p - e.yxy).x,
                          map(p + e.yyx).x - map(p - e.yyx).x));
}

float FOG_AMOUNT = 0.2;
void applyFog( inout vec3  rgb, in float dist )
{
    //FOG_AMOUNT += pow(.8 * noise(vec2(iTime * 10.)), 10.);
    float fogAmount = 1.0 - exp( -dist*FOG_AMOUNT );
    vec3  fogColor  = vec3(.05, .05, .1);
    fogColor = vec3(0);
    rgb = mix( rgb, fogColor, fogAmount );
}


vec3 color(vec3 o, vec3 r, vec3 n, vec2 d) {
    vec3 p = o + r * d.x;

    vec3 col;
    col = vec3(1.);
    vec2 q = floor(p.yz);
   	//if (d.y == 11.) col = vec3(mod(q.x+q.y,2.));
    if (d.y < 6.) col = vec3(1., .95, 0.95);
    float ao = 1.;
    for (float i = 0.; i < 6.; i++) {
   		ao -= 0.3 * abs(length(n * i /16.) - map(p + n * i / 16.).x);
    }
    col *= ao;

    applyFog(col, d.x);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec3 o = vec3(0., iTime*0., -5.5);
    vec3 r = normalize(vec3(uv, 2.)); 



    vec2 d = strace(o, r);
    vec3 p = o + r * d.x;
    vec3 n = normal(p);  
    vec3 col = color(o, r, n, d);



    r = reflect(r, n);
    o = p + r;
    d = strace(o, r);
    n = normal(o + r * d.x);
    col = mix(col, color(o, r, n, d), .05);


    // Output to screen
    fragColor = vec4(col,1.0);
}

