const float epsilon = 0.001;  

struct Ray
{
    vec3 from; // position
    vec3 dir; // vector
};

Ray[4] createRayDiff(vec3 eye, vec3 xAxis,vec3 yAxis,vec3 p){
    vec2[4] multisampleDiff;
    float x = 0.5;
    float y =0.5;
    // offset from p
    multisampleDiff[0]=vec2(x,y)/iResolution.y;   
    multisampleDiff[1]=vec2(-y,x)/iResolution.y; 
    multisampleDiff[2]=vec2(-x,-y)/iResolution.y;
    multisampleDiff[3]=vec2(y,-x)/iResolution.y;

    Ray[4] rays;
    for(int i=0;i<4;++i){
        rays[i].from =p+( multisampleDiff[i].x*xAxis + multisampleDiff[i].y*yAxis );
        rays[i].dir =normalize(rays[i].from-eye);
    }

    return rays;
}

// from https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdVerticalCapsule( vec3 p, float h, float r,float offset )
{
    p.y -= clamp( p.y, offset, h+offset );
    return length( p ) - r;
}

float bottle(vec3 pos){
	float H =2.,R =1.,Offset=0.;
	float h=1.,r=0.25,offset =3.;

    //return sdVerticalCapsule(pos,h,r,offset);
    return min(sdVerticalCapsule(pos,H,R,Offset),sdVerticalCapsule(pos,h,r,offset));
}

// gradient is normal
vec3 getNormal(vec3 pos){
    float delta =0.01;
    vec3 temp=vec3(bottle(pos+vec3(delta,0.,0.))-bottle(pos),
                   bottle(pos+vec3(0.,delta,0.))-bottle(pos),
                   bottle(pos+vec3(0.,0.,delta))-bottle(pos)
                  );
	return normalize(temp);
}

vec3 shading(Ray ray,vec3 eye){
    vec3 shadingColor = vec3(0.0,0.0,0.0);

    // Áâ©‰ª∂ 3Ëª∏ and ‰ΩçÁßªobj_offset
    vec3 obj_offset=vec3(0.,1.,0.);
    vec3 zAxis =vec3(0.,0.,1.);
    float para_obj_rot=iTime;
    vec3 yAxis = vec3(-sin(para_obj_rot),cos(para_obj_rot),0.);
    vec3 xAxis =vec3(cos(para_obj_rot),sin(para_obj_rot),0.);


	// Â∫ßÊ®ôËÆäÊèõ
	// http://gpnnotes.blogspot.com/2018/10/worldpointvector.html

    // to local space(ËÆäÊèõÂà∞Áâ©‰ª∂ÁöÑlocalÂ∫ßÊ®ôÁ≥ª)
    vec3 temp=(ray.from-obj_offset);
    Ray localRay;
    localRay.from =vec3(dot(xAxis,temp),dot(yAxis,temp),dot(zAxis,temp));
    localRay.dir=vec3(dot(xAxis,ray.dir),dot(yAxis,ray.dir),dot(zAxis,ray.dir));

    vec3 lightDir=normalize(vec3(1.,1.,1.));
    vec3 localLightDir =vec3(dot(xAxis,lightDir),dot(yAxis,lightDir),dot(zAxis,lightDir));

    //front side of bottle
    vec3 final_color=vec3(0.2,0.2,0.2);
    for(int i=0;i<20;i++){ // ÊîπÂ∞è‰∏ÄÈªûË©¶ÁúãÁúãÊúÉÊÄéÊ®£
    	float dist = bottle(localRay.from);
        if(dist<0.01){

            //shading bottle color
            vec3 n=getNormal(localRay.from);
        	final_color= vec3(0.0,1.0,0.75)*max(0.,dot(n,localLightDir)) // ÁáàÂÖâ1
                +vec3(1.0,1.0,0.0)*max(0.,dot(n,-localLightDir)); // ÁáàÂÖâ2

            break;
        }

        localRay.from+=dist*localRay.dir;
    }

    return final_color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // weight (from -1 to 1)
    vec2 weight = fragCoord/iResolution.xy;
    weight= weight*2.0-1.0;

    vec3 lookAt =vec3(0.0,1.0,0.0);
    vec3 eye = vec3(0.0,1.0,5.0);
    //vec3 eye = lookAt+ 5.0*vec3(cos(iTime),0.0,sin(iTime))+vec3(0.0,1.0,0.0);

    // camera 3Ëª∏
    vec3 zAsix = normalize(eye-lookAt);
    vec3 yAxis = vec3(0.0,1.0,0.0);
    vec3 xAxis = cross(yAxis,zAsix);

    //view frustum ËøëÂπ≥Èù¢(z = -1) ÈÄôË£°Áî®Âè≥ÊâãÂ∫ßÊ®ô
    //ÈÄôË£°Â∞±ÊòØprojection matrixÂ∞çx,yÂÅöÁöÑ‰∫ãÔºåÂèçÈÅé‰æÜÂÅö
    float fovDegree =90.0;
    float halfFov = radians(0.5*fovDegree);
    float tanH = tan(halfFov);
    float tanW = tanH*iResolution.x/iResolution.y;

    // generate point from the plane
    vec3 pointOnPlane = eye -zAsix + xAxis*weight.x*tanW + yAxis*weight.y*tanH;

    // Antialiasing (Â§öÂ∞ÑÂπæÊ¢ùÔºåÂèñÂπ≥Âùá)
    Ray[4] rays =createRayDiff(eye,xAxis,yAxis,pointOnPlane);

    vec3 color= vec3(0.0,0.0,0.0);
    for(int i=0;i<1;++i) // Âè™Â∞Ñ1Ê¢ù„ÄÅ‰∏çÁÑ∂ÊâãÊ©üË∑ëËµ∑‰æÜË∂ÖÊÖ¢
        color += shading(rays[i],eye);
    //color *=0.25;

    fragColor = vec4(color,1.0);
}

