const vec3 color0 = vec3(0.2,0.1,0.3);
const vec3 color1 = vec3(0.6,0.1,1.9);
const vec3 lightColor = vec3(0.7,1.6,0.6);

vec3 shape(vec2 g)
{
	float c = 9.0;

    float t = iTime;
	float t1 = t * 0.1;

	vec2 p = vec2(0), sp = p;

    g *= 2.0;

    for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
    {	
        p = vec2(x,y);
        p += .5 + .5*sin( t1 * 10. + 9. * fract(sin((floor(g)+p)*mat2(2,5,5,2)))) - fract(g);
        p *= mat2(cos(t1), -sin(t1), sin(t1), cos(t1));
        float d = max(abs(p.x)*.866 - p.y*.5, p.y);
        if (d < c)
        {
            c = d;
            sp = p;
        }
    }

    return vec3(c,sp);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

	float t = iTime * 0.5;

    float k = 0.01;
	vec3 f = shape(uv);
	float fx = shape(uv + vec2(k,0.0)).x-f.x;
	float fy = shape(uv + vec2(0.0, k)).x-f.x;

    vec3 n = normalize(vec3(fx, 0.1, fy) );

	vec3 col = mix( color0, color1, f.x );

    float r = sin(t + f.y)*cos(t + f.z) * .5 + .5;
	col = mix(col.xyz, mix(col.zxy, col.yzx, r), 1.-r);

	col += .4 * pow(max(dot( n, vec3(0,1,0)), 0.), 100.) * lightColor;

	fragColor = vec4( col, 1.0 );
}

// TEST_ITR    ... testing count using for loop.
// OBJ_NUM     ... as the name suggests.
// ENABLE_PMOD ... when is defined it, use polar mod. 
//                 please comment out to compare.
// ANIMATE     ... object animate.
#define TEST_ITR 50
#define OBJ_NUM 12
//#define ENABLE_PMOD
#define ANIMATE

const float PI = acos(-1.);
const float TWO_PI = 2.*PI;

mat2 rotate(float a) { float c=cos(a), s=sin(a); return mat2(c,-s,s,c); }
// polar mod definition
vec2 pmod(vec2 p, float r)
{
    float a = atan(p.x, p.y) + PI / r;
    float n = TWO_PI / r;
    a = floor(a / n) * n;
    return rotate(-a)*p;
}

float sphere(in vec3 p, in float r) { return length(p) - r; }
#define repeat(p,c) mod(p,c)-0.5*c
float object( in vec3 p ) {
	float res = 1e4;
#ifdef ANIMATE
    p.z -= mod(iTime, 1.);
#endif
    p.z = repeat(p.z, 1.);

    for(int j=0;j<TEST_ITR;j++) {
#ifdef ENABLE_PMOD
	    p.xy = pmod(p.xy, float(OBJ_NUM));
	    res = min(res, sphere(p-vec3(0., 1., 0.), 0.2));
#else
	    for(int i=0;i<OBJ_NUM;i++) {
	        float fi = float(i);
	        float theta = fi * 2.*PI / float(OBJ_NUM);
	       	vec3 o = vec3(cos(theta), sin(theta), 0.);
	        float r = 0.2;
		    res = min(res, sphere(p-o, r));
	    }
#endif
    }
    return res;
}

float oMarch(in vec3 ro, in vec3 rd, in vec2 tmm) {
	float t = tmm.x;
    for(int i=0;i<100;i++) {
    	float d = object(ro + rd*t);
        if(d<1e-4) break;
        t += d;
    }
    if(tmm.y<t) return -1.;
    return t;
}


vec3 render(in vec3 ro, in vec3 rd, in vec2 uv) {
	vec2 cmm = vec2(0., 10.);
    float t = oMarch(ro, rd, cmm);
    vec3 col;
    if(t<0.) {
        col = vec3(0.);
    } else {
	    col = vec3(exp(-.1*t));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 p = (fragCoord.xy*2. - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 ro = vec3(0., 0., 2.);
    vec3 rd = normalize(vec3(p, -1.));

    vec3 col = render(ro, rd, uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159
#define s(a,b,c) smoothstep(a,b,c)

float Hash(in vec2 p, in float scale) {
	return fract(sin(dot(mod(p, scale), vec2(27.16898, 38.90563))) * 1.5473453);
}

float Noise(in vec2 p, in float scale ) {
	vec2 f;
	p *= scale;
	f = fract(p);
    p = floor(p);
    f = f*f*(3.0-2.0*f);
    return mix(mix(Hash(p, scale),
			Hash(p + vec2(1.0, 0.0), scale), f.x),
			mix(Hash(p + vec2(0.0, 1.0), scale),
			Hash(p + vec2(1.0, 1.0), scale), f.x), f.y);
}

float fbm(in vec2 p) {
	float f = 0.0;
	float scale = 10.;
    p = mod(p, scale);
	float amp   = .6;
	for (int i = 0; i < 5; i++)
	{
		f += Noise(p, scale) * amp;
		amp *= .5;
		scale *= 2.;
	}
	return min(f, 1.0);
}

vec3 check(vec2 uv) {
    const float s = 20.;
    return vec3(.12+0.06*mod(floor(s*uv.x)+floor(s*uv.y),2.0));
}

vec4 over( in vec4 a, in vec4 b ) {
    return mix(a, b, 1.-a.w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y*2.;

	float len = length(uv);
    float time = iTime + 33.;

    float size = .8;
    vec2 sc = fract(uv*asin(len/size)/(len*PI*2.));
    sc.x += time*.006;

    vec4 c = vec4(.0);

    float alpha = smoothstep(.0, .01, size-len);
    float value = pow(fbm(sc), 0.75);

    c = over(vec4(vec3(.00,.00,.20), s(.99, .80, value) * alpha), c);
    c = over(vec4(vec3(.02,.14,.47), s(.80, .63, value) * alpha), c);
	c = over(vec4(vec3(.82,.68,.56), s(.63, .60, value) * alpha), c); // sand
    c = over(vec4(vec3(.25,.41,.13), s(.61, .52, value) * alpha), c); // green coast
    c = over(vec4(vec3(.35,.28,.20), s(.52, .50, value) * alpha), c);
    c = over(vec4(vec3(.62,.48,.36), s(.50, .40, value) * alpha), c);
    c = over(vec4(vec3(.38,.34,.26), s(.40, .30, value) * alpha), c);
    c = over(vec4(vec3(.38,.31,.20), s(.21, .10, value) * alpha), c);
    c = over(vec4(vec3(1.0,1.0,1.0), s(.20, .00, value) * alpha), c);

    sc.x += time*.0014;
    value = fbm(sc*3.);
    c = over(vec4(vec3(0.), s(.21, .10, pow(value, 2.)) * alpha * 0.50), c);
    c = over(vec4(vec3(1.), s(.20, .00, pow(value, 3.)) * alpha * 0.75), c);
    c.rgb *= smoothstep(1., .7, len);
