//Finds the distance to a given circle
float distToCirc(vec2 point, vec2 centerCirc, float radius)
{
    float dist = length(point - centerCirc);

    dist -= radius;

    if (dist < 0.0)
    {
        dist = 0.0;
    }

   return dist;
}


//Raymarching:
float rayMarching (vec2 camPos, vec2 pixPos, vec2 obj, float radius)
{
    //Logic
    float dist = 0.0;
    float totalDist = 0.0;
    bool hit = false;

    //Massage inputs
    vec2 dirToPix = normalize(pixPos - camPos);
    float distToCam = length(pixPos - camPos);

    //Output
    float depth = 0.0;

    //Constants
    const float depthStepSize = 1.0;

    //Loop
    for (int i = 0; i < MAXITS; ++i)
    {

        //Find distance to nearest object from camera.
        dist = distToCirc(obj, camPos, radius);

        if (dist == 0.0) hit = true;

        //If dist > 0
        if (!hit)
        {
            //Move in direction of raycast.
            camPos += dirToPix * dist;
            //Add to the total distance
            totalDist += dist;

            if (dist < radius / 100.0)
            {
                depth += ((radius / radius) - (dist / radius));
            }
        }
        //Has hit
        else
        {
            if (dist > 0.0)
            {
               depth += (depthStepSize - dist);
               break;
            }

             //Move in direction of raycast.
            camPos += dirToPix * depthStepSize;
            //Add to the total distance
            totalDist += depthStepSize;
            //Add to the depth
            depth += depthStepSize;
        }


        if (totalDist >= distToCam)
        {
            break;
        }
    } //Repeat until distance covered, or collided.
    return depth;
}
