// https://en.wikipedia.org/wiki/Line%E2%80%93plane_intersection
// https://en.wikipedia.org/wiki/Specular_reflection#Law_of_reflection
// https://en.wikipedia.org/wiki/Lambertian_reflectance

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = 1.0 - uv.y;
    uv = uv * 2.0 - 1.0;
 	uv *= iResolution.x / iResolution.y;

    // plane equation
    // (p - p0) . n = 0

    // ray equation
    // p = l0 + l * t

    // solve for parameter t
    // t = ((p0 - l0) . n) / (l . n)

    vec3 n = vec3(0.0, 1.0, 0.0);
    vec3 p0 = vec3(-1.0, -1.0, -1.0);
	vec3 l0 = vec3(0.0, 0.0, 1.0);
    vec3 l = normalize(vec3(uv, -1.0));

    float yOffset = sin(iTime) * 2.0 + 2.0;
    float zOffset = cos(iTime) * 3.0 + 3.0;
    float xOffset = cos(iTime) * 4.0 + 4.0;

    l0.y += yOffset;
    l0.z += zOffset;
    l0.x += xOffset;

    float denom = dot(n, l);

    if (denom > 1e-6) {
    	float t = dot((p0 - l0), n) / denom;
        vec3 hit = l0 + l * t;

		float t0 = iTime;

        vec3 lightPos = vec3(0.0, 2.0, 5.0);
        //lightPos.y += sin(t0) * 4.0 + 4.0;
        lightPos.z += cos(t0) * 8.0 + 8.0;
        lightPos.x += sin(t0) * 8.0 + 8.0;

		vec3 lightDir = normalize(lightPos - hit);

        // ambient
        float ambientStrength = 0.1;
    	vec3 ambient = ambientStrength * vec3(1.0);

        // diffuse
        float diff = max(dot(n, lightDir), 0.0);
		vec3 diffuse = diff * vec3(1.0);

        // specular
        vec3 viewDir = normalize(hit - l0);
        vec3 reflectDir = reflect(lightDir, n); 

        float spec = pow(max(dot(viewDir, reflectDir), 0.0), 128.0);
         vec3 c = vec3(
            sin(iTime) * 0.1 + cos(iTime) * 0.25 + 1.0,
            sin(iTime) * 0.2 + cos(iTime) * 0.25 + 1.0,
            cos(iTime) * 0.2 + cos(iTime) * 0.25 + 1.0
        );

        c += -1.0 * texture(iChannel0, hit.xz / 7.0).xyz * vec3(0.2, 0.8, 0.8);

        // checker board
        float u = mod(hit.x, 2.0);
        float v = mod(hit.z, 2.0);

        if ((u > 1.0 && v > 1.0) || (u < 1.0 && v < 1.0)) {
            c -= 0.5;
        }


        fragColor = vec4(c * (ambient + diffuse + spec), 1.0);
    } else {
    	fragColor = vec4(vec3(0.0), 1.0);
    }
}

