  return length(p)-t;
}

float torus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float mb(float fl, float fr, vec3 z) {
  vec3 offset = z;
  float dr = 1.0;
  float fd = 0.0;
  for(int n = 0; n < 5; ++n) {
    box_fold(fl, z, dr);
    sphere_fold(fr, z, dr);
    z = scale * z + offset;
    dr = dr * abs(scale) + 1.0;        
    float r1 = sphere(z, 5.0);
    float r2 = torus(z, vec2(8.0, 1));        
    float r = n < 4 ? r2 : r1;        
    float dd = r / abs(dr);
    if (n < 3 || dd < fd) {
      fd = dd;
    }
  }
  return fd;
}

float df(vec3 p) { 
  float tm = p.z;
  vec3 wrap = camPath(tm);
  vec3 wrapDeriv = normalize(camPathDeriv(tm));
  p.xy -= wrap.xy;
  p -= wrapDeriv*dot(vec3(p.xy, 0), wrapDeriv)*0.5*vec3(1,1,-1);

  p -= rep*vec3(0.5, 0.0, 0.0);
  p.y *= (1.0 + 0.1*abs(p.y));
  vec3 i = mod3(p, rep);

  float fl = folding_limit + 0.3*sin(0.025*iTime+1.0)- 0.3; 
  float fr = fixed_radius2 - 3.0*cos(0.025*sqrt(0.5)*iTime-1.0);

  float d1 = mb(fl, fr, p);

  return d1; 
} 


float hash(vec2 p)  {
  float h = dot(p,vec2(127.1,311.7));   
  return fract(sin(h)*43758.5453123);
}

float intersect(vec3 ro, vec3 rd, out int iter) {
  float res;
  float r = hash(ro.xy + ro.xz + ro.yz);
  float t = 10.0*mix(0.01, 0.02, r);
  iter = max_iter;

  for(int i = 0; i < max_iter; ++i) {
    vec3 p = ro + rd * t;
    res = df(p);
    if(res < 0.001 * t || res > 20.) {
      iter = i;
      break;
    }
    t += res;
  }

  if(res > 20.) t = -1.;
  return t;
}

float ambientOcclusion(vec3 p, vec3 n) {
  float stepSize = 0.012;
  float t = stepSize;

  float oc = 0.0;

  for(int i = 0; i < 12; i++) {
    float d = df(p + n * t);
    oc += t - d;
    t += stepSize;
  }

  return clamp(oc, 0.0, 1.0);
}

vec3 normal(in vec3 pos) {
  vec3  eps = vec3(.001,0.0,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

vec3 lighting(vec3 p, vec3 rd, int iter) {
  vec3 n = normal(p);
  float fake = float(iter)/float(max_iter);
  float fakeAmb = exp(-fake*fake*9.0);
  float amb = ambientOcclusion(p, n);

  vec3 col = vec3(mix(1.0, 0.125, pow(amb, 3.0)))*vec3(fakeAmb)*bone;
  return col;
}

vec3 post(vec3 col, vec2 q) {
  col=pow(clamp(col,0.0,1.0),vec3(0.65)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.5);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )  { 
  vec2 q=fragCoord.xy/iResolution.xy; 
  vec2 uv = -1.0 + 2.0*q; 
  uv.x*=iResolution.x/iResolution.y; 

  float tm = 2.0*iTime;

  vec3 up = vec3(0.0, 1.0, 0.0) + 2.0*camPathDeriv2(tm);

  vec3 ro = camPath(tm);
  vec3 cf = normalize(camPathDeriv(tm));
  vec3 cs = normalize(cross(cf, up)); 
  vec3 cu = normalize(cross(cs,cf)); 
  vec3 rd = normalize(uv.x*cs + uv.y*cu + (3.0 - 1.0*length(uv))*cf);  // transform from view to world

  vec3 bg = mix(bone*0.5, bone, smoothstep(-1.0, 1.0, uv.y));
  vec3 col = bg;

  vec3 p=ro; 

  int iter = 0;

  float t = intersect(ro, rd, iter);

  if(t > -0.5) {
    p = ro + t * rd;
    col = lighting(p, rd, iter);
    col = mix(col, bg, 1.0-exp(-0.001*t*t)); 
  } 


  col=post(col, q);
  fragColor=vec4(col.x,col.y,col.z,1.0); 
}

const int num_spheres = 15;

const vec4 lights[2] = vec4[2] (
    vec4( 28.0, 80.0, 28.0, 8742.0 ),
    vec4( -28.0, 8.0, 8.0,  616.0 )
);

float w_intersect_sphere( float max_t, vec3 ray_dir, vec3 origin, 
    vec3 sphere, float sphere_radius2, int idx_in, 
    out vec3 pos, out vec3 norm, out int idx ) {
   // intersect with sphere 
    vec3 origToSphere = sphere - origin;
    float tCA = dot( origToSphere, ray_dir);
    if( tCA < 0.0 ) {
        // ray center is towards back of ray. cant intsesect
        return max_t;
    } else 
    {
        float dd = length(origToSphere);
        float distToMidpoint2 = dd*dd-tCA*tCA;
        if( distToMidpoint2 > sphere_radius2 ) {
            return max_t;
        } 
        else {
            float thc = sqrt(sphere_radius2-distToMidpoint2);
            float t0 = tCA - thc;           // entry 
            if( t0 < max_t ) {
                pos = origin + t0*ray_dir;
                norm = normalize( pos-sphere);
                idx = idx_in;
                return t0;
            } else {
                return max_t;
            }
        }
    }
}

// For shadows we only care if there was intersection
bool intersects_sphere( vec3 ray_dir, vec3 origin, vec3 sphere, float sphere_radius2 ) {
   // intersect with sphere 
    vec3 origToSphere = sphere - origin;
    float tCA = dot( origToSphere, ray_dir);
    if( tCA < 0.0 ) {
        // ray center is towards back of ray. cant intsesect
            return false;
    } else 
    {
        float dd = length(origToSphere);
        float distToMidpoint2 = dd*dd-tCA*tCA;
        if( distToMidpoint2 > sphere_radius2 ) {
            return false;
        } 
        else {
            return true;
        }
    }
}

float ground_plane_intersect( vec3 ray_dir, vec3 origin, float ground, out vec3 pos, out vec3 norm ) {
    if( ray_dir.y >= 0.0 ) {
        return 99999999.0;
    }
    float t = ( ground-origin.y ) /  ray_dir.y; 
    norm = vec3( 0.0, 1.0f, 0.0f );
    pos = origin + ray_dir*t;
    return t;
}

// code from demo foxes blog
// https://blog.demofox.org/2017/01/09/raytracing-reflection-refraction-fresnel-total-internal-reflection-and-beers-law/
float fresnel( float n, vec3 normal, vec3 incident )
{
        // Schlick aproximation
        float r0 = (1.0-n) / (1.0+n);
        r0 *= r0;
        float cosX = -dot(normal, incident);
        float x = 1.0-cosX;
        float ret = r0+(1.0-r0)*x*x*x*x*x;
        return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = vec2( 0.5, 0.5 );
    vec3 spheres[num_spheres];
    for( int idx=0; idx < num_spheres; idx++ ) 
    {
        float fidx = float(idx);
        float fidx2 = fidx*fidx;
        vec3 spos = vec3( sin( fidx*0.12312 + iTime * 0.121312 + 0.423 + fidx2*0.1324), 
            sin( fidx*0.13312 + iTime * .372 + 0.4223+ fidx2*1.54 ),
            sin( fidx*0.2912 + iTime * .255552 + 1.423+ fidx2*1.1324 )
        );
        spos *= 15.0f;
        spheres[ idx ] = spos;
    }
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    // [ -0.5, 05 ]    
    vec2 screen_pos_2d = 2.0*(uv - center);
    vec3 screen_pos_3d = vec3( screen_pos_2d, -29.0 );

    float _angle = iTime/3.0;
    mat3 rot_m = mat3( cos(_angle),0,  -sin( _angle ), 
                        0,          1,          0,
                        sin(_angle), 0, cos(_angle) );

    vec3 origin = rot_m*vec3( 0,0,-30.0) ;
    vec3 ray_dir = normalize(rot_m*screen_pos_3d - origin);


    float contribution = 1.0;
    vec3 final_color = vec3( 0,0,0);
    for( int bounce =3; bounce >0 ; bounce -- ) {
        vec3 new_ray_dir;
        vec3 norm;
        vec3 pos;
        vec3 diffuseCol;
        float refractive_index;
        float reflectance = 0.0;
        int final_idx = -1;
        float current_t = 99999999.0;

        current_t = ground_plane_intersect( ray_dir, origin , -20.0, pos, norm );
        if( current_t <= 99999999.0 ) {
            diffuseCol = vec3( 0.05, 0.55, 0.05 );
            if( ( ( int( pos.x/5.0) +int(pos.z/5.0) ) & 1 )== 1){
                diffuseCol = vec3( 0.59, 0.6, 0.5 );
            }   
        } else {
            diffuseCol = vec3( 1.0, 1.0, 0.0);

        }

        for( int idx=1; idx < num_spheres; idx++ ) {
            vec3 vv = vec3( 0.0, 0.0, 0.0 )*float(idx);
           current_t  =  w_intersect_sphere( current_t, ray_dir, origin, spheres[idx], 4.2*4.2, idx, 
               pos, norm, final_idx);
        }
        // workout out material properties
        if( final_idx > 0 ) {
            if( final_idx % 3 == 0 ) {
                diffuseCol = vec3( 0.02, 0.02, 0.12 );
                refractive_index = 1.77;
            } else if( final_idx % 3 == 1 ) {
                diffuseCol = vec3( 0.5, 0.1, 0.1 );
                refractive_index = 1.2;
            } else {
                diffuseCol = vec3( 0.2, 0.5, 0.1 );
                refractive_index = 1.5;
            }
            reflectance = fresnel( refractive_index, norm, ray_dir);
            new_ray_dir = reflect( ray_dir, norm );
        }


        // light the point
        vec3 point_color = vec3( 0, 0, 0 );
        for( int lt=0; lt<2; lt++ ) 
        {
            // Is the light shadowed
            vec3 point_light = lights[ lt ].xyz - pos;
            vec3 point_light_dir = normalize( point_light );
            float current_t = 99999999.0;
            bool in_shade = false;
            for( int idx=1; idx < num_spheres; idx++ ) 
            {
                if( intersects_sphere( point_light_dir, pos, spheres[idx], 4.2*4.2 ) ) 
                {
                    in_shade = true;
                    break;
                }
            }
            if( !in_shade)
            {
                float dd = length( point_light );
                float attenutation = lights[ lt ].w/( dd*dd);
                vec3 reflectedLight = reflect( -point_light_dir, norm );
                vec3 toCamera = -ray_dir;
                float diffuse = attenutation*dot( point_light_dir, norm );

                vec3 halfway = normalize( toCamera + point_light_dir );
                float specular = attenutation*pow( dot( norm, halfway ), 121.0 );

                specular = clamp( specular, 0.0, 1.0 );

                vec3 fragDiffuse = diffuseCol * diffuse;
                point_color += vec3(specular,specular,specular) + fragDiffuse;
            }
        }
        final_color += point_color * contribution * ( 1.0 - reflectance );
        contribution = contribution * reflectance;
        ray_dir = new_ray_dir;
        origin = pos;
    }
    vec3 fragFinal = pow( final_color, vec3(1.0 / 2.2) );
    fragColor = vec4(fragFinal, 1.0);
}

//http://www.tannerhelland.com/4435/convert-temperature-rgb-algorithm-code/
//Start with a temperature, in Kelvin, somewhere between 1000 and 40000.  (Other values may work,
//but I can't make any promises about the quality of the algorithm's estimates above 40000 K.)
//Note also that the temperature and color variables need to be declared as floating-point.
vec3 ColFromTemp(float temp)
{
    temp /= 100.;

    vec3 col;

    if(temp <= 66.)
        col.r = 1.;
    else
    {
        col.r = temp - 60.;
        col.r = 329.698727446 * pow(col.r, -0.1332047592);
        col.r/=255.;
        col.r = clamp(col.r,0.,1.);
    }


    if(temp <= 66.)
    {
        col.g = temp;
        col.g = 99.4708025861 * log(col.g) - 161.1195681661;
    }
    else
    {
        col.g = temp - 60.;
        col.g = 288.1221695283 * pow(col.g,-0.0755148492);
    }
    col.g/=255.;
    col.g = clamp(col.g,0.,1.);


    if(temp >= 66.)
        col.b = 1.;
    else
    {

        if(temp <= 19.)
            col.b = 0.;
        else
        {
            col.b = temp - 10.;
            col.b = 138.5177312231 * log(col.b) - 305.0447927307;
    		col.b/=255.;
    		col.b = clamp(col.b,0.,1.);

         }
     }
    return col;
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st)
{
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


float starfield(vec2 uv, float density, float spacing)
{
    float base = (noise(uv*400.)+0.9)/2.;
    return pow(base,15.*spacing)*200.*density;
}

float galaxyMask(vec2 uv)
{
    uv.x *=2.;
    uv.y *=.8;
    float base = (noise(uv*2.)+1.0)/2. + .3;
    return pow(base,6.)*4.;
}

float tempMap(vec2 uv)
{
    float base = (noise(uv*200.)+1.0)/2.;
    base = pow(base,6.)*10.;
    return base*25000.+3000.;
}

vec3 doColor(vec2 uv)
{
    vec3 col = ColFromTemp(tempMap(uv))*vec3(starfield(uv*.8, galaxyMask(uv)*1.8,1.)); //stars
    col += ColFromTemp(tempMap(uv*.2))*vec3(starfield(uv*.2, galaxyMask(uv*.2),1.3)); //galaxies
	return col;
}

#define gauss_width 3    
float gauss_fact[gauss_width]=float[](0.27901,0.44198,0.27901);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
