vec2 r(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return vec2(c * p.x - s * p.y, s * p.x + c * p.y);
}

float oneArmDistance(vec2 p) {
    float d = dot(p, vec2(0.0, 1.0));
    d = max(d, -dot(vec2(abs(p.x), p.y), normalize(vec2(-1.0,0.3))) - 0.1);
    return d;
}

vec3 palette(float v) {
    return vec3(0.6,0.3,0.8) + vec3(0.2,0.5,0.2) * cos(6.28318 * (v + vec3(0.0, 0.665, 0.667)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - iResolution.xy / 2.) / iResolution.y;

    uv.y = -uv.y;

    uv *= (2.5 + sin(iTime * 0.23) * 0.5);
    float radius = length(uv);
    uv = r(uv, pow(radius * 0.5, 2.0) * -0.2);

    float d = 1e5;
    for (int i = 0; i < 5; i++) {
        float a = float(i) * 6.283 / 5.0 + iTime * 0.2;
        d = min(d, oneArmDistance(r(uv, a)));
    }

    float innerMask = smoothstep(fwidth(d),0.0, d);

    float angle = atan(uv.y, uv.x) - 0.2 * pow(radius * 0.5,2.0);
    d -= 0.05 * (sin(angle * 11. + iTime * 1.1 + radius * 0.3) + 0.5 * cos(angle * 19. + iTime * 1.31 + radius * 0.4)) * max(radius - 0.3, 0.);

    float outerDistance = floor(d * 15.0 - iTime * 0.3);

    vec3 outerColor = clamp(palette(outerDistance * 0.2 - iTime * 0.1 + angle / 3.1416), 0.0, 1.0);

    vec3 c = mix(pow(outerColor, vec3(1.4)) * 1.2, vec3(0.0), innerMask);

    fragColor = vec4(vec3(c),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )  
{
    float PI = 3.14159265;
	//*sin(iTime+PI/2.)
    vec2 c1 = vec2(iResolution.x*0.33,iResolution.y*0.33);
    vec2 c2 = vec2(iResolution.x*0.66,iResolution.y*0.33);
    vec2 c3 = vec2(iResolution.x*0.5,iResolution.y*0.66);
    float c1D = 1. - distance(fragCoord,c1+cos(iTime*0.2)*(iResolution.xy/5.))/(iResolution.x*0.66);
    float c2D = 1. - distance(fragCoord,c2+sin(iTime*0.3+PI/4.)*(iResolution.xy/5.))/(iResolution.x*0.66);
    float c3D = 1. - distance(fragCoord,c3+sin(iTime)*(iResolution.xy/5.))/(iResolution.x*0.66);
    vec3 col = vec3(c1D,c2D,c3D);
    fragColor = vec4(col,0.0);
}

