// Inspired by FabriceNeyret2's https://www.shadertoy.com/view/tdcXW2
// remade in "real" 3D with raymarching

#define FDIST 0.5
#define MAXSTEP 100
#define TOL 0.001
#define MAXDIST 20.
#define INTERP_SCALE -3.
#define REPEAT_SCALE 1.5
#define MOUSE_SCALE 0.014
#define TEXSCALE vec2(1.5, 1.)
#define TEX_BIAS 0.49803921568

//comment this out if you have weird artifacts where segments don't line up
#define SHUFFLE

float rand(vec2 uv) {
    return fract(814.*sin(uv.x*15829.+uv.y*874.));
}

float noise1D(float t) {
    return fract(14950.5*sin(1905.1*t));
}

vec2 id2coord(in float id) {
    #ifdef SHUFFLE
    id = floor(noise1D(id) * 256.);
    #endif
    return vec2(mod(id, 16.), floor(id/16.))/16.;
}

void transform(inout vec3 ro) {
    vec2 id = floor((ro.xy+REPEAT_SCALE*0.5)/REPEAT_SCALE);
    float offset = rand(id)*256.;
    ro = vec3(mod(ro.xy+REPEAT_SCALE*0.5, REPEAT_SCALE)-REPEAT_SCALE*0.5, ro.z*INTERP_SCALE + offset);
}

/**
Compute analytic gradient
*/
vec3 grad(in vec3 ro) {
    transform(ro);
    float t = ro.z;
    float id = floor(t);
    vec2 offset = clamp(ro.xy+0.5, 0., 1.)/16.;
    vec2 coords1 = id2coord(id) + offset;
    vec2 coords2 = id2coord(id+1.) + offset;

    vec4 tex1 = textureGrad(iChannel0,coords1,dFdx(offset),dFdy(offset));
    vec4 tex2 = textureGrad(iChannel0,coords2,dFdx(offset),dFdy(offset));

    float dist1 = tex1.w-TEX_BIAS;
    float dist2 = tex2.w-TEX_BIAS;

    vec2 norm1 = (tex1.yz-TEX_BIAS)*2.;
    vec2 norm2 = (tex2.yz-TEX_BIAS)*2.;

    float tf = fract(t);
    float alpha = smoothstep(0., 1., tf);
    vec3 n = vec3(norm1 * (1.-alpha) + norm2 * alpha, (dist2 - dist1) * INTERP_SCALE * 6. * (tf - tf*tf));
    n.y = -n.y;
    return normalize(n);
}

float map(in vec3 ro) {
    transform(ro);
    float t = ro.z;
    float id = floor(t);
    vec2 offset = clamp(ro.xy+0.5, 0., 1.)/16.;
    vec2 coords1 = id2coord(id) + offset;
    vec2 coords2 = id2coord(id+1.) + offset;

    float dist1 = textureGrad(iChannel0, coords1,dFdx(offset), dFdy(offset)).w-TEX_BIAS;
    float dist2 = textureGrad(iChannel0, coords2,dFdx(offset), dFdy(offset)).w-TEX_BIAS;
    vec2 displacement = abs(ro.xy)-0.5;
    float alpha = smoothstep(0., 1., fract(t));
    return max(max(displacement.x, displacement.y), 0.5*(dist1 * (1.-alpha) + dist2*alpha));
}

vec2 raymarch(in vec3 eye, in vec3 rd) {
    float t = 0.0;
    int i;
    for (i=0; i<MAXSTEP; i++) {
        vec3 pos = eye + rd*t;
        float dist = map(pos);
        t += dist;
        if (abs(dist) < TOL || t > MAXDIST) break;
    }
    return vec2(t, i);
}

bool shadowmarch(in vec3 eye, in vec3 rd, float lightdist) {
    float t = 0.0;
    float minstep = 0.01;
    for (int i=0; i<50; i++) {
        vec3 pos = eye + rd * t;
        float dist = map(pos);
        if (dist < 0.) return true;
        else if (t > lightdist) return false;
        float s = max(dist, minstep);
        minstep += 0.001;
        t += s;

    }
    return false;
}

vec3 albedo(in vec3 n, in vec3 ro) {
    vec2 texcoord1 = ro.yz * TEXSCALE;
    vec2 texcoord2 = ro.xz * TEXSCALE;
    vec3 tex1 = texture(iChannel1, texcoord1.yx).xyz;
    vec3 tex2 = texture(iChannel1, texcoord2.yx).xyz;
    return (abs(n.x) * tex1 + abs(n.y) * tex2 - 0.5)*0.7 + 0.5;
}

float shade(in vec3 n, in vec3 ro, in vec3 lightpos, in bool shadow) {
    vec3 l = lightpos - ro;
    float ldist = length(l);
    if (shadow && shadowmarch(ro + n*TOL*2., l/ldist, ldist)) {
    	return 0.;
    }
    return max(0.,dot(l, n))/ldist*min(1., 2.5/(ldist*ldist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.xx;

    vec3 up = vec3(0, 0, 1);
    vec3 eye = vec3(REPEAT_SCALE*0.6*cos(iTime*0.5 + iMouse.x*MOUSE_SCALE), REPEAT_SCALE*0.6*sin(iTime*0.5 + iMouse.x*MOUSE_SCALE), iTime*2.);
    vec3 lightpos = vec3(REPEAT_SCALE*0.2, REPEAT_SCALE*0.5, iTime*2.+1.5);
    vec3 lightpos2 = vec3(-REPEAT_SCALE*0.2, -REPEAT_SCALE*0.5, iTime*2.);
    vec3 w = normalize(vec3(-eye.xy, iMouse.w > 1. ? (iMouse.y-iResolution.y*.5) * MOUSE_SCALE * REPEAT_SCALE * 0.66 : 2.*cos(iTime*0.13)));
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);
    vec3 rd = normalize(w*FDIST + u*uv.x + v*uv.y);
	vec2 t = raymarch(eye, rd);
    vec3 ro = t.x * rd + eye;
    vec3 n = grad(ro);
    float fac1 = shade(n, ro, lightpos, true);
    float fac2 = shade(n, ro, lightpos2, false);
    vec3 a = albedo(n, ro);
    float fogfac = min(0.1,t.x*0.005);
    vec3 finalcol = a*fac1*vec3(0.7, 0.8, 1.) + a*fac2*vec3(0.5, 0.25, 0.25) + vec3(0., 0., 0.05)*n.z/t.x + fogfac*mix(vec3(2, 0.4, 0.0), vec3(2, 3, 5), rd.z*.5+.5);
    fragColor = vec4(pow(finalcol, vec3(0.6)),1.);
}

// size of the windows
#define STRIDE 0.25

// depth of the holes
#define DEPTH 0.125

// raymarching
#define FDIST 0.5
#define STEPS 50
#define SHADOWSTEPS 25
#define MIN_SHADOW_STEP 0.075
#define TOL 0.005
#define SHARPNESS 6.5

// distance function for hull geometry
float map(in vec3 ro) {
    float dist1 = length(ro-vec3(0., 1., 0.))-1.;
    float dist2 = length(ro-vec3(0., -1., 0.))-1.;
    float ang = iTime * 0.4564;
    float dist3 = length(ro-vec3(1.5*cos(ang), 1., 1.5*sin(ang)))-0.4;
    float dist4 = length(ro-vec3(-1.5*cos(ang), 1., -1.5*sin(ang)))-0.4;
    float dist5 = length(ro-vec3(0, -1., 0.6))-0.5;

    float final_dist = -log(exp(-dist1*SHARPNESS) + exp(-dist2*SHARPNESS) + exp(-dist3*SHARPNESS) + exp(-dist4*SHARPNESS))/SHARPNESS;
    final_dist = log(exp(final_dist*SHARPNESS) + exp(-dist5*SHARPNESS))/SHARPNESS;
    return final_dist;
}

// normals of hull geometry
vec3 grad(in vec3 ro) {
    vec2 diff = vec2(TOL, 0.);
    float dx = map(ro + diff.xyy) - map(ro - diff.xyy);
    float dy = map(ro + diff.yxy) - map(ro - diff.yxy);
    float dz = map(ro + diff.yyx) - map(ro - diff.yyx);
    return normalize(vec3(dx, dy, dz));
}

float raymarch(in vec3 ro, in vec3 rd) {
    float t=0.;
    for (int i=0; i<STEPS; i++) {
        float dist = map(ro + rd*t);
        t += dist;
        if (abs(dist) < TOL) {
            return t;
        }
    }
    return -1.;
}

float shadow_march(in vec3 ro, in vec3 rd) {
    float t=0.;
    for (int i=0; i<SHADOWSTEPS; i++) {
        float dist = map(ro + rd*t);
        t += max(dist, MIN_SHADOW_STEP);
        if (dist < 0.) return 0.;
    }
    return 1.;
}


// Raytrace box, returns (t_in, t_out)
vec2 box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 no) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pout =  k - n;
    vec3 pin =  - k - n;
    float tout = min(pout.x, min(pout.y, pout.z));
    float tin = max(pin.x, max(pin.y, pin.z));
    if (tin > tout) {
        return vec2(-1.);
    }
    no = -sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    return vec2(tin, tout);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
	float ang = iTime * 0.3;
    vec3 eye = 2.5*vec3(cos(ang), sin(ang), .5);
    vec3 w = -normalize(eye);
    vec3 u = normalize(cross(w, vec3(0., 0., 1.)));
    vec3 v = cross(u, w);
    vec3 rd = normalize(u*uv.x + v*uv.y + FDIST * w);

    float t = raymarch(eye, rd);
    vec3 ro = eye + t * rd;
	vec3 n = grad(ro);
    vec3 col;

    if (t > 0.) {

        //get offsets and cell IDs and their centroids/normals
        vec3 offset = vec3(iMouse.x/iResolution.x, iTime*0.3, iMouse.y/iResolution.y);
        ro += offset;
        vec3 mask = 1.-step(0.9,abs(fract(ro/STRIDE)-0.5)*2.);
        vec3 modro = fract(ro/STRIDE);
        vec3 id = floor(ro/STRIDE);
        ro -= offset;
        // cell centroid, normal at centroid, and offset of centroid from surface
        vec3 cc = (id+0.5)*STRIDE - offset;
        vec3 nn = grad(cc);
        float OFFSET = map(cc);

        // transform to tangent space for raytracing cavities
        // offset depth by centroid's distance from the surface
        vec3 uu = normalize(cross(vec3(0., 0., 1.), nn));
        vec3 vv = cross(nn, uu);
        mat3 rot = mat3(uu, vv, nn);
        mat3 rott = transpose(rot);
        vec3 rd_inner = rott * rd;
        vec3 ro_inner = rott * (ro-cc) + vec3(0., 0., OFFSET);
        vec3 n_inner;
        vec3 dims = vec3(vec2(0.3*STRIDE), DEPTH);
        vec2 t_inner = box(ro_inner, rd_inner, dims, n_inner);

        float shadow_fac = 1.;
        vec3 sundir = normalize(vec3(1., 2., 1.5));
        if (t_inner.y > 0. && t_inner.x < 0. && mask.x*mask.y*mask.z > 0.5) {
            // determine inner shadows
            vec3 sundir_inner = rott * sundir;
            vec3 n_shadow;
            vec3 ro_shadow = ro_inner + t_inner.y * rd_inner;
			vec2 t_shadow = box(ro_shadow, sundir_inner, dims, n_shadow);
            // where the shadow ray hits the cavity in world space; if it's outside the hull, no shadow
            ro = cc + rot * (ro_shadow - vec3(0., 0., OFFSET) + t_shadow.y * sundir_inner);
            shadow_fac = step(0., map(ro));
            n = rot * n_inner;
        }
        // compute global shadows
        if (shadow_fac > 0.5)
        	shadow_fac *= shadow_march(ro + n * TOL, sundir);

        // shade
        shadow_fac *= max(0., dot(n, sundir));
        float ambient = abs(dot(n, -sundir));
        col = shadow_fac * vec3(1., 1., 0.95) + ambient * vec3(0.05, 0.05, 0.04);
    } else {
        col = vec3(0.);
    }
    fragColor = vec4(pow(col, vec3(0.45)),1.0);
}

