// see https://shadertoy.com/view/Wtfczs
float violetnoise3(vec2 p)
{
    return .5*(hash21(p+vec2(1,0)) + hash21(p+vec2(0,1)) - hash21(p));
}

float violetnoise4(vec2 p)
{
    p *= 2.; // looks better with?
    return (hash21(p+vec2(1,0)) + hash21(p+vec2(0,1))
          - hash21(p-vec2(1,0)) + hash21(p-vec2(0,1))) * .5;
}

float bluenoise5(vec2 p)
{
    return hash21(p)
        - .25 * (
			hash21(p + vec2( 1,0))
		+	hash21(p + vec2(-1,0))
		+	hash21(p + vec2(0, 1))
		+	hash21(p + vec2(0,-1))
        );
}

