// http://wikipedia.org/wiki/Halton_sequence
// Van der Corput sequence translator
// from https://shadertoy.com/view/tdffWS
float H1(int i, int m)
{
	float h = 0., r = 1. / float(m), recip = 1.;
	int digit;
	while (i != 0) {
        int k = i / m;
        digit = i - k * m; //i % m; //
		i = k;
		h += float(digit) * (recip *= r);
	}
	return h;
}

float Dither3(vec2 p)
{
    //ivec2 q = ivec2(p);
     //+ osctri(iTime * 1e-3)
    int i = int(p.x + 3.*p.y); //q.x + 5*q.y;
    return H1(i, 5) * 2. - 1.;
}

uint ReverseBits32(uint n) // reverses the bit pattern, i.e. 1101 becomes 1011
{
	n = ((n >>  1u) & 0x55555555u) | ((n & 0x55555555u) <<  1u);
	n = ((n >>  2u) & 0x33333333u) | ((n & 0x33333333u) <<  2u);
	n = ((n >>  4u) & 0x0f0f0f0fu) | ((n & 0x0f0f0f0fu) <<  4u);
	n = ((n >>  8u) & 0x00ff00ffu) | ((n & 0x00ff00ffu) <<  8u);
	n = ((n >> 16u) & 0x0000ffffu) | ((n & 0x0000ffffu) << 16u);
	return n;
}

float Dither4(vec2 p)
{
    uint x = uint(p.x + iResolution.x * p.y);
    uint y = ReverseBits32(x);
    x ^= (y ^ (y >> 16)); // ^ (y << 16));
    return float(x & 0xffffu) / exp2(16.) * 2.-1.; //exp2(23.) / float(x >> 9); //
}
// an extremely simple bit-munging PRNG
uint XorSmash(uint x)
{
//    x = ReverseBits32(x);
    for (int i = 11; i-- > 0; x = ((x >> 6) ^ 5u) ^ (x << 3) ^ (x << 1));
    return x;
}

float Dither5(vec2 p)
{
    uint x = uint(p.x + iResolution.x * p.y);
    x = XorSmash(x);
    return float(x & 0xffffu) / exp2(16.) * 2.-1.; //exp2(23.) / float(x >> 9); //
}
