void mainImage(out vec4 c, vec2 p)
{
    const float zoom = 1.; //2.; //4.; //8.; //16.; // zoom 
    // use amp of zero if you want to watch the bands crawl by
    float amp = .6/256.; //.5; //.0; //.5/127.; //.25; //1.; // should be nigh imperceptible at around .7/256.
    vec2 uv = p / iResolution.xy;
    // show pattern in PiP window at bottom left if divider doesn't hide it
    if (all(lessThan(uv, vec2(1./6.)))) amp = .5;
    p /= zoom;
    p = floor(p) + .5; // pixellate - be careful not to break the 1:1 case!  round() does not work!
    // color gradient designed to exhibit banding, which the dithering is expected to be able to counter.
    c.rgb = .5 + .5 * cos(.1*iTime + .125 * uv.xyx + vec3(0,2,4));
    float divider = (dot(iMouse,iMouse) < 2.5) ? .085 : iMouse.x / iResolution.x;
    c += exp2(-1024.*abs(uv.x-divider));
    c.rgb = pow(c.rgb, vec3(1./2.2)); // gamma correct to srgb gamut
	// dithering works best in output gamma, as the quanta are equally spaced.
    if (uv.x >= divider) {
	    float g = ChooseDither(p);
	    g *= amp; // dither scale
	    c.rgb += g;
    }
    c.a = 1.;
}
