#define FDIST 0.7
#define PI 3.1415926


#define GROUNDSPACING 0.5
#define GROUNDGRID 0.05
#define BOXDIMS vec3(1., 1., 1.)

#define ABSORPTION_RATE vec3(0.7, 0.5, 0.5)
#define IOR 1.33
#define SCATTER_FACTOR 0.02
#define SAMPLES 25
#define REFLECTIONS 4
#define RAYMARCH_STEPS 15
#define RAYMARCH_TOL 0.005

#define TIME_T 2.
#define TIME_H 2.
#define TIME_L 10.


vec2 rand2d(in vec2 uv) {
    return fract(mat2(-199.258, 457.1819, -1111.1895, 2244.185)*sin(mat2(111.415, -184, -2051, 505)*uv));
}

float box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 nn, bool entering) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pin = - k - n;
    vec3 pout =  k - n;
    float tin = max(pin.x, max(pin.y, pin.z));
    float tout = min(pout.x, min(pout.y, pout.z));
    if (tin > tout) return -1.;
    if (entering) {
    	nn = -sign(rd) * step(pin.zxy, pin.xyz) * step(pin.yzx, pin.xyz);
    } else {
        nn = sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    }
    return entering ? tin : tout;
}

float boxsdf(in vec3 ro, in vec3 r) {
    vec3 b = abs(ro) - r;
    float d = max(b.x, max(b.y, b.z));
    d += 0.005 * (1.+sin(50.*length(ro) - 2.*iTime));
    return d;
}

vec3 boxgrad(in vec3 ro, in vec3 r) {
    vec2 diff = vec2(RAYMARCH_TOL, 0.);
    float dx = boxsdf(ro + diff.xyy, r) - boxsdf(ro - diff.xyy, r);
    float dy = boxsdf(ro + diff.yxy, r) - boxsdf(ro - diff.yxy, r);
    float dz = boxsdf(ro + diff.yyx, r) - boxsdf(ro - diff.yyx, r);
    return normalize(vec3(dx, dy, dz));
}

float hybridbox(in vec3 ro, in vec3 rd, in vec3 r, out vec3 n, in bool entering) {
    float t = box(ro, rd, r, n, entering);
    if (t > 0.) {
        for (int i=0; i<RAYMARCH_STEPS; i++) {
            float dist = boxsdf(ro + t*rd, r);
            t += (entering ? dist : -dist);
            if (dist < RAYMARCH_TOL) {
                n = boxgrad(ro + t*rd, r);
                return t;
            }
        }
    }
    return -1.;
}

vec2 sphere(in vec3 ro, in vec3 rd, in float r, out vec3 ni) {
	float pd = dot(ro, rd);
    float disc = pd*pd + r*r - dot(ro, ro);
    if (disc < 0.) return vec2(-1.);
    float tdiff = sqrt(disc);
    float tin = -pd - tdiff;
    float tout = -pd + tdiff;
    ni = normalize(ro + tin * rd);

    return vec2(tin, tout);
}

vec3 bgcol(in vec3 rd) {
    return mix(vec3(0., 0., 1.), vec3(0.6, 0.8, 1.), 1.-pow(abs(rd.z), 2.));
}

//raytrace the exterior surroundings
vec3 background(in vec3 ro, in vec3 rd) {
    float t = (-1. - ro.z)/rd.z;
    if (t < 0.) return bgcol(rd);
    vec2 uv = ro.xy+t*rd.xy;
    if (max(abs(uv.x), abs(uv.y)) > 8.) return bgcol(rd);
    vec2 checkers = smoothstep(vec2(GROUNDGRID*0.75), vec2(GROUNDGRID), abs(mod(uv, vec2(GROUNDSPACING))*2.-GROUNDSPACING));
    float aofac = smoothstep(-0.5, 1., length(abs(uv)-min(abs(uv), vec2(0.75))));
    return mix(vec3(0.2), vec3(1.), min(checkers.x,checkers.y)) * aofac;
}

//raytrace the insides
vec3 insides(in vec3 ro, in vec3 rd, in float INNERRAD, in mat2 rot, out float tout) {
    vec3 ni;
    vec2 t = sphere(ro, rd, INNERRAD, ni);
    vec3 ro2 = ro + t.x * rd;
    // shading/texture
    vec2 checkers = step(mod(rot * ro2.xy, vec2(0.25)), vec2(0.01));
    vec3 tex = mix(vec3(1.), vec3(0., 0.7, 0.), abs(checkers.x-checkers.y));
    float fac = -ni.z;

    //inner background
    vec3 n;
    float tb = box(ro, rd, vec3(INNERRAD), n, false);
    vec3 rob = ro + tb * rd;
    vec3 checkersb = abs(mod(rob.xyz, vec3(0.5))-0.25)*4.;
    vec3 texb = mix(vec3(0., 0., 1.), vec3(0.), step(0.25, abs(abs(checkersb.x-checkersb.y)-checkersb.z)));
    tout = mix(tb, t.x, step(0., t.x));
    return mix(mix(vec3(0.5), texb, step(0., tb)) * 0.5, tex * fac, step(0., t.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t_osc = 0.5*(TIME_H+TIME_L)+TIME_T;
    float h_l = 0.5*TIME_L/t_osc;
    float h_h = (0.5*TIME_L+TIME_T)/t_osc;
    float osc = smoothstep(0., 1., (clamp(abs(mod(iTime, t_osc*2.)/t_osc-1.), h_l, h_h) - h_l) / (h_h - h_l));
    float INNERRAD = mix(0.5, 1.5, osc);
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    float mouseY = iMouse.y < 1. ? 0.5 : (1.0-1.15*iMouse.y/iResolution.y) * 0.5 * PI;
    float mouseX = iMouse.x < 1. ? iTime*0.25 : -(iMouse.x/iResolution.x) * 2. * PI;
    vec3 eye = 4.*vec3(cos(mouseX) * cos(mouseY), sin(mouseX) * cos(mouseY), sin(mouseY));
    vec3 w = normalize(-eye);
    vec3 up = vec3(0., 0., 1.);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);

    vec3 rd = normalize(w * FDIST + uv.x * u + uv.y * v);

    vec3 ni;
    float t = hybridbox(eye, rd, BOXDIMS, ni, true);
    vec3 ro = eye + t * rd;
    vec2 coords = ro.xy * ni.z + ro.yz * ni.x + ro.zx * ni.y;

    if (t > 0.) {
        float ang = -iTime * 0.33;
    	float c = cos(ang);
    	float s = sin(ang);
    	mat2 rot = mat2(c, -s, s, c);
        vec3 col = vec3(0.);
        float R0 = (IOR-1.)/(IOR+1.);
        R0*=R0;
        for (int i=0; i<SAMPLES; i++) {

            vec2 theta = rand2d(coords + float(i) * vec2(1., 0.) * vec2(104., -30.6));
            theta *= vec2(2.*PI, SCATTER_FACTOR*PI);
            vec3 n = vec3(cos(theta.x)*sin(theta.y), sin(theta.x)*sin(theta.y), cos(theta.y));
            // reflection
            vec3 nr = n.zxy * ni.x + n.yzx * ni.y + n.xyz * ni.z;
            vec3 rdr = reflect(rd, nr);
            vec3 reflcol = background(ro, rdr);

            // refraction & insides

            //vec3 rd2 = rd.yzx * ni.x + rd.zxy * ni.y + rd.xyz * ni.z;

            vec3 rd2 = refract(rd, nr, 1./IOR);

            vec3 insidecol = vec3(0.);
            float accum = 1.;
            vec3 transmission = vec3(1.);
            vec3 no2 = ni;
            vec3 ro_refr = ro;

            for (int j=0; j<REFLECTIONS; j++) {
                float tb;
                //no2 = -no2;
                vec2 coords2 = ro_refr.xy * no2.z + ro_refr.yz * no2.x + ro_refr.zx * no2.y;
                vec3 eye2 = vec3(coords2, -max(INNERRAD, 1.));
                vec3 rd2trans = rd2.yzx * no2.x + rd2.zxy * no2.y + rd2.xyz * no2.z;
                rd2trans.z = -rd2trans.z;
                vec3 internalcol = insides(eye2, rd2trans, INNERRAD, rot, tb);
                if (tb > 0.) {
                    //terminate at interior geometry
                    insidecol += accum * internalcol * transmission * pow(ABSORPTION_RATE, vec3(tb));
					break;
                } else {
                    //compute contribution of the light leaked from the environment through this bounce
                    float tout = hybridbox(ro_refr, rd2, BOXDIMS, no2, false);
                    no2 = n.zyx * no2.x + n.xzy * no2.y + n.yxz * no2.z;
                    vec3 rout = ro_refr + tout * rd2;
                    vec3 rdout = refract(rd2, -no2, IOR);
                    float fresnel2 = R0 + (1.-R0) * pow(1.-dot(rdout, no2), 5.);
                    rd2 = reflect(rd2, -no2);

                    ro_refr = rout;
                    ro_refr.z = max(ro_refr.z, -0.999);

                    transmission *= pow(ABSORPTION_RATE, vec3(tout));
                    insidecol += accum * (1.-fresnel2) * background(ro_refr, rdout) * transmission;
                    if (fresnel2 < 0.1) break;
                    accum *= fresnel2;
                }
            }

            // background

            float fresnel = R0 + (1.-R0) * pow(1.-dot(-rd, nr), 5.);
            col += mix(insidecol, reflcol, fresnel);
        }
        col /= float(SAMPLES);

        fragColor = vec4(col, 1.);
    } else {
		fragColor = vec4(background(eye, rd), 1.);
    }
}

/**
 * SDF's from:
 * http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
 **/

float sd_sphere(vec3 p, float r) {
	return length(p) - r;
}
float sd_box(vec3 p, vec3 s) {
	vec3 d = abs(p) - s;
	return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}
float sd_capsule(vec3 p, vec3 a, vec3 b, float r)
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

mat2 op_rotate(float a) {
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c);
}


// http://iquilezles.org/www/articles/smin/smin.htm
float smin(float a, float b, float k)
{
    float h = max(k - abs(a - b), 0.0);
    return min(a, b) - h * h * 0.25 / k;
}

// http://iquilezles.org/www/articles/smin/smin.htm
float smax(float a, float b, float k)
{
    float h = max(k - abs(a - b),0.0);
    return max(a, b) + h * h * 0.25 / k;
}


vec2 map(vec3 pos)
{

    float s = 0.75;

    // create floor
    float d = pos.y;
    float m = 2.0;

    pos.xz *= op_rotate(iMouse.x / iResolution.x * 8.0);


    pos.y += 0.54;

	float prevd = d;

    { // Base
    vec3 p = pos - vec3(0, s, 0);
    d = min(d, sd_box(p, s * vec3(1.75, 0.20, 0.5)) - s * 0.1);
	d = min(d, sd_box(p, s * vec3(2.0, 0.20, 0.5)) - s * 0.05);

    p.x -= s * 2.15;
    d = smax(d, -sd_sphere(p, s * 0.12), 0.01);
    }
    if (d < prevd)
        m = 3.0;
    prevd = d;

    { // Cap
    vec3 p = pos - s * vec3(-1.25, 1, 0);
	float q = sd_box(p, s * vec3(1.75, 0.20, 0.5)) - s * 0.1;

    p = pos - s * vec3(0.1, 1, 0);
    q = max(q, -sd_box(p, s * vec3(2, 1, 1)));
    d = min(d, q);
    }

    { // Clip
        vec3 p = pos - s * vec3(-1.3, 1.5, 0);
	    d = min(d, sd_box(p, s * vec3(0.9, 0.06, 0.25)) - s * 0.02);
        p -= s * vec3(-1.3, -0.08, 0.0);
        p.xy *= op_rotate(-0.2);
	    d = min(d, sd_box(p, s * vec3(0.4,0.06, 0.25)) - s * 0.02);

    }
    if (d < prevd)
        m = 4.0;
    prevd = d;


    return vec2(d, m);
}


vec3 calc_normal(vec3 p)
{
    vec2 e = vec2(0.00005, 0.0);
    return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x,
                          map(p + e.yxy).x - map(p - e.yxy).x,
                          map(p + e.yyx).x - map(p - e.yyx).x));
}
vec2 cast_ray(vec3 ro, vec3 rd)
{
    float m = -1.0;
    float total = 0.0;
    for (int i = 0; i < 200; ++i) {
        vec3 pos = ro + total * rd;

        vec2 dist = map(pos);
	    m = dist.y;

        if (dist.x < 0.001) break; // to close
        total += dist.x;
        if (total > 20.0) { // to far
            total *= -1.0;
            break;
        }
    }
    return vec2(total, m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    fragCoord *= 2.0;
	vec2 p = (fragCoord - iResolution.xy ) / iResolution.y;

    vec3 ro = vec3(0.0, 2.0, -4.5); // ray origin
    vec3 rd = normalize(vec3(p + vec2(0, -0.75), 2.0)); // ray direction

    vec3 col = vec3(0.6, 0.7, 0.8) - 0.4 * rd.y; // sky
    col = mix(col, vec3(0.75, 0.8, 0.85), exp(-13.0 * rd.y)); // horizon

    vec2 res = cast_ray(ro, rd);
    float total = res.x;

    if (total > 0.0) {
        vec3 pos = ro + total * rd;
        vec3 norm = calc_normal(pos);

        vec3 mat = vec3(0.18); 
        if (res.y > 3.5) {
            mat = vec3(0.007, 0.007, 0.007);
        } else if (res.y > 2.5) {
            mat = 0.3 * vec3(0.8, 1.0, 0.00);
        } else if (res.y > 1.5) {
            mat = texture(iChannel0, pos.xz * 0.1).xyz * 0.2;
            mat.r *= 1.1;
        }

        // dot(a, b) -> how aligned are a and b
        vec3 sun = normalize(vec3(sin(iTime * 0.1), 0.4, cos(iTime * 0.1)));
        float sun_dif = clamp(dot(norm, sun), 0.0, 1.0);
        // step(a, b) -> if a < b then 0 else 1
        float sun_sha = step(cast_ray(pos + norm * 0.001, sun).x, 0.0);
        col = mat * vec3(7.0, 4.5, 3.0) * sun_dif * sun_sha;


        // Blueish sky light from above
        float sky_dif = clamp(0.5 + 0.5 * dot(norm, vec3(0, 1, 0)), 0.0, 1.0);
        col += mat * vec3(0.5, 0.8, 0.9) * sky_dif;

        // Remove blacks, by bouncing light of the ground
        float bou_dif = clamp(0.5 + 0.5 * dot(norm, vec3(0, -1, 0)), 0.0, 1.0);
        col += mat * vec3(0.7, 0.3, 0.2) * bou_dif;
    }
    col = pow(col, vec3(0.4545)); // Gamma correction

    fragColor = vec4(col, 1.0);
}

#define MAXITS 256
#define CIRCLE_RAD_MOD 50.0
#define NEAR_THRESH 15.0
#define COLLIDE_THRESH 0.001
#define DEPTH_STEP_SIZE 1.0
#define SHADOW_FALL 1.0
//Finds the distance to a given circle
float distToCirc(vec2 point, vec2 centerCirc, float radius)
{
    float dist = length(point - centerCirc);

    dist -= radius;

    if (dist < 0.0)
    {
        dist = 0.0;
    }

   return dist;
}

//Finds the distance to a given circle
vec2 closestToCirc(vec2 point, vec2 centerCirc, float radius)
{
    return normalize(centerCirc - point) * distToCirc(point, centerCirc, radius);
}

float SampleDepth(vec2 normedDir, vec2 start, float maxDepth, vec2 obj, float radius)
{
    //Logic
    float dist;

    //Output
    float depth = 0.0;

    //Has hit
    for (int i = 0; i < MAXITS; ++i)
    {
        //Find distance to nearest object from camera.
        dist = distToCirc(obj, start, radius);

        if (dist > COLLIDE_THRESH)
        {
            depth = (radius * 2.0);
            return depth;
        }
        if (depth > maxDepth)
        {
          return maxDepth;
        }

        //Move in direction of raycast.
        start += normedDir * DEPTH_STEP_SIZE;

        //Add to the depth
        depth += DEPTH_STEP_SIZE;
    } //Repeat until distance covered, or collided.
    return depth;
}

//Raymarching:
float rayMarching (vec2 CamPos, vec2 pixPos, vec2 obj, float radius)
{
    //Logic
    float dist = 0.0;
    float totalDist = 0.0;
    bool hit = false;
    bool near = false;
    vec2 Pos = CamPos;

    //Massage inputs
    vec2 dirToPix = normalize(pixPos - Pos);
    float distToCam = length(pixPos - Pos);

    //Output
    float depth = 0.0;

    //Constants
    const float depthStepSize = 1.0;

    //Stores the previous distance.
    float prevDist = 1000.0f;

    vec2 closestPoint;
    float distToClosest;

    //Loop
    for (int i = 0; i < MAXITS; ++i)
    {

        //Find distance to nearest object from camera.
        dist = distToCirc(obj, Pos, radius);

        //If the object is hit, sample its depth
        if (dist <= COLLIDE_THRESH) 
        {
            return SampleDepth(dirToPix,
                               Pos,
                               length(Pos - pixPos),
                               obj, 
                               radius);
        }

        //Otherwise,
         //Move in direction of raycast.
         Pos += dirToPix * dist;
         //Add to the total distance
         totalDist += dist;

          //Find closest point for soft-shadows if close enough
        if (dist < NEAR_THRESH)
          {
            near = true;
          }

        if (totalDist >= distToCam)
        {

            if (near && distToCam > (length(obj - CamPos)))
            {
                float dotted = dot(normalize((obj - CamPos)), dirToPix);
                //depth = (radius * 2.0) * pow(dotted, SHADOW_FALL);

            }
            break;
        }

        prevDist = dist;
    } //Repeat until distance covered, or collided.

    return depth;
}

