vec3 XYZ_to_xyY(vec3 XYZ) {
 	float inv_sum = 1.0/dot(XYZ,ONE);
    return XYZ.xyy * vec3(inv_sum,inv_sum,1.0);
}

vec3 xyY_to_XYZ(vec3 xyY) {
 	float s = xyY.z/xyY.y;
    float z = 1.0-dot(vec2(1.0,1.0), xyY.xy);
    return vec3(xyY.x, xyY.z, z) * vec3(s,1.0,s);
}

vec3 LUV_to_XYZ(vec3 LUV) {
    vec2 uv = LUV.yz / (13.0*LUV.x) + D65_Yuv.yz;
    float Y = LUV.x > 8.0 ? 
        D65_Yuv.x * pow((LUV.x+16.0)/116.0, 3.0) : 
    	LUV.x * D65_Yuv.x * 0.00110705645;
    float X = Y * 9.0 * uv.x / (4.0*uv.y);
    float Z = Y * (12.0-3.0*uv.x-20.0*uv.y)/(4.0*uv.y);
    return vec3(X,Y,Z);
}
vec3 LUV_to_RGB(vec3 LUV) {
 	return XYZ_to_RGB * LUV_to_XYZ(LUV);   
}
vec3 HCL_to_LUV(vec3 HCL) {
 	vec2 uv = vec2(cos(HCL.x), sin(HCL.x)) * HCL.y;
    return vec3(HCL.z, uv);
}

//other stuff
vec2 lmin(vec2 a, vec2 b) {
    if(abs(a.x-b.x) < 0.0000000001) {
      return a.y > b.y ? a : b;
    }

    return a.x < b.x ? a : b;
}

// Test if point p crosses line (a, b), returns sign of result
float testCross(vec2 a, vec2 b, vec2 p) {
    return sign((b.y-a.y) * (p.x-a.x) - (b.x-a.x) * (p.y-a.y));
}

// Determine which side we're on (using barycentric parameterization)
float signBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{ 
    vec2 a = C - A, b = B - A, c = p - A;
    vec2 bary = vec2(c.x*b.y-b.x*c.y,a.x*c.y-c.x*a.y) / (a.x*b.y-b.x*a.y);
    vec2 d = vec2(bary.y * 0.5, 0.0) + 1.0 - bary.x - bary.y;
    return mix(sign(d.x * d.x - d.y), mix(-1.0, 1.0, 
        step(testCross(A, B, p) * testCross(B, C, p), 0.0)),
        step((d.x - d.y), 0.0)) * testCross(A, C, B);
}

// Solve cubic equation for roots
vec3 solveCubic(float a, float b, float c)
{
    float p = b - a*a / 3.0, p3 = p*p*p;
    float q = a * (2.0*a*a - 9.0*b) / 27.0 + c;
    float d = q*q + 4.0*p3 / 27.0;
    float offset = -a / 3.0;
    if(d >= 0.0) { 
        float z = sqrt(d);
        vec2 x = (vec2(z, -z) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        return vec3(offset + uv.x + uv.y);
    }
    float v = acos(-sqrt(-27.0 / p3) * q / 2.0) / 3.0;
    float m = cos(v), n = sin(v)*1.732050808;
    return vec3(m + m, -n - m, n - m) * sqrt(-p / 3.0) + offset;
}

// Find the distance to the curve, and signed distance to unclamped curve
vec2 sdBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{    
    B = mix(B + vec2(1e-4), B, abs(sign(B * 2.0 - A - C)));
    vec2 a = B - A, b = A - B * 2.0 + C, c = a * 2.0, d = A - p;
    vec3 k = vec3(3.*dot(a,b),2.*dot(a,a)+dot(d,b),dot(d,a)) / dot(b,b);      
    vec3 t = solveCubic(k.x, k.y, k.z);
    vec2 pos = A + (c + b*t.x)*t.x;
    float dis = length(pos - p);
    pos = A + (c + b*t.y)*t.y;
    dis = min(dis, length(pos - p));
    pos = A + (c + b*t.z)*t.z;
    dis = min(dis, length(pos - p));
    float unclamped = dis * signBezier(A, B, C, p);

    t = clamp(t, 0.0, 1.0);
    pos = A + (c + b*t.x)*t.x;
    dis = length(pos - p);
    pos = A + (c + b*t.y)*t.y;
    dis = min(dis, length(pos - p));
    pos = A + (c + b*t.z)*t.z;
    dis = min(dis, length(pos - p));

    return vec2(dis, unclamped);
}

vec3 sdFlame(vec2 pos) {

    if(dot(pos,pos) > 4.0) return vec3(length(pos)-2.0, 0.0, 0.0);

    float yd = clamp((pos.y + 1.0)* 0.5,0.0, 1.0);

    pos.x += sin((iTime+yd*0.1)*30.0) * yd * 0.06;
    pos.x += sin((iTime+yd*0.3)*23.12) * yd * 0.01;
    pos.y += sin((iTime+yd*0.1 + 1.0)*30.0) * yd * 0.03;
    pos.x = -abs(pos.x);
    float w = 0.3;
    float b = -0.7;
    float m = -0.3;
    float t = 1.0;
    vec2 p0 = vec2(0.0, -1.0);
    vec2 p1 = vec2(-w, m);
    vec2 p2 = vec2(-0.2, 0.5);
    vec2 p3 = vec2(0.0, t);
    vec2 c0 = vec2(-w, b);
    vec2 c1 = vec2(-w, -0.2);
    vec2 c2 = p2 + (p2-c1) * 0.5;
    vec2 sd = vec2(1000.0, 0.0);
    sd = lmin(sd, sdBezier(p0, c0, p1, pos));
    sd = lmin(sd, sdBezier(p1, c1, p2, pos));
    sd = lmin(sd, sdBezier(p2, c2, p3, pos));

    float d = sd.x * -sign(sd.y);
    float id = 1.0;
    float grad = sd.x*5.0 + (1.-yd) * 4.0;
    return vec3(d, id, grad);
}

vec3 sdMidPetal(vec2 pos) {

    if(dot(pos,pos) > 16.0) return vec3(length(pos)-4.0, 0.0, 0.0);
    pos.x = abs(pos.x);
    float w = 0.5;
    float m = -0.6;
	vec2 p0 = vec2(0.0, 0.0);
    vec2 p1 = vec2(w, m);
    vec2 p2 = vec2(0.05, m*2.0);
    vec2 c0 = vec2(w+0.3, -0.2);
    vec2 c1 = vec2(0.05, m-0.3);

    vec2 sd = vec2(1000.0, 0.0);
    sd = lmin(sd, sdBezier(p0, c0, p1, pos));
    sd = lmin(sd, sdBezier(p1, c1, p2, pos));

    float d = sd.x * -sign(sd.y);
    float id = 2.0;
    float grad = sqrt(sd.x / w * 1.0);
    return vec3(d, id, grad);
}

vec3 sdSidePetal(vec2 pos) {
    if(dot(pos,pos) > 4.0) return vec3(length(pos)-2.0, 0.0, 0.0);
    pos.x = abs(pos.x);
    float w = 0.8;
    float m = -0.65;
    float t = 0.1;
	vec2 p0 = vec2(0.1, m);
    vec2 p1 = vec2(w, t);
    vec2 p2 = vec2(w*1.5, t-0.4);
    vec2 p3 = vec2(w, m-0.2);
    vec2 p4 = vec2(0.01, m-0.6);
    vec2 c0 = vec2(w-0.1, t-0.1);
    vec2 c1 = vec2(w*1.5, t);
    vec2 c2 = vec2(w*1.5-0.1, m-0.1);
    vec2 c3 = vec2(0.1, m-0.2);

    vec2 sd = vec2(1000.0, 0.0);
    sd = lmin(sd, sdBezier(p0, c0, p1, pos));
    sd = lmin(sd, sdBezier(p1, c1, p2, pos));
    sd = lmin(sd, sdBezier(p2, c2, p3, pos));
    sd = lmin(sd, sdBezier(p3, c3, p4, pos));

    float d = sd.x * -sign(sd.y);
    float id = 2.0;
    float grad = sqrt(sd.x / w * 2.0);
    return vec3(d, id, grad);
}

vec3 scene_min(vec3 a, vec3 b) {
 	return a.x < b.x ? a : b;   
}

vec3 scene_overlay(vec3 a, vec3 b) {
 	return b.x < 0.0 ? b : a;   
}

vec3 sdFlower(vec2 pos) {
    float h = (pos.y + 4.0) / 5.0;
    pos.x += sin(iTime*4.0) * 0.04 * h;
	vec3 sd = sdFlame(pos);
    vec2 offset = vec2(0., -0.7);
    sd = scene_overlay(sd, sdSidePetal(pos-offset));
    sd = scene_overlay(sd, sdMidPetal(pos-offset));
    return sd;
}



