void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    uv *= 2.0;

    vec2 m = iMouse.xy/iResolution.xy;
	m.x *= iResolution.x/iResolution.y;

	vec3 sd = sdFlower(uv * 2.0);

    vec3 col = vec3(0.0, 0.0, 0.0);
    if(sd.x >= 0.0) {
        col = LUV_to_RGB(mix(blues[0], blues[1], min(1., length(uv * 0.1))));
    }
    else if(sd.y == 1.0) {
        vec3 a = HCL_to_LUV(vec3(1.269591657661457,66.76847476254702,91.24781583307198));

        vec3 b = HCL_to_LUV(vec3(1.21570998919011,32.3252321015046,94.58142126830768));
        col = LUV_to_RGB(mix(a,b, max(0.,sd.z)));
    }
    else if(sd.y == 2.0) {

        col = LUV_to_RGB(mix(golds[0], golds[1], clamp(sd.z, 0.0, 1.0)));  
    }



	fragColor = vec4( col, 1.0 );;
}

