const float w = 2.;

const float PI = 3.1416;

vec2 hash2( vec2 p )
{
    // procedural white noise	
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),
                          dot(p,vec2(269.5,183.3))))*43758.5453);
}

//From http://www.iquilezles.org/www/articles/voronoilines/voronoilines.htm
vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    vec2 ml = vec2(0.0);
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;

        if( dot(r-mr,r-mr)>0.00001 ) {
            vec2 l = normalize(r-mr);
            float d = dot( 0.5*(mr+r), l );
            if (md > d) { 
        		md = d;
                ml = l;
            }
        }           
    }

    return vec3(md, n+mg);
}

//From https://www.shadertoy.com/view/MtyGWK
void voronoi_s(in vec2 x, inout vec2 n,  inout vec2 f, 
                          inout vec2 mg, inout vec2 mr) {

    n = floor(x);
    f = fract(x);

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(i,j);
		vec2 o = hash2(n + g);
        vec2 r = g + o - f;
        float d = dot(r,r);

        if (d < md) {
            md = d;
            mr = r;
            mg = g;
        }
    }  
}

vec3 voronoi_n(vec2 rd, vec2 n,  vec2 f, 
               inout vec2 mg, inout vec2 mr) {
    float md = 1e5;
    vec2 cmg = mg;
    vec2 cmr = mr;
    vec2 ml = vec2(0.0);

    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = cmg + vec2(i,j);
		vec2 o = hash2(n + g);
		vec2 r = g + o - f;

    	vec2 l = r-cmr;

        if(dot(rd, l) > 1e-5) {
            float d = dot(0.5*(cmr+r), l)/dot(rd, l);

            if (d < md) {
                md = d;
                mg = g;
                mr = r;
                ml = l;
            }
        }   
    }

    return vec3(md, normalize(ml));
}

float grid(vec2 uv, float e, float v) 
{
    return smoothstep(0.0, w*e, v);
}

float point(vec2 uv, float e, vec2 p) 
{
	return smoothstep(0.07, 0.07 + w*e, length(uv - p)); 
}

float segment(vec2 uv, float e, vec2 d, vec2 p, float l) 
{
    return max(1.0 - min(step(0.0, dot(d, uv - p)), 
                         step(0.0, dot(d, p + d*l - uv))),
   		       smoothstep(0.015, 0.015 + w*e, abs(dot(vec2(-d.y, d.x), p - uv))));
}

float refract_index(vec2 c) 
{
    c -= vec2(7.0, 6.0);
    const float r = 3.0;  
    return dot(c, c) > r*r ? 1.0 : 2.7;
}

vec2 traversal(vec2 uv, float e, vec2 uvv, vec2 ro, vec2 rd) 
{   
    vec2 v = vec2(1.0);

    vec2 i = vec2(0.0), f = vec2(0.0);
    vec2 mg = vec2(0.0), mr = vec2(0.0);

    voronoi_s(ro, i, f, mg, mr);

    float refri = refract_index(i + mg);    

    vec2 p = ro;
    for( int s = 32; s > 0; --s ){

        //Draw cell and point.
       	vec2 c = abs(i + mg - uvv);
        float cc = 1.0 - step(0.0, -(c.x + c.y));
        v = min(v, vec2(point(uv, e, p), cc));

        vec2 pmg = mg;
    	vec3 vor = voronoi_n(rd, i, f, mg, mr);

        v.x = min(v.x, segment(uv, e, rd, p, vor.x)); // Draw segment.

       	p += rd*vor.x;

        //Refraction part.
        #if 1
        float nrefri = refract_index(i + mg);

        vec2 reflrd = reflect(rd, -vor.yz);
        rd = refract(rd, -vor.yz, refri/nrefri);

        float t = step(0.0, -dot(rd, rd));
       	refri = mix(nrefri, refri, t);
        rd += reflrd*t;
        mg = mix(mg, pmg, t);
        #endif

        vec2 o = mr - mg + f;
        vec2 ip = floor(p);
        f = fract(p);

        mg += i - ip;
        mr = mg + o - f;
        i = ip;
    }

    v.x = min(v.x, point(uv, e, p)); 

    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float e = 25.0 / iResolution.x;
    vec2 offset = vec2(5.0, 3.0);
	vec2 uv = fragCoord.xy*e - offset;

    vec2 ro = vec2(3.8, 1.2);

    vec2 rd = (length(iMouse.xy) < 10.0)
        	? normalize(vec2(1.0, sin(iTime*0.2) + 1.5))
    		: normalize(iMouse.xy*e - offset - ro);


    vec3 v = voronoi(uv);
    float ri = refract_index(v.yz);
    vec3 c = vec3(1.0 - step(0.0, 1.0 - ri), 0.2, 
                  step(0.0, 1.0 - ri)) + 0.3;

    vec2 t = traversal(uv, e, v.yz, ro, rd);
    c += (1.0 - t.y) * 0.2;
    c *= t.x;
    c *= grid(uv, e, v.x);

	fragColor = vec4(pow(clamp(c, 0.0, 1.0), vec3(0.4545)), 1.0);
}

#define MAX_STEPS 200
#define MIN_DIST .01
#define MAX_DIST 100.

// Get the distance and normal to the surface 
// (if the distance is < MIN_DIST in w).
vec4 GetDist(vec3 pos) 
{
    vec4 sphere = vec4(0., 1., 6., 1.);
    float distSphere = length(pos - sphere.xyz) - sphere.w;
    float distPlane = pos.y;
    if (distSphere < distPlane) 
        return vec4(normalize(pos - sphere.xyz), distSphere);
    else 
        return vec4(0, 1, 0, distPlane);
}

// Get the new distance and the normal to the surface.
// (if the dirance is < MIN_DIST in w).
vec4 RayMarching(vec3 ray_origin, vec3 ray_direction) 
{
    float dist0 = 0.0f;
    for (int i = 0; i < MAX_STEPS; ++i) {
        vec3 p = ray_origin + ray_direction * dist0;
        vec4 normal_dist = GetDist(p);
        dist0 += normal_dist.w;
        if (normal_dist.w < MIN_DIST || dist0 > MAX_DIST) 
            return vec4(normal_dist.xyz, dist0);
    }
    return vec4(0, 1, 0, dist0);
}

// Calculate the shade float.
float GetLight(vec3 pos, vec3 normal) 
{
    vec3 light_pos = vec3(0, 5, 6);
    light_pos.xz += vec2(sin(iTime), cos(iTime)) * 2.;
    vec3 light_normal = normalize(light_pos - pos);
    float light_value = dot(normal, light_normal);
    float dist_light = 
        RayMarching(pos + normal * MIN_DIST * 2., light_normal).w;
    if (dist_light < length(light_pos - pos)) light_value *= .1;
    return light_value;
}

// Main entry point.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 ray_origin = vec3(0, 1, 0);
    vec3 ray_direction = normalize(vec3(uv.x, uv.y, 1));
    vec4 result = RayMarching(ray_origin, ray_direction);
    vec3 pos = ray_origin + ray_direction * result.w;
    float shade = GetLight(pos, result.xyz);
    vec3 color = vec3(shade);

    // Output to screen
    fragColor = vec4(color, 1);
}

