// This is my first structure, Note ";" after "}"
struct ray {
    vec3 o,d; // Def ray with 2 origin and direction vec3's
};

ray CameraSetup(vec2 uv,vec3 camPos,vec3 la,float zoom) {
  ray a;                             // define ray 
  a.o = camPos;                      // ray origin set to camera 
  vec3 f = normalize(la-camPos);     // forward 
/* Cross product z of vectors x & y is orthogonal/normal to plane xy with a direction
   given by the right hand rule.(Eg. Thumb is z vector point out of screen ). */
  vec3 r = cross(vec3(0,1,0),f);     // right (Cross product)
  vec3 u = cross(f,r);               // up 
  vec3 c = a.o + f * zoom;           // screen centre
  vec3 i = c + uv.x * r + uv.y * u;  // intersection pt
  a.d = normalize(i - a.o);          // ray direction 
  return a;
}

/* Dot product of 2 vectors a & b with angle c between them is a scalar value, actually 
   the ‚àö(a ‚Ä¢ a) = |a| (the magnitude of vector a). Given P=(a,b) & Q=(c,d) with angle 
   @ between them, then P ‚Ä¢ Q is ac + bd. The dot product of two normalised vectors,
   a ‚Ä¢ b = cos(c), or generally |a||b|cos(c), so to find the angle between them 
   use c= arccos((a ‚Ä¢ b)/(|a|*|b|)). 
   When vectors are at 90¬∞ the ‚Ä¢ product = 0 as when normalised it represents the 
   amount of a going in direction b, its like projection a onto b,. If c>90¬∞ a‚Ä¢b will 
    be -ve indicating opposing directions (or forces) */

vec3 ClosestPt(ray r,vec3 p) {
    return r.o + max(0.,dot(p-r.o,r.d)) * r.d;
}

float DistRay(ray r, vec3 p) {
    return length(p-ClosestPt(r,p));
}

// Bokeh Japanese term that describes out of focus highlights 
float Bokeh(ray r, vec3 p, float size, float blur, float pf,float pe, float ssMin ) {   
    float d = DistRay(r,p);              // define direction
//  size *= length(p);      // pe=pf=1. stops pts from getting smaller in distance
    size *= pow(length(p*pf),pe);  // CDA Let's pts enlarge pf=p factor & pe=p exponent         
    float c = S(size,size*(1.-blur),d);     // define circle around pt p
    c *= mix(.6,1.,S(size*ssMin,size,d));   // brighten Circumf. when>.8 of radius
   return c;
}

vec3 PlaneLights(ray r, float t) {
    t = -t*10.;  // reverse light direction & increase speed
    // need to make lights appear alternately from each side 
    float side = step(r.d.x,0.); //*2.+1.;   // if r.d.x -ve side=0 else side =1
    r.d.x = abs(r.d.x);                  // mirror horizontally to get left side
//    r.d.y = abs(r.d.y);            //CDA  mirror vertically to get road reflected pts
    float s = 1./20.;
    float m = 0.;
    for (float i=0.;i<1.;i+=s) {
      float n = N(i);             // *side;
      if (n>.015) continue; // skip to next plane for 60% of virtual cars 
      for (float q=i;q<i+.25;q+=1./100.) { 
        float ti = fract(t+q+side*s*.5);     // +side*s*.5 shifts lights by half step
        vec3 p = vec3(2.,2.,100.-(ti*100.));  // def pt,fract resets posn, 

        m += Bokeh(r,p,.01,.1,1.,1.,.88)*pow(ti+(q/50.),5.); // Def Circle,*ti^3 causes dist. fade.  
   //   m += Bokeh(r,p,.0125,.1,6260.,0.257,.88)*pow(ti+(q/100.),20.); // CDA test lights enlarging A
   //   m += Bokeh(r,p,.0125,.1,8.,0.65,.88)*pow(ti+(q/100.),20.);    // CDA test lights enlarging B */
      }
      float ti = fract(t+side*s*.5); // @ t+side 
      vec3 p = vec3(2.,2.,100.-(ti*100.));  // ti*ti*ti*ti*ti
   // m += Bokeh(r,p,.015,.1,1.,1.,.88)*pow(ti,10.); // Def Circle,*ti^3 causes dist. fade.   
      m += Bokeh(r,p,.0125,.1,1.,1.,.88)*ti*3.95;//*ti
    }
    return vec3(.3,.3,.99) * m;           // define color jet blue 
}

