      m += Bokeh(r,p,.05,.1,8.,0.65,.88)*ti*ti*ti;    // CDA test lights enlarging B */
    }
    return vec3 (1.,.7,.3) * m;             // define color sodium lamp
}

vec3 EnvLights(ray r, float t) {
    // need to make lights appear alternately from each side 
    float side = step(r.d.x,0.);         // if r.d.x -ve side=0 else side =1
    r.d.x = abs(r.d.x);                  // mirror horizontally to get left side
//   r.d.y = abs(r.d.y);            //CDA  mirror vertically to get road reflected pts
    float s = 1./10.;
    vec3 c = vec3(0.);       // can't use mask for different colors,so accumulate c
    for (float i=0.;i<1.;i+=s) {
      float ti = fract(t+i+side*s*.5);     // +side*s*.5 shifts lights by half step.    
      vec4 n = N14(i+side*100.);      // Generate random locations based on i & side

      float fade = ti*ti*ti*2.69;                // *ti^3 causes distance fade

      float occlusion = sin(ti*6.28*10.*n.x)*.5+.5;  // 10 * TWO_PI revolutions
      fade *= occlusion;
      float x = mix(2.5,10.,n.x);          // x location beyond road 
      float y = mix(0.1,1.5,n.y);          // y locn. from just above ground to 1.5 

      vec3 p = vec3(x,y,50.-(ti*50.));     // def pt,fract resets posn, 

      vec3 col = n.wzy;                    // get random color, (blueness ~= height);
      float envLightSize=NR(sin(i))/40.+.025;     // CDA set light size range .025 to .05
      c += Bokeh(r,p,envLightSize,.1,1.,1.,.88)*fade*col; // Def Circle
/*    c += Bokeh(r,p,envLightSize,.1,6260.,0.257,.88)*fade*col; // CDA test lights enlarging A
      c += Bokeh(r,p,envLightSize,.1,8.,0.65,.88)*fade*col;    // CDA test lights enlarging B */
    }
    return c;             //  color
}

vec3 HeadLights(ray r, float t) {
    t *=2.;                  // Oncoming traffic twice as frequent as streetlights
    r.d.x = -r.d.x;          // UK Fix !
    float w1 = .25;          // Define width of a car
    float w2 = w1 * 1.2;     // Define width of a car headlight

    float s = 1./30.;        // Car frequency = 30 per sec 
    float m = 0.;            // Mask
    vec3 bulb;               // CDA defined outside loop mutant car bulb 

    for (float i=0.;i<1.;i+=s) {
      float n = N(i);            // for each car get rnd between 0 and 1
      if (n>.1) continue;        // skip to next car for 90% of virtual cars 
      bulb = vec3(.9,.9,1.);     // CDA set normal bulb color
      /* doesn't work as NR(i) fixed only evaluated at compile time ? 
      if (NR(i)<.02) {                    // CDA Probability of mutant bulb 
         bulb = vec3(NR(i),NG(i),NB(i));  // CDA set random bulb color
      } */
      float ti = fract(t+i);     // fract() resets Position
      float z = 100.-(ti*100.);  // Dist along road 

      float fade = ti*ti*ti*ti*ti;  // ti^5 causes dist. fade as light recedes
      float focus = S(.9,1.,ti); // Step into focus while Dist between .8 and 1.
      float size = mix(.05,.03,focus);             // reduce size when in focus
      // defn of pt "p" moved inside Bokeh parameters ,
      m += Bokeh(r,vec3(-1.-w1,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 1a
      m += Bokeh(r,vec3(-1.+w1,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 2a
      m += Bokeh(r,vec3(-1.-w2,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 1b
      m += Bokeh(r,vec3(-1.+w2,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 2b

/*    m += Bokeh(r,vec3(-1.-w1,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 1a
      m += Bokeh(r,vec3(-1.+w1,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 2a 
      m += Bokeh(r,vec3(-1.-w2,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 1b
      m += Bokeh(r,vec3(-1.+w2,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 2b */

      float ref = 0.;  // define faint road reflection. Note .9 blur
      ref += Bokeh(r,vec3(-1.-w2,-.15,z),size*3.,.9,1.,1.,.8)*fade; // Def reflection 1
      ref += Bokeh(r,vec3(-1.+w2,-.15,z),size*3.,.9,1.,1.,.8)*fade; // Def reflection 2

/*    ref += Bokeh(r,vec3(-1.-w2,.15,z),size*3.,.1,2260.,0.257,.8)*fade; // CDA ref 1
      ref += Bokeh(r,vec3(-1.+w2,.15,z),size*3.,.1,2260.,0.257,.8)*fade; // CDA ref2 */

      m += ref * focus;                            // Add reflection when in focus 
    }
 // return vec3(.9,.9,1.) * m;              // define color blue-white
    return bulb * m;                        // CDA define bulb color mostly blue-white
}

vec3 TailLights(ray r, float t) {
    t /=4.;                  // Overtaken traffic ¬º as frequent as streetlights
    r.d.x = -r.d.x;          // UK Fix !
    float w1 = .25;          // Define width of a car
    float w2 = w1 * 1.2;     // Define width of a car headlight

    float s = 1./15.;        // Car frequency = 15 per sec 
    float m = 0.;            // Mask
    vec3 bulb;               // CDA defined outside loop - mutant car bulb 

    for (float i=0.;i<1.;i+=s) {
      float n = N(i);            // for each car get rnd between 0 and 1
      if (n>.5) continue;        // skip to next car for 50% of virtual cars 
      bulb = vec3(1.,.15,.025);    // CDA set normal bulb color RED was (1.,.1,.1)             
/* Trick : reducing blue from .1 to .03 gives orange blinker eg. Original (1.,.1,.03) - 
  Orange = -25% Green -75% Blue. From Red's Brightness .1 -> .075g .025b, 
  or try .2 -> 0.15g 0.05b halfway = .1125g .0375b */

      /* doesn't work as NR(i) etc fixed, only evaluated at compile time ? 
      if (NR(i)<.02) {                    // CDA Probability of mutant bulb 
         bulb = vec3(NR(i),NG(i),NB(i));  // CDA set random bulb color
      } */
      float lane = step(.25,n);  // returns 0 or 1 (as n range is now 0 to .5)
      float ti = fract(t+i);     // fract resets posn,
      float z = 100.-(ti*100.);  // dist along road 
      float fade = pow(ti,5.);   // ti^5 causes dist. fade as light recedes
   //   float fade = ti*ti*ti*ti*ti;  // Not using pow(ti,5) reintroduces BUG
      float focus = S(.9,1.,ti); // Step into focus while Dist between .9 and 1.
      float size = mix(.05,.03,focus);      // reduce size when in focus
      float laneshift = S(1.,.96,ti);       // 1 if far away 0 if close 
      float x = 1.5-(lane*laneshift);       // car position (was -1. for headlights)
      float tmp3=7.*(lane-.00000001);       // BUG Fix requires var & *..01 Only lane 2
      float blink = step(0.,sin(t*1000.))*tmp3*step(.796,ti);  // Blinker, off in dist @ .796 was .96
      // defn of pt "p" is now vec3() moved inside Bokeh parameters ,
      m += Bokeh(r,vec3(x-w1,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 1a
      m += Bokeh(r,vec3(x+w1,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 2a
      m += Bokeh(r,vec3(x-w2,.15,z),size,.1,1.,1.,.8)*fade; // Def Circle 1b
      m += Bokeh(r,vec3(x+w2,.15,z),size,.1,1.,1.,.8)*fade*(1.+blink); // Def Circle 2b

/*    m += Bokeh(r,vec3(x-w1,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 1a
      m += Bokeh(r,vec3(x+w1,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 2a 
      m += Bokeh(r,vec3(x-w2,.15,z),size,.1,2260.,0.257,.8)*fade; // CDA Circle 1b
      m += Bokeh(r,vec3(x+w2,.15,z),size,.1,2260.,0.257,.8)*fade*(1.+blink); // CDA Circle 2b */

      float ref = 0.;  // define faint road reflection. Note .9 blur
      ref += Bokeh(r,vec3(x-w2,-.15,z),size*3.01,.9,1.,1.,.8)*fade; // Def refl 1 BUG Fixed with 3.01
    //  blink *= 0.0;  // BUG prevented .1 being used
      float tmp4=(blink-.0000001)*.1;   // Reduce glare by 90%
      ref += Bokeh(r,vec3(x+w2,-.15,z),size*3.,.9,1.,1.,.8)*fade*(1.+tmp4); // blink);
      // Def refl2 & blink - Bug *1.0000001 but * 1.00000001 OK
/*    ref += Bokeh(r,vec3(x-w2,.15,z),size*3.,.1,2260.,0.257,.8)*fade; // CDA Ref1
      ref += Bokeh(r,vec3(x+w2,.15,z),size*3.,.1,2260.,0.257,.8)*fade; // CDA Ref2 */

      m += ref * focus;                            // Add reflection when in focus 
    }
 // return vec3(1.,.1,.1) * m;              // define color red
    return bulb * m;                        // CDA define bulb color mostly Red
}

vec2 Rain(vec2 uv, float t) {   
//  vec2 st=uv*10.;     // #1 Rasta flag as ¬º's G,Y,R,Black clockwise from TopLeft
//  vec2 st=fract(uv*10.);     // #2 makes grid boxes 10 high
  t *= 40.;               // increase speed of droplets

  vec2 a = vec2(3.,1.);   // #3 makes boxes 3 * taller 
  vec2 st=uv*a;           // 

  vec2 id=floor(st);
  st.y +=t*.22;           // Makes entire fame move down, .22 tweak cancels up motion
  float n = fract(sin(id.x*716.34)*768.34); // noise 
  st.y += n;              // pseudo random #
  uv.y += n;              //     offsets drops vertically from centre of box 
//  st.x +=n;  // CDA
//  uv.x +=sin(n*.8*t/19.); // pow(n*.6,2.);   // CDA offsets drops horizontally from centre of box 


  id = floor(st);      // make box id 
  st = fract(st)-.5;   // #3 within each box uv coords from (0,0) to (1,1),
                       // #3 -.5 moves origins to centers 

  t += fract(sin(id.x*716.34+id.y*1453.7)*768.34)*6.283; // rnd # Time * TWO_PI to 
                     // gives phase difference . 

  float y =-sin(t+sin(t+sin(t)*.5))*.43; // #3 Adds control of vertical posn. of drop 
   // Above modification approximates a saw tooth wave, up quick down slow 
  vec2 p1 = vec2(0.,y);        // #3 .43 above is tweak to prevent drop overlapping its box 
  vec2 o1 = (st-p1)/a;    // #5 define offset 1 
  float d = length(o1);   // #3 distance to droplet ,/a fixes ellipses 

  float m1 = S(.07,.0,d);     // #3 mask for droplet (yellow on black,only RG cols used!)

  vec2 o2 = (fract(uv*a.x*vec2(1.,2.))-.5)/vec2(1.,2.); // #4 Trail of smaller drops,  
                           // fract=per box,-.5=centred,fn*vec2)/vec2 = more cells  
  d = length(o2); // dist to drops, 

  float m2 = S(.3*(.5-st.y),.0,d)*S(-.1,.1,st.y-p1.y); // #4 mask for smaller droplets 
                              // (.5-st.y) shrinks, * S() prevents trail below drop 

//  if (st.x>.46||st.y>.49) m1=1.;    // Debug #3 show vertical & horizontal yellow lines

  return vec2(m1*o1*20.+m2*o2*10.);        // #1& #2 was(st), #3 m1, #4 m
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;                             // moves origin from bottom left to centre
    uv.x*=iResolution.x/iResolution.y;  // turn ellipse to circle using aspect ratio

    vec2 m = iMouse.xy/iResolution.xy;  // defines m from Mouse to mirror uv.xy coords
    float t = iTime * (.01+ m.x/5.);   // m.x allows mouse control of playback speed. 
                                       // fixed mouse by adding brackets

    vec3 camPos = vec3(0.6,.2,0);       // camera x position was 0 (now fast lane UK)
    vec3 la     = vec3(0.6,.2,1);       // look at vec [CDA .9 not .5 4 close overtake]
//  uv.y += logistic(uv.x,.235,19.,-0.25)*1.0-.23;       // CDA distorted heights 

    float rainScale=5.;               // sets drop size, was 3., wee =15. big=.85
    vec2 rainDistort = Rain(uv*rainScale,t)*.5;    // Add Rain layer 1
    rainScale=7.;
    rainDistort += Rain(uv*rainScale,t)*.5;        // Add Rain layer 2

    uv.x += sin(uv.y*70.)*.005;      // add horizontal wave distortion to scene 
    uv.y += sin(uv.x*117.)*.003;     // add vertical   wave distortion to scene 

    ray r = CameraSetup(uv-rainDistort*.5,camPos,la,2.);    // get ray from cam to la

    vec3 col = StreetLights(r,t);
    col += HeadLights(r,t);
    col += TailLights(r,t);
    col += EnvLights(r,t);

    if (t-float(int(t))>.9) col += PlaneLights(r,t);  // allow 1 in 10 planes to show

    col += (r.d.y+.25)*vec3(.32,.1,.75);   // add sky gradient
 //   col = vec3(rainDistort,0.);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);     // draw it

}

float dist(vec2 p1, vec2 p2) {
	return sqrt(pow(p1.x - p2.x,2.0) + pow(p1.y - p2.y,2.0));
}


