}

#ifdef GL_ES
precision mediump float;
#endif
#define PI 3.14159265359

vec2 rotate2D(vec2 st, float angle){
    st =  mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    return st;
}
vec2 tile(vec2 st, float zoom){
    st *= zoom;
    if (mod(st.x, 2.) < 1.){
        st = vec2(st.y, -st.x);
    }
    return fract(st);
}

float proceduralSplatter(vec2 st, float radius, float numCircles){
    float pct = 0.;
    st.x -= .5;
    for (float i = 1.; i < numCircles; i++){
        st.y -=(.3/ (i+1.));
        pct +=smoothstep(radius * 1./i, radius * 1./i - .1, length(st));
    }
    return pct;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord/iResolution.xy;
    float aspect = iResolution.x/iResolution.y;
    st.x *= aspect;
    st += rotate2D(st, PI * .1 * iTime);
    vec3 color = vec3(sin(iTime / 2.));
    st += rotate2D(st, PI * .25);
    vec2 grid2 = tile(st,2.);
    grid2.y -= .003 * iMouse.y;
    color = mix(color, vec3(0.2,0.52,0.502 *(iMouse.x * .003)), proceduralSplatter(grid2, .2 , 10.));
    vec2 grid3 = tile(st, 3.);
    grid3 -= .1;
    color = mix(color, vec3(0.6, 0.3, 0.3), proceduralSplatter(grid3, .2 * sin(iTime), 9.));
    fragColor = vec4(color,1.0);
}

void getSkyColor(float x, float y, inout vec3 color) {
    float h = max(0.0, 1.4 - y - pow(abs(x - 0.5), 3.0));
    color.r = pow(h, 3.0);
    color.g = pow(h, 9.0);
    color.b = 0.3 + pow(max(0.0, h - 0.1), 10.0);
}

float rand(float x){
    return fract(sin(x)*1000.0);
}

float noise(float x){
    float i = floor(x);
    float f = fract(x);
    return mix(rand(i),rand(i+1.0),f * f * (3.0 - 2.0*f));
}

float terrain(float x){
    float y = 0.0;
    for(int oct = 0; oct<10; ++oct){
        float k = pow(2.0,float(oct));
        y+=noise(x * k)/k;
    }
    return y*0.5;
}

float water(float x){
    float y = 0.0;
    y = sin(x*75.0)*0.01 + 0.2;
    return y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = vec3(0,0,0);
	getSkyColor(uv.x,uv.y,col2);
    col = mix(col,col2,sin(uv.x*0.1+0.9));
    // Output to screen
    float shift = 0.1 *iTime + 0.2;
    uv.x += shift;
    float h = max(water(uv.x), terrain(uv.x));
    if(uv.y<h) col = vec3(0,0,0);
    fragColor = vec4(col,1.0);

}

// reusing base ray-marcher https://shadertoy.com/view/XstBWn

float K = 4.;
//#define smin(a,b) pow( pow(a,-K) + pow(b,-K), -1./K ) 
#define ell(v)    length( (v) / vec3(1,2,2) ) 
//#define body(P) min( min(P.x,P.y), min(P.z,P.w))
//#define body(P) smin( smin(P.x,P.y), smin(P.z,P.w))
#define rot(a)    mat2(cos(a),-sin(a),sin(a),cos(a) )

void mainImage(out vec4 O, vec2 U) {

    float t = iTime+1.,v0,v1, r,l=1.;
    vec3  q = iResolution, R,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),      // ray direction
          p = 30./q, q0;                        // marching point along ray 
    vec2  M = 10.* iMouse.xy / q.xy;
    mat2  Rx = rot(length(iMouse.xy)<20. ? -t : -M.y ),
          Ry = rot(length(iMouse.xy)<20. ? -t : M.x );
    vec4 P;
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.01 ) {
        q = p,
        q.xz *= Ry, q.yz *= Rx,                 // camera rotation
        v0 = v1 = 0.;
        for (float x = -4.; x <= 2.; x++ ) {    // for each segments
           //q0 = q-vec3(3.*x,0,0);
            r = 9., R = 3.*vec3(x,0,sqrt(r*r-x*x)-r);   // vector dist to rotation axis
            q0 = q - R;                                 // offset
            q0.xz *= rot(asin(x/r));                    // and rotate
            l = length(q0)/r;
            K = 8., v0 += pow( ell(q0), -K );           // inner membrane
            K = 2., v1 += pow( ell(q0), -K );           // outer membrane
          //K = 2., v1 += pow( ell(q0)*2.*l, -K );      // (with rot jacobian)
        }
        K = 8., v0 = pow(v0, -1./K) - 2.,
        K = 2., v1 = pow(v1, -1./K) - 2.,
        v0 = max(v0, -(v0+.15)),  v0 = max(v0, q.y), t = v0,        // thick inner membrane 
        v1 = max(v1, -(v1+.15)),  v1 = max(v1, q.y), t = min(t,v1), // thick outer membrane 
     /* P = vec4( ell(q), ell(q-vec3(3,0,0)), ell(q+vec3(3,0,0)) , ell(q+vec3(6,0,0)) ),
        K = 8., v = body(P) -2., v = max(v, -(v+.15)),  v = max(v, q.y), t = v,
        K = 2., v = body(P) -2., v = max(v, -(v+.15)),  v = max(v, q.y), t = min(t,v),
     */
       // t = max(t, q.y),
        p += t*D;                             // step forward = dist to obj
    }
    if ( t < .01 )
        O = t==v1 ? O*vec4(.8,.3,.4,1) + vec4(.2,0,0,0)
                  : O*vec4(.7,.7,.2,1) + vec4(.4,0,0,0);  // color membranes
}

const float PI = 3.14159265f;
const float GoldenAngle = PI * (3.0 - sqrt(5.0));
const float PointRadius = 2.0 + 2.0/3.0;

const vec2 DropShadow = vec2(-0.25, 0.75);

vec2 Vogel(uint sampleIndex, uint samplesCount, float Offset)
{
  float r = sqrt(float(sampleIndex) + 0.5f) / sqrt(float(samplesCount));
  float theta = float(sampleIndex) * GoldenAngle + Offset;
  return r * vec2(cos(theta), sin(theta));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Size = iResolution.y / 2.0;
    vec2 UV = fragCoord/iResolution.xy;   
    vec3 Color = mix(vec3(0.75), vec3(0.5), distance(UV,vec2(0.5)));

    Color = mix(
        Color,vec3(0.0),
        smoothstep(PointRadius*2.0, -PointRadius*2.0, abs(Size * 0.90 - distance(fragCoord + DropShadow, iResolution.xy/2.0)))
    );
    Color = mix(
        Color,vec3(1.0),
        smoothstep(PointRadius*2.0, -PointRadius*2.0, abs(Size * 0.90 - distance(fragCoord, iResolution.xy/2.0)))
    );

    UV.x *= iResolution.x / iResolution.y;
    float Pulse = sin(iTime * 0.0125 * PI * 2.0);
    uint Samples = uint(1024.0 * Pulse * Pulse);

    for( uint i = 0u; i < Samples; ++i )
    {
        float Phase = float(i) / float(Samples - 1u);
        vec2 VogelPos =
            iResolution.xy / 2.0
            + Vogel(i, Samples, iTime * 0.5)
            * Size * 0.85;
        Color = mix(
            Color,vec3(0.0),
            smoothstep(PointRadius + 1.0, PointRadius - 1.0, length((fragCoord + DropShadow) - VogelPos))
       	);
        Color = mix(
            Color,vec3(1.0),
            smoothstep(PointRadius + 1.0, PointRadius - 1.0, length(fragCoord - VogelPos))
        );
    }

    fragColor = vec4(Color, 1.0);
}

#define AA 3

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec4 fractal(vec2 U) {
    float m=1e3,t=iTime;
    U*=.13-sin(t*.2)*.1;
    U*=rot(sin(t*.1)*3.14);
    U+=vec2(sin(t),cos(t))*.005*sin(t*.2);
    vec4 p=vec4(cos(U.x),sin(U.x),cos(U.y),sin(U.y));
    for (int i=0;i<8;i++) {
    	p=abs(p+1.)-abs(p-1.)-p;
        p=p*1.5/min(1.,dot(p,p))-1.;
		m=min(m,fract( length(p*2.) -t*.1 ) );
    }
    return pow(m,1.7)*normalize(2.+abs(p.xyww))*2.;
}

void mainImage(out vec4 O,vec2 u)
{
    vec2 R=iResolution.xy,
         U=(u-R*.5)/R.y;
    U*=1.-dot(U,U)/4.;
    O=vec4(0);
	vec2 pix=1./R/float(AA)+max(0.,2.-iTime)*.005;
    for (int x=-AA;x<AA;x++) 
        for (int y=-AA;y<AA;y++)
		    O+=fractal(U+vec2(x,y)*pix);

	O/=float(AA*AA);
}

