const bool ENABLE_AND_PATTERN   = false;
const bool ENABLE_INVERSION     = false;
const bool ENABLE_LOOPING       = true;
const bool ENABLE_LOOP_REWIND   = true;

const float LOOP_LENGTH = 420.0;
const float LAYER_A_SPEED = 144.0;
const float LAYER_B_SPEED = 96.0;

const vec3 COLOR = vec3(0.0, 1.0, 0.0);

int isPrime(int num)
{
     if (num <= 1) return 0;
     if (num % 2 == 0 && num > 2) return 0;

     for(int i = 3; i < int(floor(sqrt(float(num)))); i+= 2)
     {
         if (num % i == 0)
             return 0;
     }
     return 1;
}

int patternA(int x){
    return ((x*x)&x);
}
int patternB(int x){
    return (x>>7)&x;
}

float isMagical(int x, int y){
    int v;
    if (ENABLE_AND_PATTERN)
        v = x & y;
    else
        v = x ^ y;

    float r = patternA(v) > patternB(v) ? 0.0 : 1.0;
    if (ENABLE_INVERSION)
        r = 1.0 - r;
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
    if (ENABLE_LOOPING){
        while(time > LOOP_LENGTH) time -= LOOP_LENGTH;
        if (ENABLE_LOOP_REWIND && time > LOOP_LENGTH / 2.0)
            time = LOOP_LENGTH - time;
    }

    int XA = int(fragCoord.x + time * LAYER_A_SPEED);
    int YA = int(fragCoord.y + time * LAYER_A_SPEED);

    int XB = int(fragCoord.x + time * LAYER_B_SPEED);
    int YB = int(fragCoord.y + time * LAYER_B_SPEED);

    float VA, VB;
    if (ENABLE_CROSSFIRE){
        VA = isMagical(XA, YB);
        VB = isMagical(XB, YA);
    } else {
        VA = isMagical(XA, YA);
        VB = isMagical(XB, YB);
    }

    float V;
    if (ENABLE_DOUBLE_LAYERS)
	    V = VA * VB;
    else
        V = VA;

    float S = 1.0;
    if (ENABLE_SHADING){
        vec2 halfRes = iResolution.xy / vec2(2.0, 2.0);
        float dx = abs(float(fragCoord.x) - halfRes.x) / float(halfRes.x);
        float dy = abs(float(fragCoord.y) - halfRes.y) / float(halfRes.y);
        S = 1.0 - sqrt(dx * dx + dy * dy);
    } else
        S = 1.0;
    fragColor = vec4(S * V, S * V, S * V, 1.0) * vec4(COLOR, 1.0);
}

#define PI 3.14159265359
vec2 c2p(vec2 c){return vec2(atan(c.y,c.x),length(c));}
vec2 p2c(vec2 p){return vec2(cos(p.x)*p.y,sin(p.x)*p.y);}
vec2 crep(vec2 uv, float c){
	uv=c2p(uv);
    uv.x=uv.x+PI;
    uv.x=mod(uv.x,2.*PI/c);
    uv.x=abs(uv.x-PI/c);
    uv.x=uv.x-PI;
    return p2c(uv);
}
vec3 look(vec2 uv, vec3 o, vec3 t)
{
    vec3 fwd=normalize(t-o);
    vec3 right=normalize(cross(fwd,vec3(0.,1.,0.)));
    vec3 up=normalize(cross(fwd,right));
    return fwd+right*uv.x+up*uv.y;
}
float box(vec3 p, float s)
{
    float d=abs(p.y)-s;
    d=max(d,abs(p.x)-s);
    d=max(d,abs(p.z)-s);
    return d;
}
float map(vec3 p)
{
    p-=6.5;
    p=mod(p,13.)-6.5;
    for(int i=0;i<5;i++)
    {
        p.xz=crep(p.xz,9.-float(i));
        p.xz+=0.7-0.01;
        float t=p.x;
        p.x=p.y;
        p.y=p.z;
        p.z=t;
    }
    float d=box(p,0.45);
    return d;
}
vec3 gradient(vec3 p)
{
    vec2 e=vec2(0.,0.001);
    return normalize(vec3(map(p+e.yxx)-map(p-e.yxx),
                map(p+e.xyx)-map(p-e.xyx),
                map(p+e.xxy)-map(p-e.xxy)));
}
float march(vec3 o, vec3 r)
{
    float t=0.2;
    for(int i=0;i<70;i++)
    {
        float d=map(o+r*t);
        if(d<0.004) return t;
        t+=d*0.8;
    }
    return 0.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv=uv-0.5;
    uv.x=uv.x*iResolution.x/iResolution.y;

	vec3 o=vec3(cos(iTime),cos(iTime),sin(iTime))*(sin(iTime*0.6)+1.15)*4.;
    vec3 t=vec3(0.);
    vec3 r=look(uv,o,t);
    float d=march(o,r);

    fragColor=vec4(0.);
    if(d==0.) return;
    float shade=dot(normalize(vec3(o)),gradient(o+r*d))/pow(1.1,d);
    // Output to screen
    fragColor = vec4(vec3(shade),1.0);
}

#define PI 3.14159265359
#define EPSILON 1e-4
#define INFTY 1e6
const float sphereSize = 0.15;
const float num = 3.0;

float sphereSDF(vec3 camPos, vec3 sphPos) {
    vec3 p =- camPos + sphPos;
    return length(p) - sphereSize;
}
float getAngle(vec3 p, vec3 q) {
    return acos(dot(normalize(p), normalize(q)));
}
float hitSphere(vec3 camPos, vec3 sphPos, vec3 ray) {
    vec3 p = - camPos + sphPos;
    return length(p) * sin(getAngle(p, ray)) - sphereSize;
}
vec3 getSpherePos(vec3 camPos, vec3 sphPos, vec3 ray) {
    vec3 p = - camPos + sphPos;
    float ang = getAngle(p, ray);
    float d = length(p);
    float theta = d*cos(ang) - sqrt(pow(d * cos(ang), 2.0) - pow(d, 2.0) + pow(sphereSize, 2.0));
    return camPos + theta*ray;
}

vec3 getNormal(vec3 p, vec3 sphPos) {
    float d = EPSILON;
    return normalize(vec3(
            sphereSDF(p + vec3(d, 0.0, 0.0), sphPos) - sphereSDF(p, sphPos),
            sphereSDF(p + vec3(0.0, d, 0.0), sphPos) - sphereSDF(p, sphPos),
            sphereSDF(p + vec3(0.0, 0.0, d), sphPos) - sphereSDF(p, sphPos)
        ));
}
mat3 rotX(float theta) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, sin(theta), cos(theta),
        0.0, - cos(theta), sin(theta)
    );
}
mat3 rotY(float theta) {
    return mat3(
        sin(theta), 0.0, cos(theta),
        0.0, 1.0, 0.0,
        - cos(theta), 0.0, sin(theta)
    );
}
mat3 rotZ(float theta) {
    return mat3(
        sin(theta), cos(theta), 0.0,
        - cos(theta), sin(theta), 0.0,
        0.0, 0.0, 1.0
    );
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // pixel position
    vec2 pixPos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 camPos = vec3(0.0, 0.0, 1.0);
    vec3 camDir = vec3(0.0, - 0.0, - 1.0);
    vec3 camUp = vec3(0.0, 1.0, 0.0);
    vec3 camSide = cross(camDir, camUp);
    float targetDepth = 1.0;
    float theta = iTime * 0.3;
    camPos = rotY(theta) * rotX(theta) * rotZ(theta) * camPos;
    camDir = rotY(theta) * rotZ(theta) * rotX(theta) * camDir;
    camUp = rotY(theta) * rotZ(theta) * rotX(theta) * camUp;
    // camSide = cross(camDir, camUp);	// moderate deformation
    float colMode = mod(theta * 2.0 / PI, 2.0); 
    // 0<colMode<1 if 0<theta<PI/2, 1<colMode<2 if PI/2<theta<PI, 

    //light
    vec3 lightDir = vec3(0.0, 0.0, - 5.0);
    lightDir = normalize(lightDir);

    vec3 ray = normalize(camSide * pixPos.x + camUp * pixPos.y + camDir * targetDepth);
    vec3 sphPos = vec3(INFTY);
    bool hit = false;
    for(float i = - num; i < num; i ++ ) {
        for(float j = - num; j < num; j ++ ) {
            for(float k = - num; k < num; k ++ ) {
                if (hitSphere(camPos, vec3(i, j, k), ray) < EPSILON && distance(camPos, vec3(i, j, k)) > 0.2) {
                    vec3 p = vec3(i, j, k);
                    if (distance(p, camPos) < distance(sphPos, camPos)) {
                        sphPos = p;
                    }
                    hit = true;
                }
            }
        }
    }
    if (hit) {
        vec3 rayPos = getSpherePos(camPos, sphPos, ray);
        vec3 normal = getNormal(rayPos, sphPos);
        float diff = clamp(dot(lightDir, - normal), 0.1, 1.0);
        fragColor = mix(
            vec4(0.0, 0.2667, 1.0, 1.0),
            vec4(0.0, 1.0, 0.298, 1.0), 
            mod(colMode, 1.0) * diff
            );       
    }else {
        fragColor = vec4(
            vec3(step(1.0, colMode)), 
            1.0);
    }
}

float noiseFunction(vec2 pos)
{
    return texture(iChannel0, pos - 0.5).x * 2.0 - 1.0;
}

float fbm(vec2 pos)
{
    pos *= 0.02;
    pos.y += iTime * -0.05;
    float sum = 0.0;
    float freq = 1.0;
    float amp = 1.0;

    for (int i = 0; i < 4; i++)
    {
        sum += amp * noiseFunction(pos * freq);
        amp *= 0.5;
        freq *= 3.0;
    }

    return sum;
}

vec2 toPolarCoords(vec2 pos)
{
    float radius = length(pos);
    float angle = atan(pos.y, pos.x);
    return vec2(radius, angle);
}

vec2 toRectCoords(vec2 polar)
{
    float x = polar.x * cos(polar.y);
    float y = polar.x * sin(polar.y);

    return vec2(x,y);
}

vec3 fire(vec2 pos)
{
    pos.y += 0.75;
    pos *= 5.0;

    float n = tanh(exp(fbm(pos) - pos.y));
    vec3 col = mix(vec3(pow(n, 0.4)), vec3(1.5 * n, 1.5 * pow(n,3.0), pow(n, 6.0)), n);

    return col;
}

vec4 rocketPlume(vec2 pos)
{
    pos.y *= -0.75;

    vec2 p = toPolarCoords(pos);
    p.x -= 1.2;

    pos = toRectCoords(p);
    vec3 col = fire(pos);
    float alpha = smoothstep(0.1, -0.2, p.x) * (smoothstep(2.5, 2.0, p.y) * smoothstep(0.5, 1.5, p.y));

    return vec4(col, alpha);
}

vec4 alf(vec2 pos)
{
    pos.y -= 0.06;
    pos.y *= -1.0;
    pos *= 4.0;
    pos += 0.5;
    float a = pos.x >= 0.0 && pos.x <= 1.0 ? 1.0 : 0.0;
    float b = pos.y >= 0.0 && pos.y <= 1.0 ? 1.0 : 0.0;
    vec4 col = texture(iChannel1, pos) * a * b;
    return col;
}

vec3 rocketAlf(vec2 pos)
{
    vec3 col = vec3(0.0);
    vec4 rocketc = rocketPlume(pos);
    col = mix(col, rocketc.xyz, rocketc.w);
    vec4 alfc = alf(pos);    
    col = mix(col, alfc.xyz, alfc.w);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float offset = mix(1.0, -2.1, smoothstep(0.0, 10.0, mod(iTime, 10.0)));
    uv.y += offset;

    vec3 col = rocketAlf(uv);    

    fragColor = vec4(
        col,
        1.0);
}

