// use conversion between polar and cartesian coordinates to generate rotated boundaries
float drawBoundary(vec2 pos, vec2 fragPos, int iAngle, float radius)
{   
    // add 0.001 to avoid 0 division on tan(90) & tan(270)
    float angle = float(iAngle % 360) + 0.001;

    // tan(angle) == y / x
    float gradient = tan(radians(angle));

    // y = mx + c => c = mx - y
    float fragEquation = (fragPos.x * gradient) - fragPos.y;

    // rotate around pos from a radius distance
    float radx = radius * cos(radians(angle));
    float rady = radius * sin(radians(angle));

    // antialiasing
    float aax = .8 * cos(radians(angle));
    float aay = .8 * sin(radians(angle));

    float boundaryPos1 = ((pos.x - rady - aay) * gradient) - (pos.y + radx + aax);
    float boundaryPos2 = ((pos.x - rady + aay) * gradient) - (pos.y + radx - aax);


    float inBounds = smoothstep(boundaryPos1, boundaryPos2, fragEquation);
    return inBounds;
}


float drawLine(vec2 pos, vec2 fragPos, int iAngle, float radius, float thickness)
{
    thickness /= 2.;
    float inBounds = drawBoundary(pos, fragPos, iAngle,       thickness + radius) *
                     drawBoundary(pos, fragPos, iAngle + 180, thickness - radius);

    return inBounds;
}


float drawShape(vec2 pos, vec2 fragPos, int iAngle, float size, float sides)
{
    float inBounds = 1.;
    float sideAngle = 360. / sides;

    for (float i = 0.; i <= 360.; i += sideAngle)
    {
        inBounds *= drawBoundary(pos, fragPos, iAngle + int(i), size);
    }

    return inBounds;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 lineCoord = vec2(0.5) * iResolution.xy;
    vec2 polyCoord = vec2(0.25, 0.75) * iResolution.xy;

    // iFrame probably isn't a great angle metric for time consistency but it works

    float b1 = drawBoundary(lineCoord, fragCoord, iFrame + 180, -0.25 * iResolution.y);

    float l1 = drawLine(lineCoord, fragCoord, 2 * iFrame,                 0.00, 2.);
    float l2 = drawLine(lineCoord, fragCoord,     iFrame, iResolution.y * 0.25, 1.);

    float s1 = drawShape(lineCoord, fragCoord, iFrame, 0.16 * iResolution.y, 4.0);
    float s2 = drawShape(polyCoord, fragCoord, iFrame, 0.1  * iResolution.y, 7.0);

    // max is the equivilant of drawing both shapes, would have liked to use binary | though
    vec3 col = vec3(max(s1, s2), max(l1, l2), max(l1, b1));

    // Output to screen
    fragColor = vec4(col,1.0);
}

