    p.y = -0.5 + p.y;
    vec2 a = p*vec2(6.283185,3.141593);

    vec3 q = vec3( cos(a.y)*cos(a.x),
        		   sin(a.y),
                   cos(a.y)*sin(a.x) );
#endif    

    //if( length(q-vec3(0,1,0))<0.1 ) return vec3(1,0,0);

    return getColor(q);    
}

