vec3 plot3D( in vec2 px )
{
    vec2 p = (-iResolution.xy + 2.0*px)/iResolution.y;

    // camera
    vec3 ro = vec3(0.0, 0.0, 3.0 );
    vec3 rd = normalize( vec3(p,-2.0) );

    // sphere
    vec4 sph = vec4( 0.0, 0.0, 0.0, 1.0 );

    vec3 col = vec3(0.0);

    // intersect geometry
    float tmin = 1e10;
    vec3 nor;
    float occ = 1.0;
    vec3 mate = vec3(1.0);

    // plane/floor
    float t1 = iPlane( ro, rd );
    if( t1>0.0 )
    {
        tmin = t1;
        vec3 pos = ro + t1*rd;
        nor = vec3(0.0,1.0,0.0);
        occ = 1.0-sphOcclusion( pos, nor, sph );
        mate = vec3(0.2);
    }

    // sphere
    float t2 = sphIntersect( ro, rd, sph );
    if( t2>0.0 && t2<tmin )
    {
        tmin = t2;
        vec3 pos = ro + t2*rd;
        nor = sphNormal( pos, sph );
        occ = 0.5 + 0.5*nor.y;
        mate = getColor(nor);
    }

    // apply color and lighting
    if( tmin<1000.0 )
    {
        vec3 pos = ro + tmin*rd;

        vec3 lig = normalize( vec3(0.6,0.3,0.4) );
        float sha = sphSoftShadow( pos, lig, sph, 2.0 );

        vec3 lin = vec3(1.5)*clamp(dot(nor,lig),0.0,1.0)*sha;
        lin += 0.5*occ;
        lin += 0.5*occ*pow(clamp(1.0+dot(nor,rd),0.0,1.0),3.0);

        col = mate*lin;
        // fog
        col *= exp( -0.05*tmin );
    }
    return col;
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2   // make this 2 or 3 for antialiasing
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);
    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 p = fragCoord + vec2(float(m),float(n))/float(AA)-0.5;
        #else    
        vec2 p = fragCoord;
        #endif

        // draw stuff
        vec3 col = (sin(0.7*iTime)<-0.5) ? plot2D(p) : plot3D(p);

        // gamma correction
        col = pow(col,vec3(0.4545));

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // cheap dithering
    tot += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    // output color
    fragColor = vec4( tot, 1.0 );
}

#define PI 3.14159265359
#define EPSILON 1e-4
#define INFTY 1e6

float surfaceSDF(vec3 p) {
    float t = iTime * 0.1;
    return 0.5 * 
        length(
            mod(
                vec3(
                    2.0 * abs(sin(t) * cos(t)) * p.x,
                    1.0 * abs(cos(t) + sin(0.5 * t)) * p.z,
                    2.0 * cos(t) * p.y
                )
                + vec3(
                    2.0 * sin(t), 
                    cos(t), 
                    0.0
                ) 
                ,1.0
            )
            - 0.5
        )
        - 0.4 * (0.8 * cos(t) + sin(3.0 * p.x)) 
        * (sin(t) + 0.5 * sin(2.0 * p.y)) 
        * cos(4.0 * p.z);
}
float getAngle(vec3 p, vec3 q) {
    return acos(dot(normalize(p), normalize(q)));
}
vec3 getNormal(vec3 p) {
    float d = EPSILON;
    return normalize(vec3(
            surfaceSDF(p + vec3(d, 0.0, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, d, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, 0.0, d)) - surfaceSDF(p)
        ));
}
float getDiff(vec3 p, vec3 dir) {
    float d = EPSILON;
    return (surfaceSDF(p + d*dir) - surfaceSDF(p)) / d;
}
mat3 rotX(float t) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, sin(t), cos(t),
        0.0, - cos(t), sin(t)
    );
}
mat3 rotY(float t) {
    return mat3(
        sin(t), 0.0, cos(t),
        0.0, 1.0, 0.0,
        - cos(t), 0.0, sin(t)
    );
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 lightDir = vec3(0.0, 4.0, 1.0);

    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 cPos = vec3(0.0, 0.0, 2.0);
    vec3 cDir = vec3(0.0, - 0.0, - 1.0);
    vec3 cUp = vec3(0.0, 1.0, 0.0);
    float targetDepth = 1.0;
    float t = iTime * 0.3;
    cPos = rotX(t) * rotY(t) * cPos;
    cDir = rotX(t) * rotY(t) * cDir;
    cUp = rotX(t) * rotY(t) * cUp;
    vec3 cSide = cross(cDir, cUp);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);
    float ang = getAngle(ray, - normalize(cPos));
    vec3 rPos = cPos + (1.0 / cos(ang)) * ray;

    // marching loop
    for(int i = 0; i < 120; i ++ ) {
        rPos += 0.015 * ray;
        if (surfaceSDF(rPos) < 0.01) {
            break;
        }
    }
    vec3 normal = getNormal(rPos);
    float diff = clamp(dot(normalize(lightDir - rPos), normal), 0.01, 1.0);

    // hit check
    if (surfaceSDF(rPos) < 0.01) {
        fragColor = vec4(diff * vec3(rPos.x, 1.0, rPos.p), 1.0);
    } else {
        fragColor = vec4(vec3(1.0, 1.0, 1.0), 1.0);
    }
}

