const float PI = 3.14159265;

mat2 r2d(float a){float sa = sin(a);float ca=cos(a);return mat2(ca,sa,-sa,ca);}

float lenny(vec2 uv)
{
  return abs(uv.x)+abs(uv.y);
}

float sat(float a)
{
  return clamp(a,0.,1.);
}

vec3 sat(vec3 v)
{
  return vec3(sat(v.x),sat(v.y), sat(v.z));
}

float _sub(float a, float b)
{
  return max(-a,b);
}

float _union(float a, float b)
{
  return max(a,b);
}

float sdf_sqr(vec2 uv, float sz)
{
  return lenny(uv*r2d(PI/4.))-sz;
}

float sdf_sqrborder(vec2 uv, float sz, float th)
{
  return _sub(sdf_sqr(uv,sz-th),sdf_sqr(uv,sz));
}

vec3 rdr(vec2 uv)
{
  vec3 col;
  vec3 acc=vec3(1.);
  const int cnt = 8;
  float r=0.2;
  for (int i=0;i<cnt;++i)
  {
    float fi =float(i);
    float fc = float(cnt);
    vec2 pos = vec2(sin(iTime+fi),cos(iTime+fi))*r;
    acc *=sat((vec3(sin(iTime+fi)*0.5+0.9)*sdf_sqrborder(uv-pos,0.9,0.01)*20.));
  }
  acc*= sat(sdf_sqr(uv, 0.6)*100.);
  return col + acc*0.2;
}

vec3 rdrCross(vec2 uv, float sharp)
{
  uv *= r2d(PI/4.);
  float len = 9.*sin(iTime)*(sin(iTime*7.37));
  float a = (1.-sat(sdf_sqr(uv*vec2(1.,len), 0.5)*sharp));
  float b = (1.-sat(sdf_sqr(uv*vec2(len,1.), 0.5)*sharp));

  return vec3(1.)*_union(a,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  uv -= vec2(.5)*(iResolution.xy/iResolution.xx);

  vec3 col;
  uv*=1.5;
uv.y = abs(uv.y);
  uv = uv*r2d(iTime*sign(uv.x)*0.5);

  uv.y = mix(abs(uv.x*uv.y),uv.y,0.5*sin(iTime)+0.5);
  float modsz = .9*(sin(sign(uv.y)*iTime)*0.2+0.9);
  uv = mod(uv, vec2(modsz))-vec2(modsz/2.);

  col = rdr(uv);
  col += 0.5*mix(rdrCross(uv,200.),vec3(1.)*(1.-sat(sdf_sqr(uv,.5)*100.)),-1.);

  col += 0.5*rdrCross(uv*r2d(iTime*3.),10.)*vec3(1.,0.7,0.2);
col += 0.5*rdrCross(uv*r2d(-iTime),5.)*vec3(1.,0.7,0.2).xzy;
col += vec3(1.,0.9,0.7)*(1.-sat(lenny(uv*2.)))*2.;
  col *= 1.-lenny(uv*1.)*2.;
  fragColor = vec4(col, 1.0);
}

const float PI = 3.14159265359;
const float size = 5.0;

#define animationTimeCounter mod(iTime+6.2831*iMouse.x/iResolution.x, 10.0)

vec2 fuv_build(in vec2 uv) {
    vec2 fuv = uv * 2.0 - 1.0;
    fuv *= max(iResolution.xy / iResolution.yx, vec2(1.0));
    return fuv;
}

mat2 mRotate(float theter) {
    float c = cos(theter);
    float s = sin(theter);
    return mat2(c, -s,
                s, c);
}

void rotate(inout vec2 uv, in float theter) {
    uv = mRotate(theter) * uv;
}

float triangle(in vec2 puv) {
	float e1 = 0.57735 * (1.0 - puv.x) - abs(puv.y); 
    float e2 = puv.x + 1.0; 
	return min(smoothstep(0.0, 0.05, e1), smoothstep(0.0, 0.0443, e2));
}

float triangle(in vec2 puv, in float d) {
    puv.x -= d;
	return triangle(puv);
}

float cdot(in vec2 puv) {			//Just a light dot
    float dist = length(puv);
	dist = smoothstep(0.0, 2.4, 2.4 - dist) * 1.1;
    return pow(dist, 2.4);
}

float cdot(in vec2 puv, in float d) {
    puv.x += d;
	return cdot(puv);
}

float track(float animationTime) {				//Running track of graph
    const float edge0 = log(size) / log(1.618) * 1.3333;		//The angle of beginning
    const float edge = floor(edge0) * PI * 0.25;				//Correct rotation angle

    float frametime = (smoothstep(-5.0, 5.0, animationTime) - 0.5) * 8.0;
    float theter = edge * (3.0 - frametime);
    float r = pow(1.618, 2.0 * theter / PI);			//Fibonacci helix
    return max(r, 1.0);
}

vec2 getPuv(in vec2 fuv, int t, vec2 p, mat2 m) {
    float r = track(animationTimeCounter - float(t) * 0.2);

    vec2 puv = (fuv * size - r * p) / 0.4142;
    return m * puv;
}

void ot(inout float tri, vec2 fuv) {
	const vec4 trianglePos[] = vec4[16] (		//Center position(x,y), 1/size, angle
		vec4(-1.17, 	-0.464, 4.348, 	1.4),
		vec4(-0.3356, 	1.176, 	2.1739,	1.05),
		vec4(-1.73, 	0.3, 	5.376, 	-0.4),
		vec4(1.42578,	-0.042,	3.760, 	1.24),
		vec4(0.711,		-0.92,	4.7619,	-0.3),
		vec4(0.551, 	0.64, 	16.667,	-0.5),
		vec4(-0.117, 	-0.672,	25.0, 	1.1),
		vec4(-1.56, 	-0.17,	24.39,	1.15),
		vec4(-1.26, 	0.728,	26.3,	-0.2),
		vec4(0.86,		-0.7,	12.5,	0.0),
		vec4(1.4933,	1.14,	3.70,	-0.9),
		vec4(1.36889,	-0.362,	14.3,	1.26),
		vec4(-0.89245,	0.1,	50.0,	0.1),
		vec4(1.3511,	-0.814,	37.453,	0.01),
		vec4(-1.45778,	-1.16,	58.82,	-0.2),
		vec4(1.39,		-0.722,	100.0,	1.25)
	);

	float frametime = smoothstep(0.0, 7.0, animationTimeCounter);

	for (int i = 0; i < trianglePos.length(); ++i) {
		vec4 pos = trianglePos[i];
		pos.w -= (1.0 - frametime) * pos.z;
		vec2 puv = (fuv - pos.xy / frametime) * pos.z;
		rotate(puv, pos.w);

		tri += triangle(puv, 0.3333);
	}
}

void HyperCol_Logo(inout vec3 fcolor, in vec2 fuv0) {
    vec2 fuv = fuv0;
    const vec3 logoColor = vec3(0.0, 0.62, 0.9);
    float logo = 0.0;

    const float sqrt2 = inversesqrt(2.0);
    const mat2 rot45 = mat2(sqrt2, -sqrt2,
                           sqrt2, sqrt2);
    const float edge0 = log(size) / log(1.618) * 1.3333;		//The angle of beginning
    const float edge = floor(edge0) * PI * 0.25;				//Correct rotation angle

    float frametime = (smoothstep(-6.0, 6.0, animationTimeCounter) - 0.5) * 8.0;
    float theter = edge * (3.0 - frametime);
    vec2 p = vec2(cos(theter), sin(theter));
    mat2 m = mRotate(theter - PI * 0.5);

    for (int i = 0; i < 8; ++i) {
        fuv = rot45 * fuv;

        vec2 puv = getPuv(fuv, i, p, m);
        logo += mix(cdot(puv, 0.3333), triangle(puv), smoothstep(1.5, 3.0, animationTimeCounter));
    }

    ot(logo,fuv0);
    fcolor = mix(fcolor, logoColor, logo);
}

vec4 HyperCol_Logo_Build(in vec2 uv) {
    vec2 fuv = fuv_build(uv);
    vec3 background = vec3(1.0) * smoothstep(1.0, 3.0, animationTimeCounter);

    HyperCol_Logo(background, fuv);
    return vec4(background, 1.0) * (1.0 - smoothstep(9.0, 10.0, animationTimeCounter));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = HyperCol_Logo_Build(uv);
}

#define EPSILON 0.0001
