 if(dot(z,z)==0.) z=reflect(A,e),a.x*=-1.;
 i-=e*a.x;
 a=t(i,z);
 if(a.x==0.) break;
 p*=mix(exp(-a.y*1.3*vec4(.1,.3,.5,1.)),vec4(1.),sign(a.x)*.5+.5);
 r+=m(i,z,a,e,w)*p;
 if(w.y==false)break;    
 }
fragColor = r;
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdHexPrism( vec3 p, vec2 h )
{
  const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
  p = abs(p);
  p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
  vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec4 GetDist(vec3 p) {

    vec3 prevP = p;
    float _floor = p.y;

    float y = 0.25;

    // body
    float b = sdBox(p-vec3(0.0,1.0+y,0.0),vec3(1.3,0.7,2.8));
    float b2 = sdBox(p-vec3(0.0,1.4+y,1.7),vec3(1.1,0.35,1.0));

    prevP*= matRotateX(radians(15.0));

    float _cut = prevP.y -1.75-y;
    prevP = p;

    prevP*= matRotateX(radians(-8.0));
    float _cut2 = prevP.y -1.6-y;

    vec3 hexP = p-vec3(0.0,0.3+y,-0.0);
    hexP*= matRotateY(radians(90.0));

    hexP.x = abs(hexP.x);
    hexP.x -= 1.7;

    float hex = sdHexPrism(hexP, vec2(0.6,2.0));
    prevP = p;

    prevP.x = abs(prevP.x);
    prevP.x -= 0.3;
    prevP*= matRotateY(radians(-40.0));

    float _cut3 = prevP.z +2.5;

    b = max(-_cut3,max(-b2,max(-hex,max(_cut2,max(_cut,b)))));

    prevP = p;
    vec4 resB = vec4(vec3(0.8),b);

    // ground
    vec4 f = vec4(vec3(0.3),_floor*0.6);

    // mud guard
    vec3 hexP2 = prevP-vec3(0.0,0.31+y,-0.0);
    hexP2*= matRotateY(radians(90.0));

    hexP2.x = abs(hexP2.x);
    hexP2.x -= 1.7;
    float hex2 = sdHexPrism(hexP2, vec2(0.7,1.45));
    float hex3 = sdHexPrism(hexP2, vec2(0.6,1.8));
    prevP = p;
    float hexCut = prevP.y-0.55;
    vec4 resMud= vec4(vec3(0.3),max(-hexCut,max(-hex3,hex2)));

    // tyres
    vec3 tyreP = prevP-vec3(0.0,0.3+y,-0.0);
    tyreP*= matRotateX(radians(90.0))*matRotateZ(radians(90.0));
    tyreP.x = abs(tyreP.x);
    tyreP.x -= 1.7;
    tyreP.y = abs(tyreP.y);
    tyreP.y -= 1.2;
    float tyre = sdCappedCylinder(tyreP,0.52,0.2);
    vec4 resTyre = vec4(vec3(0.2),tyre);
    prevP = p;

    // side windows
    prevP.x*=0.99;
    prevP.z*=1.1;
    float gy = -0.05+y;
    float g = sdBox(prevP-vec3(0.0,1.4+gy,0.0),vec3(1.3,0.3,2.8));

    prevP*= matRotateX(radians(15.0));

    float _gcut = prevP.y -1.75-gy;
    prevP = p;

    prevP*= matRotateX(radians(-8.0));
    float _gcut2 = prevP.y -1.6-gy;
    prevP = p;
    float _gcut3 = prevP.z -0.6;
    float _gcut4 = prevP.y -1.3-gy;

    g = max(-_gcut4,max(_gcut3,max(_gcut2,max(_gcut,g))));
    prevP = p;
    vec4 resG = vec4(vec3(0.3),g);

    // front window
    prevP*= matRotateX(radians(15.0));
    float fg = sdBox(prevP-vec3(0.0,1.75+y,-0.7),vec3(1.2,0.01,0.7));
    vec4 resFG = vec4(vec3(0.3),fg);

    vec4 model = combine(combine( combine(combine(combine(f,resB),resTyre),resG),resFG),resMud);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(iTime*20.0));
    ro.yz *= Rot(radians(-20.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz;
        col *= dif.y;

    } else {
        // background
        col = vec3(1.0);
    }

    fragColor = vec4(col,1.0);
}

#define PI 3.14159
#define N 6.

const float r1 = 0.75;
const float r2 = 1./r1;

vec4 over( in vec4 a, in vec4 b ) {
    return mix(a, b, 1.-a.w);
}

float nCap(vec2 uv, float angle, float r) {
    float a = atan(uv.y, uv.x)+angle;
    float b = a/PI*N/2.+PI*r2*2.0;
    float f = fract(b);
    float l = length(uv);
    float d = sin(f*PI*r2) * step(f, r1);
    return (1.-d*0.12)*r-l;
}

float line( in vec2 p, in vec2 a, in vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-iResolution.xy*0.5)/-iResolution.y*3.0;

    float av = sin(iTime)*PI*5./6.; // angle value

    mat2 m = mat2(cos(av), sin(av), -sin(av), cos(av));

    float a = atan(uv.y, uv.x);
    float l = length(uv);
	float g = -1. + smoothstep(.5, -.5, uv.y) * 2.;

    vec4 col = vec4(vec3(.2+g*.04), 1.); // background

    if(l < .96) {
	    float d = .0;
        for(float q = 0. ; q < 11. ; q += 1.) {
            float aq = -PI*1./3.+q*PI*1./6.;
            mat2 m = mat2(cos(aq), sin(aq), -sin(aq), cos(aq));
            d += smoothstep(.002, .001, line(uv, vec2(.0, .0), vec2(.88, .0)*m)-.012);
        }
        col = over(vec4(vec3(.8), d), col); // divs

        float h = smoothstep(.04, .08, 1.25 - distance(vec2(.0, -1.4), uv))*.2
            + smoothstep(.40, .7, l)*.01;
        col = over(vec4(vec3(.04), smoothstep(.001, .3, .88 - distance(uv, vec2(.0, .1)))*.75), col); // shadow
        col = over(vec4(vec3(.04), smoothstep(.001, .04, .74 - l)), col); // bottom
        col = over(vec4(vec3(.13+g*.04), smoothstep(.001, .01, .71 - l)), col); // bottom solid
        col = over(vec4(vec3(.13), smoothstep(.02, .001, abs(.64 - l))), col); // bottom outline
        col = over(vec4(vec3(.04), smoothstep(0.001, 0.06, nCap(uv+vec2(.0, -.12), av, .64))*.7), col); // cap shadow
        col = over(vec4(vec3(.09), smoothstep(0.01, 0.02, nCap(uv, av, .64))), col); // cap edge
        col = over(vec4(vec3(.16)+g*.1+h, smoothstep(0.001, 0.01, nCap(uv, av, .61))), col); // cap solid
        col = over(vec4(vec3(.24), smoothstep(.001, .01, .46 - l)), col); // cap top
        col = over(vec4(vec3(.44
                             + pow(abs(sin(a)),10.)*(0.1+l*1.)
                             + pow(abs(sin(a+1.4)),64.)*0.12
                             + abs(sin(l*32.)+.5)*.02
                             + g*.2
                            ), smoothstep(.001, .01, .44 - l)), col); // cap metal
        col = over(vec4(vec3(.34), smoothstep(.001, .01, .050 - distance(uv, vec2(.0, -.52)*m))), col); // value edge
        col = over(vec4(vec3(.97), smoothstep(.001, .01, .042 - distance(uv, vec2(.0, -.52)*m))), col); // value fill
    }

    col.gb += vec2(.004, .006);

	fragColor = col;
}

const float PI = 3.14159;

//const int nbSpire = 5;
//const int nbSubdiv = 30;
#define nbSpire   (iMouse.z>0.5?int(1.+iMouse.y/iResolution.y*10.):5)
#define nbSubdiv  (iMouse.z>0.5?int(iMouse.x/iResolution.x*50.+2.):30)

//const float theta_max = 2.*PI;
//#define theta_max  (iMouse.x/iResolution.x*2.*PI)
#define theta_max  (cos(iTime*.2)*1.8*PI)
const float rho_min = 0.1;
const float rho_max = 1.;

const float lineWidth = 1.;

//const bool minMax = false;
#define minMax  (texelFetch(iChannel0, ivec2(32,2), 0).x>0.5)


float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec2 pol2cart(float theta, float rho)
{
    return vec2(cos(theta), sin(theta))*rho;
}

vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

float theta(int spire, int subdiv)
{
    return float(spire)/float(nbSpire)*2.*PI + float(subdiv)/float(nbSubdiv-1)*theta_max;
}

float rho(int spire, int subdiv)
{
    return rho_min+(rho_max-rho_min)*float(subdiv)/float(nbSubdiv-1);
}

vec3 spireColor(int spire, float dist)
{
    return hsv2rgb_smooth(vec3(float(spire)/float(nbSpire), 0.8, exp(-dist)*.8+.2));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord/iResolution.xy - 0.5)*2.*vec2(iResolution.x/iResolution.y, 1.)*1.0;

    vec3 col = vec3(1.);

    float d_min_min = 1000.;
    float d_max_min = -1000.;
    float d_min_max = 1000.;
    float d_max_max = -1000.;

    int i_min_min = -1;
    int i_max_min = -1;
    int i_min_max = -1;
    int i_max_max = -1;

    for(int i=0; i<nbSpire; ++i)
    {

        float d_i_min = 1000.;
        float d_i_max = -1000.;
        for(int j=0; j<nbSubdiv-1; ++j)
        {
			vec2 p0 = pol2cart(theta(i,j), rho(i,j));
			vec2 p1 = pol2cart(theta(i,j+1), rho(i,j+1));
            float d = sdSegment(p, p0, p1);

            d_i_min = min(d_i_min, d);
            d_i_max = min(d_i_max, d);
        }

        if(d_i_min<d_min_min)
        {
            d_min_min = d_i_min;
            i_min_min = i;
        }

        if(d_i_min>d_max_min)
        {
            d_max_min = d_i_min;
            i_max_min = i;
        }

        if(d_i_max<d_min_max)
        {
            d_min_max = d_i_max;
            i_min_max = i;
        }

        if(d_i_max>d_max_max)
        {
            d_max_max = d_i_max;
            i_max_max = i;
        }
    }

    float dist = d_max_min;
    int i = i_max_min;

    if(minMax)
    {
        dist = d_min_min;
        i = i_min_min;
    }

    vec3 n = normalize(vec3(dFdx(dist), dFdy(dist), .02));
    vec3 l = normalize(vec3(cos(iTime*.5), sin(iTime*.5), 1.1));
    float phong = max(dot(n,l), 0.0);
    float spec = 0.5*pow(max(dot(reflect(vec3(0.0, 0.0, -1.0),n),l), 0.0), 256.);
    col = spireColor(i, 0.) * phong + vec3(spec);

    col=mix(col,vec3(0.),mix(1.,0.,clamp(d_min_min*iResolution.y/2. -  lineWidth, -1., 1.)*.5+.5));


    fragColor = vec4(col,1.0);
}

#define PI             3.14159265359
#define smoothing      0.006
#define lineWidth      0.002

#define mainLinesColor vec3(0.8,0.2,0.1)
#define eLinesColor    vec3(0.6,0.8,0.1)
#define dotColors      vec3(0.1,0.3,0.)

void DrawPoint(vec2 pos, vec2 uv,float size, vec3 dotColor, inout vec3 sceneColor){

    float d    = distance(uv, pos);
    sceneColor = mix(dotColor, sceneColor, smoothstep(size, size +smoothing, d));

}

void DrawLine(float m, float c, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

    vec2  xy   = vec2(uv.x, uv.x * m + c); 
    float d    = distance(xy, uv);
    sceneColor = mix(lineColor, sceneColor, smoothstep(size, size + smoothing, d));

}

void DrawVector(vec2 origin, vec2 vector, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    sceneColor = mix(lineColor, sceneColor, smoothstep(size, size +smoothing, d));

}

void DrawHalfVector(vec2 origin, vec2 vector, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    float m    = 1. - step(0.,vUv/v2);
    sceneColor = mix(lineColor, sceneColor, clamp(smoothstep(size, size +smoothing, d)+ m, 0. ,1.)); 
}

void DrawHalfVectorWithLength(vec2 origin, vec2 vector, float len, vec2 uv, float size, vec3 lineColor, inout vec3 sceneColor){

          uv  -= origin;
    float v2   = dot(vector, vector);
    float vUv  = dot(vector, uv);
    vec2  p    = vector * vUv/v2;
    float d    = distance(p, uv);
    float m    = 1. - step(0.,vUv/v2);
          m   += step(len, vUv/v2);
    sceneColor = mix(lineColor, sceneColor, clamp(smoothstep(size, size + smoothing, d)+ m, 0. ,1.)); 
}

void DrawCurveSide(vec2 graphOrigin, vec2 uvCoordinate, float sideLengths, float numberOfPoints, vec2 side1, vec2 side2, inout vec3 col){
        float side2Sqr        = dot(side2, side2);

    DrawHalfVectorWithLength( graphOrigin, normalize( side1), sideLengths,uvCoordinate, lineWidth, mainLinesColor, col);
    DrawHalfVectorWithLength( graphOrigin, side2, sideLengths,uvCoordinate, lineWidth, mainLinesColor, col);



    for(float i = 1. ; i < numberOfPoints; i ++){

        float f    = (i / numberOfPoints);

        vec2 point = graphOrigin + normalize(side1) * f*sideLengths;

        DrawPoint(point, uvCoordinate, 0.01,dotColors, col); 

        // projection on the other line

             f        = 1.-f;
        vec2 endPoint = graphOrigin + normalize(side2) * f*sideLengths;


        DrawPoint(endPoint , uvCoordinate,  0.01,dotColors, col); 

        vec2 e    = point - endPoint; 

        DrawHalfVectorWithLength(endPoint, normalize(e), length(e), uvCoordinate,lineWidth*0.1, eLinesColor, col);
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // ---------------------------------------------------------
    // ---COORDINATE SETUP

    vec2  uvCoordinate    =  fragCoord/iResolution.xy;
    float aCorreection    =  iResolution.x/iResolution.y;

          uvCoordinate.x *=  aCorreection;
          uvCoordinate   -=  vec2(aCorreection/2., 0.5);

    // ---------------------------------------------------------
	vec3  col             = vec3(0.6,0.6,0.6);


    vec2  graphOrigin     = vec2(clamp(sin(iTime)*0.5, -1.,0.), 0.);
    float sideLengths     = 0.5;
    vec2  side1           = vec2(0., -1.);
    vec2  side2           = vec2(1.,0.);

    float numberOfPoints  = 12.* abs(sin(iTime*0.4));


    DrawCurveSide(graphOrigin, uvCoordinate, sideLengths, numberOfPoints, side1, side2, col);

          graphOrigin     = vec2(clamp(sin(iTime + PI)*0.5, -1.,0.), 0.);

          side1           = vec2(0., 1.);
          side2           = vec2(1.,0.);

    DrawCurveSide(graphOrigin, uvCoordinate, sideLengths, numberOfPoints, side1, side2, col);


          graphOrigin     = vec2(clamp(sin(iTime)*0.5, 0.,1.), 0.);

          side1           = vec2(0., 1.);
          side2           = vec2(-1.,0.);

    DrawCurveSide(graphOrigin, uvCoordinate, sideLengths, numberOfPoints, side1, side2, col);

          graphOrigin     = vec2(clamp(sin(iTime + PI)*0.5, 0.,1.), 0.);

          side1           = vec2(0., -1.);
          side2           = vec2(-1.,0.);

    DrawCurveSide(graphOrigin, uvCoordinate, sideLengths, numberOfPoints, side1, side2, col);



    // Output to screen
    fragColor = vec4(col,1.0);
}

#define t iTime

float PI = acos(-1.0);

float Noise(float n)
{
    return fract(sin(n*4616.5451 + 566.163) * 7624.564);
}

float Noise21(vec2 n)
{
	return fract(sin(n.x*1265.63265 + n.y*7427.8432 + 654.163) * 6432.564);
}

float BetterNoise21(vec2 n)
{
    float decimalScale = 1000.0;
    n = round(n*decimalScale)/decimalScale;

	n = fract(n*vec2(573.721, 853.712));
    n += dot(n, n+64.853);

    return fract(n.x*n.y);
}

vec2 BetterNoise22(vec2 n)
{
	float p = BetterNoise21(n);

    return vec2(p, BetterNoise21(p+n));
}

float PerlinNoiseLayer(vec2 p)
{
	float offsetStep = 1.0;
    float gridSize = 10.0;

    vec2 st = smoothstep(0.0, 1.0, fract(p * gridSize));
    vec2 id = floor(p * gridSize);

    float upperLeft  = Noise21(id + vec2(0.0, 0.0));
    float upperRight = Noise21(id + vec2(offsetStep, 0.0));
    float lowerLeft  = Noise21(id + vec2(0.0, offsetStep));
    float lowerRight = Noise21(id + vec2(offsetStep, offsetStep));

    float upperMix = mix(upperLeft, upperRight, st.x);
    float lowerMix = mix(lowerLeft, lowerRight, st.x);

    float finalMix = mix(upperMix, lowerMix, st.y);

	return finalMix;
}

float PerlinNoise(vec2 p)
{
    float currentNoise = 0.0;

    currentNoise += PerlinNoiseLayer(p*4.0)/2.0;
    currentNoise += PerlinNoiseLayer(p*8.0)/4.0;
    currentNoise += PerlinNoiseLayer(p*16.0)/8.0;
    currentNoise += PerlinNoiseLayer(p*32.0)/16.0;
    currentNoise += PerlinNoiseLayer(p*64.0)/32.0;

    // Returns value from 0 to 0.96875    
    return currentNoise;
}

mat2 Rot(float angle)
{
	float c = cos(angle);
    float s = sin(angle);

    return mat2(c, s, -s, c);
}

float Circle(vec2 uv, vec2 position, float radius)
{
	float dist = length(uv - position);

    dist = smoothstep(radius+0.01, radius, dist);

    return dist;
}

vec3 MiddleCircles(vec2 uv, vec3 middleBoxColor)  
{
    vec3 col = vec3(0.0);

	for(float i = 0.0; i < 50.0; i++)
    {
        float movementSpeed = 0.15;
        float pt = fract(Noise(i*7412.51 + 742.74) + t*movementSpeed);

    	vec2 p = vec2(Noise(i*612.63), Noise(i*137.11 + 467.32));
        p = p * 2.0 - 1.0;
        p.y *= 0.15;

        // Slow down as it comes towards the window edge
        p.x = (1.0 - pow(pt-1.0, 2.0));

        // Flip back again
        p.x *= Noise(i*626.31) < 0.5 ? -1.0 : 1.0;

        float circleSize = Noise(i*213.74) * 0.028;

        float c = Circle(uv, p, circleSize);
        float fade = smoothstep(0.9, 0.0, abs(p.x));
        fade = clamp(fade, 0.0, 1.0);

        vec3 circleCol = vec3(c) * middleBoxColor * fade;
        circleCol = circleCol * pow(fade+1.0, 3.0);

        col += circleCol;
    }

    return col;
}

vec3 MiddleBox(vec2 uv)
{
    vec2 distances = abs(uv);
	float d = distances.y;

    float height = 0.2;
    d = smoothstep(height+0.08, height-0.01, d);
    d *= 0.8;
    d = pow(d, 5.0);
    d *= pow((1.0 - abs(uv.y))*1.5, 2.3);

    float r = d*clamp(uv.x, 0.0, 1.0);
    float b = d*clamp(-uv.x, 0.0, 1.0);

    float lineHeight = 60.0;
    float horizontalLine = Noise(round(uv.y * lineHeight)/lineHeight);
    horizontalLine = 1.0 - horizontalLine*0.1;

    vec3 mainBoxCol = vec3(r, 0.0, b) * horizontalLine;
    vec3 edges = vec3(1.0);
    edges *= smoothstep(height-0.08, height+0.01, d);
    edges *= smoothstep(height+0.2, height+0.15, d);
    edges *= 0.3;

    return mainBoxCol + edges;
}

vec3 ThreeMiddleLights(vec2 uv)
{
    float toThePower = 3.0;

	float currentDistance = 0.0;
    currentDistance += pow(1.0 - length(uv), toThePower);
    currentDistance += pow(1.0 - length(uv + vec2(0.9, 0.0)), toThePower);
    currentDistance += pow(1.0 - length(uv - vec2(0.9, 0.0)), toThePower);

    return vec3(currentDistance*0.8);
}

vec3 Smoke(vec2 uv)
{
    vec3 finalCol = vec3(0.0);

    for(float layer = 0.0; layer < 3.0; layer++)
    {
        float perl = PerlinNoise(uv*0.3 + vec2(0.04 * layer, 0.0) * iTime * sign(uv.x));

        perl *= pow(abs(uv.x), 2.0) * pow((1.0 - abs(uv.y)) * 0.9, 1.0);

        float r = perl * clamp(uv.x, 0.0, 1.0) * 4.0;
        float b = perl * clamp(-uv.x, 0.0, 1.0) * 4.0;

        float alpha = 0.3;

        finalCol += vec3(perl) * vec3(r, 0.0, b) * alpha;
    }


	return finalCol;
}

vec3 Fire(vec2 uv)
{
    vec3 col = vec3(0.0);

    for(float layers = 0.0; layers < 4.0; layers++)
    {
        // The fire
        vec2 speed = vec2(0.0, -0.6) * t  * (1.0 + 0.1*layers);
        vec2 layerOffset = vec2(632.763, 1631.631) * layers;

        float n = PerlinNoise(uv + speed + layerOffset);

        // Decrease alpha the further away it is
        n *= pow(1.0 - length(uv*vec2(1.0, 0.3)), 7.0)*1.2;

        // Decrease alpha below y = 0.0
        n *= uv.y > 0.0 ? 1.0 : pow(1.0-abs(uv.y), 10.0); 

        float threshold = 0.3 * (1.0 - pow(abs(uv.y), 4.0));
        n *= smoothstep(threshold-0.06, threshold, n);

        // Actual fire color
        //col += vec3(n*n, n*n*n, 0.0)*3.0;
        col += vec3(n)*1.3;
    }

	return col;
}

vec3 MiddleFire(vec2 uv)
{
    uv *= Rot(-0.2);
    uv.x *= 2.5;

    vec3 f = vec3(0.0);
    f += Fire(uv);
    f += Fire(-uv);

    float middleOffset = 0.1;
    float r = smoothstep(-middleOffset, -middleOffset+0.3, uv.x);
    float b = smoothstep(middleOffset, middleOffset-0.3, uv.x);

    float g = smoothstep(middleOffset, 0.05, abs(uv.x));

    r += g;
    b += g;

	return f * vec3(r, g, b);
}

vec2 ClosestPointLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pb = p-b;
    vec2 ab = a-b;

	float t = dot(pb, ab) / dot(ab, ab);
    t = clamp(t, 0.0, 1.0);

    vec2 closestPoint = t*a + (1.0 - t)*b;

    return closestPoint;
}

float Line(vec2 p, vec2 a, vec2 b)
{
	vec2 closestPoint = ClosestPointLine(p, a, b);
    float d = length(closestPoint - p);

    float radius = 0.01;

    float c = smoothstep(radius, radius*0.98, d);
    return c;
}

vec2 VoroGetDotPos(vec2 id, vec2 offset)
{
    vec2 voroTime = BetterNoise22(id+offset);

    return sin(voroTime)*0.45 + offset;
}

// This part was a combination between the voronoi created by The Art Of Code (BigWings)
// and some of own methods.
vec3 Voronoi(vec2 uv)
{
    float gridSize = 5.0;

    uv *= gridSize;

    vec2 st = fract(uv) - 0.5;
    vec2 id = floor(uv);
    vec2 dotGrid = st;

    vec2 dotPos = VoroGetDotPos(id, vec2(0.0));

    float dotRadius = 0.1;

    float l = length(dotGrid - dotPos);
    float d = smoothstep(dotRadius, dotRadius-0.01, l);

    vec2 closestNeighbor = vec2(0.0);
    float closestNeighborDist = 1000.0;

    float lineCol = 0.0;
    for(float y = -1.0; y <= 1.0; y++)
    {
    	for(float x = -1.0; x <= 1.0; x++)
        {
            vec2 currentPos = VoroGetDotPos(id, vec2(x, y));

            // Find best neighbor
            if(length(currentPos-st) < closestNeighborDist)
            {
                closestNeighborDist = length(currentPos-st);
                closestNeighbor = id + vec2(x, y);
            }

        	lineCol += Line(st, dotPos, currentPos);
        }
    }

    // Take the sides into consideration
    vec2 up = VoroGetDotPos(id, vec2( 0.0,  1.0));
    vec2 down = VoroGetDotPos(id, vec2( 0.0, -1.0));
    vec2 right = VoroGetDotPos(id, vec2( 1.0,  0.0));
    vec2 left = VoroGetDotPos(id, vec2(-1.0,  0.0));

    lineCol += Line(st, up, right);
    lineCol += Line(st, up, left);
    lineCol += Line(st, down, right);
    lineCol += Line(st, down, left);

    float randCol = BetterNoise21(closestNeighbor);

    vec3 col = vec3(randCol);
    //col += vec3(lineCol);

	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized uv coordinates
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y; 

    vec3 col = vec3(0.0);

    // Voronoi
    col += Voronoi(uv)*0.20 * pow(length(uv), 3.0);

    // Red/Blue box in the background
    vec3 b = MiddleBox(uv);
    col += b;

    // Circles
    col += MiddleCircles(uv, b);

    // Smoke from the sides
    col += Smoke(uv);

    // Three middle lights
    col += ThreeMiddleLights(uv) * (b+0.3);

    // Fire in the middle
    col += MiddleFire(uv);

    //col = Fire(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          uv = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime;
    float scale = 20.0;

    float x = uv.x* scale;
    float y = uv.y* scale;

    float a = 1.* sin(0.5* t);
    float b = 10.* sin(1.2* t);
    float c = 8.* sin(1.3* t);
    float d = 10.* sin(1.4* t);
    float e = 100.* sin(1.* t);
    float f = -1.* sin(0.78* t);
    float g = 12.* sin(1.7* t);
    float h = 6.* sin(1.7* t);
    float i = 6.* sin(1.7* t);

    float p = a* x* x* x* x + b* x* x *x + c* x* x + d* x + e
        + tan(x)* tan(x)* tan(x)* tan(x)* tan(x);
    float q = f* y* y* y* y + g* y* y* y + h* y* y + i* y
        + tan(y)* tan(y)* tan(y)* tan(y)* tan(y);

    O = vec4(vec3(1. / pow(p - q, .15)), 1.);
}

#define MAX_STEPS 100
#define MIN_DIST 0.01
#define MAX_DIST 100.


float GetDist(vec3 p)
{
    vec4 sphere = vec4(0.,pow(sin(iTime),2.)+1.,6.+pow(sin(iTime),2.)*0.5,1.);
    float d_sphere = length(p-sphere.xyz) - sphere.w;
    float d_plane = p.y;
    float d = min(d_plane,d_sphere);
    return d;
}



vec2 RayMarch(vec3 r_origin, vec3 r_direction)
{
    float d_origin = 0.;
    float min_dist = 1000000.0;

    for(int i=0; i<MAX_STEPS; i++) 
    {
        vec3 p = r_origin + d_origin*r_direction;
        float d_surf = GetDist(p);
        min_dist = min(d_surf, min_dist);
        d_origin += d_surf;
        if(d_surf<MIN_DIST || d_origin>MAX_DIST) break;
    }

    return vec2(d_origin,min_dist);
}

vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(.01,0);

    vec3 n = d - vec3( 
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetLight(vec3 p)
{

    vec3 rgb = vec3(0.,0.,0.);
    vec3 lightPos = vec3(0,3,6);


    lightPos.xz += vec2(5.*sin(iTime),5.*cos(iTime));
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n,l),0.,1.);
    vec2 rm = RayMarch(p+n*MIN_DIST*1.1,l);
    float mindist = rm.y;
    float d = rm.x;
    if(d<length(lightPos-p)) dif*=0.1;
    vec3 lightCol = vec3(0,0,0);
    if(d>=MAX_DIST)
    	lightCol = 0.05*(vec3(10./(mindist+0.09),1./(mindist+0.5),1./(mindist+0.09)));

    rgb = dif+lightCol;

    return rgb;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-0.5*iResolution.xy) / iResolution.y;

    vec3 r_origin = vec3(0,1,0);
    vec3 r_direction = normalize(vec3(uv.x,uv.y,1));
    vec2 rm = RayMarch(r_origin, r_direction);
    float mindist = rm.y;
    float dist = rm.x;
    vec3 p = r_origin + r_direction*dist;
    vec3 diffuse = GetLight(p);
    vec3 col = diffuse;

    fragColor = vec4(col*mindist*2. +vec3(1.5*col.y/dist,0.3*col.z/dist,0.3*col.x/dist),1.0);
}

