}

// Created by edubart - https://github.com/edubart
//
// Why not use SDFs for 1D too? It can be an easy introduction
// for a beginner in the concept. If you consider the space of
// a single line the signed distance from points and segments
// can be computed using a SDF function. Operatios like
// limited repetition, space scaling, translating, etc
// works the same as in 2D/3D.
//
// This shader contains the following 1D SDF primitives examples:
// 1. The first line shows the signed field for a point.
// 2. The second line shows the signed field for a segment.
// 3. The third line shows the signed field for a half line.
// 4. The forth line shows SDF operations like scale and limited repetition.
//
// Inspired by Inigo Quilez 2D/3D SDF primitives.

#define LINE_HEIGHT 0.03

// Point at the origin
float sd_point(float p) {
	return abs(p);
}

// Line segment with its center at the origin
float sd_segment(float p, float size) {
	return abs(p) - size;
}

// Half line begining at the origin and going to the left
float sd_halfline(float p) {
	return p;
}

// 1D limited repetition
float op_rep_lim(float p, float c, float l) {
    return p-c*clamp(round(p/c),-l,l);
}

// First scene (point)
float map1(float p) {
    float q = p - sin(iTime)*0.1;
   	return sd_point(q);
}

// Second scene (line segment)
float map2(float p) {
    float size = 0.3 + sin(iTime)*0.2;
    return sd_segment(p, size);
}

// Third scene (half line)
float map3(float p) {
    float q = p - sin(iTime)*0.1;
   	return sd_halfline(q);
}

// Fourth scene (many elements)
float map4(float p) {
    // scale space
    p *= 0.9+0.1*sin(iTime);

    // repeated points
    float q = op_rep_lim(p - -0.4, 0.1, 2.0);
    float d = sd_point(q);

    // line segment
    d = min(d, sd_segment(p - 0.2, 0.1));

    // half line
    d = min(d, sd_halfline(-(p - 0.6)));
    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // screen uv coords in the range [-1,1]
    vec2 uv = 2.0*fragCoord.xy/iResolution.xy - 1.0;

    // current view point in the line in the range [-1,1]
	float p = uv.x;

    // compute the neartest point
    float d;
    if(abs(uv.y - 0.6) < LINE_HEIGHT) { // first line
        d = map1(p);
    } else if(abs(uv.y - 0.2) < LINE_HEIGHT) { // second line
        d = map2(p);
    } else if(abs(uv.y + 0.2) < LINE_HEIGHT) { // third line
        d = map3(p);
    } else if(abs(uv.y + 0.6) < LINE_HEIGHT) { // third line
        d = map4(p);
    } else {
        // background
    	fragColor = vec4(vec3(0.03),1.0);
        return;
    }

    // colorize distance field
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-2.0*abs(d));
    col *= 0.8 + 0.2*cos(320.0*d);
    col = mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    fragColor = vec4(col,1.0);
}

precision highp float;

#define INF 1000000.

float rand(vec2 co){ return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }

vec2 repeat(vec2 uv, vec2 tile, vec2 off) {
    return fract(uv * tile-off);
}

float circle(vec2 uv, float radius) {
 	float c = length(uv - 0.5);
    return c - radius;
}
vec3 norm_circle(vec2 uv, float radius) {
    vec2 n2d = (uv - 0.5);
    return normalize(vec3(n2d, sqrt(radius*radius-dot(n2d,n2d))));
}

vec2 line(vec2 uv, vec2 xy, float thickness) {
    return abs (uv - xy).yx - thickness;
}

vec4 norm_line(vec2 uv, vec2 xy, float thickness) {
    vec2 dist = (uv - xy).yx;
    vec2 up=sqrt(thickness*thickness-dist*dist);
    return vec4(normalize(vec2(dist.x,up.x)),
                normalize(vec2(dist.y,up.y)));
}

vec2 pipe_id(vec2 uv, vec2 tile, vec2 off) {
	return floor(uv * tile + off);
}

float random_select(vec2 id) {
	return INF*(1.-floor(rand(id)+0.5));
}

float mask(float d) {
  vec2 R = iResolution.xy;
  float mx = max(R.x, R.y);
  return smoothstep(1.0,.0,(d+10./mx)*mx/20.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 R = iResolution.xy;
  float mx = max(R.x, R.y);
  vec2 center = R / 2.0;
  vec2 uv_static = fragCoord.xy / mx;
  vec2 uv_full = uv_static;

  uv_full += iTime/10.0;

  const float tile_size = 10.0;
  const float radius = 0.33;

  vec2 uv = repeat(uv_full, vec2(tile_size), vec2(0.0));

  float c = circle(uv, radius);

  vec2 l = line(uv,vec2(0.5), 0.15);

  vec2 id1 = pipe_id(uv_full, vec2(tile_size), vec2(0.5, 0.0));
  vec2 id2 = pipe_id(uv_full, vec2(tile_size), vec2(0.0, 0.5));

  l.x += random_select(id1*1.1);
  l.y += random_select(id2);

  float col = c;
  col = min(col, l.x);
  col = min(col, l.y);
  col = mask(col);

  float mask_c = mask(c);
  float mask_l = col - mask_c;

  // lighting
  vec3 pos = vec3(uv_static, max(-c,0.0));
  vec3 light_pos = vec3(0.7,0.7,0.6);
  light_pos.xy = vec2(cos(iTime)*cos(iTime/2.0),sin(iTime)*cos(iTime/2.0));
  vec3 light_dir = light_pos - pos;
  light_dir = normalize(light_dir);

  vec3 red = vec3(1,0,0);
  vec3 yellow = vec3(1,1,0);
  vec3 white = vec3(1.);
  vec3 sky_blue = vec3(0.5,0.8,1.);

  // background shading
  float light_dist = clamp(1.-length(light_pos.xy-uv_static),0.0,1.0);
  vec3 bg = white * light_dist + sky_blue * (1. - light_dist);
  float bg_mask = 1. - mask_c - mask_l;
  vec3 col_bg = bg * bg_mask;

  // sphere shading
  vec3 col_c = mask_c * (red * dot(norm_circle(uv, radius), light_dir));
  col_c = max(col_c,0.0);
  col_c = mix(sky_blue*0.3,col_c, length(col_c));

  // cylinder shading
  vec4 nl = norm_line(uv,vec2(0.5), 0.15);
  vec3 col_l = max(mask_l * (yellow * dot(vec3(0.0,nl.xy), light_dir)),0.0);
  col_l += max(mask_l * (yellow * dot(vec3(nl.z,0.0,nl.w), light_dir)),0.0);

  fragColor = vec4(col_c + col_l + col_bg, 1.0);
}

// Slow, high-quality 2D hash adapted from nimitz's
// WebGL2 hash collection
// (https://www.shadertoy.com/view/Xt3cDn)
vec2 hash22(uvec2 p)
{
    const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
    uint h32 = p.y + PRIME32_5 + p.x*PRIME32_3;
    h32 = PRIME32_4*((h32 << 17) | (h32 >> (32 - 17))); //Initial testing suggests this line could be omitted for extra perf
    h32 = PRIME32_2*(h32^(h32 >> 15));
    h32 = PRIME32_3*(h32^(h32 >> 13));
    h32 = h32^(h32 >> 16);
    uvec2 rz = uvec2(h32, h32*48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU)) / float(0x7fffffff);
}

// Specialty Perlin ease-in/out function
vec2 soften(vec2 t)
{
    vec2 t3 = t * t * t;
    vec2 t4 = t3 * t;
    vec2 t5 = t4 * t;
    return 6.0f * t5 - 
           15.0f * t4 +
           10.0f * t3;
}

// Perlin noise function, taking corner + pixel positions as input
float Perlin(vec2 uv)
{
    // Find corner coordinates
    vec4 lwrUpr = vec4(floor(uv), ceil(uv));
    mat4x2 crnrs = mat4x2(lwrUpr.xw, lwrUpr.zw,
                          lwrUpr.xy, lwrUpr.zy);

    // Generate gradients at each corner
    mat4x2 dirs = mat4x2(hash22(uvec2(floatBitsToUint(crnrs[0]))),
                         hash22(uvec2(floatBitsToUint(crnrs[1]))),
                         hash22(uvec2(floatBitsToUint(crnrs[2]))),
                         hash22(uvec2(floatBitsToUint(crnrs[3]))));

    // Shift gradients into [-1...0...1]
    dirs *= 2.0f;
    dirs -= mat4x2(vec2(1.0f), vec2(1.0f), 
                   vec2(1.0f), vec2(1.0f));

    // Normalize
    dirs[0] = normalize(dirs[0]);
    dirs[1] = normalize(dirs[1]);
    dirs[2] = normalize(dirs[2]);
    dirs[3] = normalize(dirs[3]);

    // Find per-cell pixel offset
    vec2 offs = mod(uv, 1.0f);

    // Compute gradient weights for each corner; take each offset relative
    // to corners on the square in-line
    vec4 values = vec4(dot(dirs[0], (offs - vec2(0.0f, 1.0f))),
                       dot(dirs[1], (offs - vec2(1.0f))),
                       dot(dirs[2], (offs - vec2(0.0f))),
                       dot(dirs[3], (offs - vec2(1.0f, 0.0f))));

    // Return smoothly interpolated values
    vec2 softXY = offs;//soften(offs);
    return mix(mix(values.z, 
                   values.w, softXY.x),
               mix(values.x, 
                   values.y, softXY.x),
               softXY.y);
}

// Compute fractal noise for a given pixel position
vec3 fbm(vec2 uv)
{
    const uint depth = 24u;
    const vec2 dFreq = vec2(1.01f, 1.02f);
    const float dAmpl = 1.2f;
    vec2 srct = vec2(1.0f);//vec2((sin(iTime) * 0.1f, cos(iTime) * 0.1f));// + vec2(200.0f);
    vec2 t = srct;//min((srct * (0.075 / log(srct)) + sin(srct * 0.01)), 400.0);
    vec2 sfreq = vec2(1.01f + (sin(t) * 2.0)); // Starting noise scaling frequency
    vec2 rfreq = vec2(1.1f); // Starting noise rotational frequency
    float ampl = 0.25f; // Starting noise intensity
    float f = 0.0f;
    for (uint i = 0u; i < depth; i += 1u)
    {
        float f0 = Perlin(uv);
        f += ampl * f0 * abs(Perlin(uv));
        ampl *= dAmpl;
        uv += (sfreq + rfreq) * ampl;
    }
    return vec3(f, pow(ampl, dAmpl), uv.x * uv.y);
}

